        ) : null}
        {subtitle ? (
          <p className="mx-auto mt-3 max-w-3xl text-base text-brand-paragraph dark:text-brand-paragraph">
            {subtitle}
          </p>
        ) : null}
        <div className="mt-7 flex flex-wrap justify-center gap-3">
          {bookLabel ? (
            <Button onClick={onBookClick} size="lg">
