 */

import { useCallback, useEffect,useId, useRef, useState } from "react";
import { useTranslation } from "react-i18next";
import clsx from "clsx";

const SEARCH_CONTAINER_CLASSES = [
  "relative",
  "w-full",
  "sm:w-80",
] as const;

const SEARCH_INPUT_CLASSES = [
  "w-full",
