// src/components/landing/SocialProofSection.tsx
import { memo } from "react";
import { useTranslation } from "react-i18next";

import { Section } from "@acme/design-system/atoms";

import { Cluster, Inline, Stack } from "@/components/ui/flex";
import hotel, { RATINGS_SNAPSHOT_DATE } from "@/config/hotel";
import type { AppLanguage } from "@/i18n.config";
import { Star } from "@/icons";

type Testimonial = {
  rating?: number;
  text: string;
  datePublished?: string;
};

const SOURCE_KEYS: Record<string, string> = {
  Hostelworld: "hostelworld",
  "Booking.com": "booking",
};
const I18N_KEY_TOKEN_PATTERN = /^[a-z0-9_]+(?:\.[a-z0-9_]+)+$/i;
const FALLBACK_SOCIAL_PROOF_TITLE =
  // i18n-exempt -- BRIK-1267 [ttl=2026-12-31] fallback copy for missing locale bundles.
  "Guests love Brikette";
const FALLBACK_SOCIAL_PROOF_SUBTITLE =
  // i18n-exempt -- BRIK-1267 [ttl=2026-12-31] fallback copy for missing locale bundles.
  "Top ratings and recent reviews from travelers.";

function resolveTranslatedCopy(value: unknown, fallback: string): string {
  if (typeof value !== "string") return fallback;
  const trimmed = value.trim();
  if (!trimmed) return fallback;
  if (I18N_KEY_TOKEN_PATTERN.test(trimmed)) return fallback;
  return trimmed;
}

function formatSnapshotMonthYear(isoDate: string, locale: string): string {
  const parsed = new Date(isoDate);
  if (Number.isNaN(parsed.getTime())) return isoDate;

  return new Intl.DateTimeFormat(locale, {
    month: "long",
    year: "numeric",
  }).format(parsed);
}

const SocialProofSection = memo(function SocialProofSection({ lang }: { lang?: AppLanguage }): JSX.Element | null {
  const translationOptions = lang ? { lng: lang } : undefined;
  const { t: tLanding } = useTranslation("landingPage", translationOptions);
  const { t: tRatings, i18n } = useTranslation("ratingsBar", translationOptions);
  const { t: tTestimonials, ready } = useTranslation("testimonials", translationOptions);
  const ratings = hotel.ratings ?? [];

  const featured: Testimonial[] = (() => {
    if (!ready) return [];
    const raw = tTestimonials("hostelworld.featured", { returnObjects: true }) as unknown;
    return Array.isArray(raw) ? (raw as Testimonial[]).slice(0, 2) : [];
  })();

  if (!ratings.length && !featured.length) return null;

  const locale = lang ?? i18n.language ?? "en";
  const snapshotMonthYear = formatSnapshotMonthYear(RATINGS_SNAPSHOT_DATE, locale);
  const snapshotAsOfLabel = resolveTranslatedCopy(
    tRatings("snapshotAsOf", {
      date: snapshotMonthYear,
      defaultValue: `As of ${snapshotMonthYear}`,
    }),
    `As of ${snapshotMonthYear}`
  );

  return (
    <section className="bg-brand-surface py-12 scroll-mt-24 dark:bg-brand-surface">
      <Section as="div" padding="none" width="full" className="mx-auto max-w-6xl px-4">
        <Stack className="gap-2">
          <h2 className="text-2xl font-semibold text-brand-heading dark:text-brand-text">
            {resolveTranslatedCopy(
              tLanding("socialProof.title", { defaultValue: FALLBACK_SOCIAL_PROOF_TITLE }),
              FALLBACK_SOCIAL_PROOF_TITLE
            )}
          </h2>
          <p className="text-sm text-brand-text/70 dark:text-brand-text/70">
            {resolveTranslatedCopy(
              tLanding("socialProof.subtitle", {
                defaultValue: FALLBACK_SOCIAL_PROOF_SUBTITLE,
              }),
              FALLBACK_SOCIAL_PROOF_SUBTITLE
            )}
          </p>
          <p className="text-xs font-medium uppercase tracking-wide text-brand-text/70 dark:text-brand-text/70">
            {snapshotAsOfLabel}
          </p>
        </Stack>

        <div className="grid grid-cols-1 gap-4 lg:grid-cols-2 mt-6">
          <Stack className="gap-3">
            {ratings.map((rating) => {
              const key = SOURCE_KEYS[rating.provider];
              const providerLabel = key
                ? resolveTranslatedCopy(
                    tRatings(`sources.${key}.label`, { defaultValue: rating.provider }),
                    rating.provider
                  )
                : rating.provider;
              const formattedCount = rating.count.toLocaleString(locale);
              const reviewText = resolveTranslatedCopy(
                tRatings("countReviews", { count: rating.count, formattedCount }),
                `${formattedCount} reviews`
              );
              return (
                <Cluster
                  key={rating.provider}
                  className="items-center justify-between rounded-2xl border border-brand-outline/30 bg-brand-bg px-4 py-3 shadow-sm border-fg-inverse/10 dark:bg-brand-surface"
                >
                  <div>
                    <p className="text-sm font-semibold text-brand-heading dark:text-brand-text">
                      {providerLabel}
                    </p>
                    <p className="text-xs text-brand-text/70 dark:text-brand-text/70">{reviewText}</p>
                  </div>
                  <Inline
                    as="span"
                    className="gap-1 rounded-full bg-brand-surface/70 px-3 py-1 text-sm font-semibold text-brand-heading bg-fg-inverse/10 dark:text-brand-text"
                  >
                    <Star className="size-4 text-brand-secondary" aria-hidden />
                    {rating.value.toFixed(1)}
                  </Inline>
                </Cluster>
              );
            })}
          </Stack>

          <div className="grid grid-cols-1 gap-4 lg:grid-cols-2">
            {featured.map((item, index) => (
              <Stack
                key={`${item.text}-${index}`}
                className="h-full gap-3 rounded-2xl border border-brand-outline/30 bg-panel/80 p-5 shadow-sm border-fg-inverse/10 dark:bg-brand-surface"
              >
                <Inline className="gap-2 text-sm font-semibold text-brand-heading dark:text-brand-text">
                  <Star className="size-4 text-brand-secondary" aria-hidden />
                  {item.rating ? item.rating.toFixed(1) : "-"}
                </Inline>
                <p className="text-sm leading-relaxed text-brand-text/80 dark:text-brand-text/80">
                  &quot;{item.text}&quot;
                </p>
              </Stack>
            ))}
          </div>
        </div>
      </Section>
    </section>
  );
});

export default SocialProofSection;

// src/components/not-found/NotFoundView.tsx
"use client";
import { Fragment, memo, useCallback } from "react";
import { useTranslation } from "react-i18next";

import { Button } from "@acme/design-system/primitives";
import { AppLink as Link } from "@acme/ui/atoms/Link";
import buildCfImageUrl from "@acme/ui/lib/buildCfImageUrl";

import Page from "@/components/common/Page";
import { BASE_URL } from "@/config/site";
import * as ModalCtx from "@/context/ModalContext";
import i18n from "@/i18n";
import type { AppLanguage } from "@/i18n.config";
import { i18nConfig } from "@/i18n.config";
import { OG_IMAGE } from "@/utils/headConstants";
import { resolveI18nMeta } from "@/utils/i18nMeta";
import { buildRouteLinks,buildRouteMeta } from "@/utils/routeHead";
import { getSlug } from "@/utils/slug";
import { useApplyFallbackHead } from "@/utils/testHeadFallback";

// Local types replacing react-router imports (used only for test head fallback)
type MetaDescriptor = Record<string, string | undefined>;
type LinkDescriptor = Record<string, string | undefined> & { key?: string };

type LoaderData = { lang?: AppLanguage; title?: string; desc?: string };

function NotFoundView() {
  // In App Router, there's no loader data - use fallbacks

  const data = undefined as LoaderData | undefined;

  const fallbackLang = i18nConfig.fallbackLng as AppLanguage;
  const docLang =
    typeof document !== "undefined"
      ? (document.documentElement?.lang as AppLanguage | undefined)
      : undefined;
  const i18nLang = i18n.language as AppLanguage | undefined;

  const supported = new Set(i18nConfig.supportedLngs);
  const lang =
    (data?.lang && supported.has(data.lang) ? data.lang : undefined) ||
    (docLang && supported.has(docLang) ? docLang : undefined) ||
    (i18nLang && supported.has(i18nLang) ? i18nLang : undefined) ||
    fallbackLang;

  const { t } = useTranslation("notFoundPage", { lng: lang });
  const { t: tTokens } = useTranslation("_tokens", { lng: lang });
  const { t: tTranslation } = useTranslation("translation", { lng: lang });

  // Prefer optional hook; fall back to useModal for older test mocks.
  const resolveModalHook: () => ModalCtx.ModalContextValue =
    typeof ModalCtx.useOptionalModal === "function" ? ModalCtx.useOptionalModal : ModalCtx.useModal;
  const { openModal } = resolveModalHook();

  const linkClasses = [
    "font-medium",
    "text-brand-primary",
    "decoration-brand-primary",
    "underline",
    "underline-offset-2",
    "transition-colors",
    "hover:decoration-brand-bougainvillea",
    "dark:text-brand-secondary",
    "dark:decoration-brand-secondary",
  ].join(" ");

  const sanitiseLabel = (value: string | undefined, fallbackKey: string) => {
    if (!value) return undefined;
    const trimmed = value.trim();
    return trimmed && trimmed !== fallbackKey ? trimmed : undefined;
  };

  const fallbackReserveLabel = (() => {
    const tFallbackTokens = i18n.getFixedT(fallbackLang, "_tokens");
    const tFallbackNotFound = i18n.getFixedT(fallbackLang, "notFoundPage");
    const tFallbackTranslation = i18n.getFixedT(fallbackLang, "translation");

    const reserve = sanitiseLabel(tFallbackTokens("reserveNow") as string, "reserveNow");
    const book = sanitiseLabel(tFallbackTokens("bookNow") as string, "bookNow");
    const buttonFallback = sanitiseLabel(tFallbackNotFound("buttonReserve") as string, "buttonReserve");
    const translationFallback = sanitiseLabel(
      tFallbackTranslation("reserve") as string,
      "reserve",
    );

    return reserve ?? book ?? buttonFallback ?? translationFallback;
  })();

  const reserveLabel = (() => {
    const reserve = sanitiseLabel(tTokens("reserveNow") as string, "reserveNow");
    const book = sanitiseLabel(tTokens("bookNow") as string, "bookNow");
    const buttonFallback = sanitiseLabel(t("buttonReserve") as string, "buttonReserve");
    const translationFallback = sanitiseLabel(tTranslation("reserve") as string, "reserve");
    const fallbackTranslation = sanitiseLabel(
      i18n.getFixedT(fallbackLang, "translation")("reserve") as string,
      "reserve",
    );

    return reserve ?? book ?? buttonFallback ?? translationFallback ?? fallbackReserveLabel ?? fallbackTranslation;
  })();

  const reserveAriaLabel = (() => {
    const fallback = t("buttonReserve") as string;
    if (fallback && fallback.trim() && fallback !== "buttonReserve") {
      return fallback;
    }
    return reserveLabel;
  })();

  const handleReserve = useCallback(() => {
    openModal("booking");
  }, [openModal]);

  // During tests, apply head tags to document.head to keep assertions simple
  const fallbackHeadDescriptors: MetaDescriptor[] | undefined = (() => {
    if (process.env.NODE_ENV !== "test") return undefined;
    const path = `/${lang}/404`;
    const url = `${BASE_URL}${path}`;
    const fallbackMeta = resolveI18nMeta(lang, "notFoundPage");
    const metaFromLoader = { title: (data?.title ?? "").trim(), description: (data?.desc ?? "").trim() };
    const title = metaFromLoader.title || fallbackMeta.title;
    const description = metaFromLoader.description || fallbackMeta.description;
    const image = buildCfImageUrl(OG_IMAGE_SOURCE, {
      width: OG_IMAGE.width,
      height: OG_IMAGE.height,
      quality: 85,
      format: "auto",
    });
    return buildRouteMeta({
      lang,
      title,
      description,
      url,
      path,
      image: { src: image, width: OG_IMAGE.width, height: OG_IMAGE.height },
      isPublished: false,
    }) as MetaDescriptor[];
  })();

  const fallbackHeadLinks: LinkDescriptor[] | undefined = (() => {
    if (process.env.NODE_ENV !== "test") return undefined;
    return buildRouteLinks() as LinkDescriptor[];
  })();

  useApplyFallbackHead(fallbackHeadDescriptors, fallbackHeadLinks);

  return (
    <Fragment>
      {process.env.NODE_ENV === "test" && (
        <Fragment>
          {(fallbackHeadDescriptors ?? []).map((d, i) => {
            const descriptor = d as Record<string, string | undefined>;
            const titleMaybe = descriptor["title"];
            if (typeof titleMaybe === "string" && titleMaybe) {
              return <title key={`t-${i}`}>{titleMaybe}</title>;
            }
            const tagName = descriptor["tagName"];
            if (tagName === "link") {
              const { ["tagName"]: _ignored, ["key"]: _key, ...rest } = descriptor;
              return <link key={`m-${i}`} {...rest} />;
            }
            const { ["key"]: _k, ["tagName"]: _tag, ...rest } = descriptor;
            return <meta key={`m-${i}`} {...rest} />;
          })}
          {(fallbackHeadLinks ?? []).map((l, i) => {
            const descriptor = l as unknown as Record<string, string | undefined> & { key?: string };
            const linkKey = descriptor["key"];
            const { ["key"]: _ignored, ...rest } = descriptor;
            return <link key={`l-${i}-${linkKey ?? ""}`} {...rest} />;
          })}
        </Fragment>
      )}
      <Page>
        <h1 className="mb-4 text-2xl font-semibold tracking-tight">{t("heading")}</h1>
        <p className="mb-2">{t("message")}</p>

        <nav aria-label={t("linksTitle")} className="mb-8">
          <ul className="mt-4 list-inside list-disc space-y-1 text-start">
            <li>
              <Link to={`/${lang}`} className={linkClasses}>
                {t("links.home")}
              </Link>
            </li>
            <li>
              <Link to={`/${lang}/${getSlug("deals", lang)}`} className={linkClasses}>
                {t("links.deals")}
              </Link>
            </li>
            <li>
              <Link to={`/${lang}/${getSlug("assistance", lang)}`} className={linkClasses}>
                {t("links.assistance")}
              </Link>
            </li>
            <li>
              <Link to={`/${lang}/${getSlug("about", lang)}`} className={linkClasses}>
                {t("links.about")}
              </Link>
            </li>
          </ul>
        </nav>
        <div className="flex flex-col items-center gap-4 sm:flex-row sm:justify-center">
          <Button asChild className="cta-light dark:cta-dark">
            <Link to={`/${lang}/${getSlug("rooms", lang)}`}>{t("buttonRooms")}</Link>
          </Button>
          <Button onClick={handleReserve} variant="outline" aria-label={reserveAriaLabel}>
            {reserveLabel}
          </Button>
        </div>
      </Page>
    </Fragment>
  );
}

const OG_IMAGE_SOURCE = "/img/positano-panorama.avif" as const;

export default memo(NotFoundView);

// src/components/rooms/FullscreenImage.tsx
import { type ComponentPropsWithoutRef, type KeyboardEvent, memo, useCallback } from "react";
import { useTranslation } from "react-i18next";
import clsx from "clsx";

import { CfImage } from "@acme/ui/atoms/CfImage";

import { getIntrinsicSize } from "@/lib/getIntrinsicSize";

type ModalProps = ComponentPropsWithoutRef<"div">;

function Modal({ className = "", ...props }: ModalProps): JSX.Element {
  return <div className={clsx("relative", "isolate", className)} {...props} />;
}

const CLOSE_KEYS = new Set(["Escape", "Enter", " ", "Space", "Spacebar"]);

/* Legacy → canonical path helper */
const resolveAsset = (p: string): string => p.replace(/^\/images\//, "/img/");

export interface FullscreenImageProps {
  src: string;
  alt: string;
  onClose: () => void;
  lang?: string;
}

/**
 * Full‑screen, click‑to‑close light‑box for room images.
 * Adds explicit `width` & `height` attrs to comply with CI rules
 * (see AGENTS.md § <Image> requirements).
 */
function FullscreenImage({ src, alt, onClose, lang }: FullscreenImageProps): JSX.Element {
  const translationOptions = lang ? { lng: lang } : undefined;
  const { t } = useTranslation("roomsPage", translationOptions);

  /* ────────────────────────────── handlers ───────────────────────────── */
  const handleKeyDown = useCallback(
    (e: KeyboardEvent<HTMLDivElement>) => {
      if (CLOSE_KEYS.has(e.key)) {
        e.preventDefault();
        onClose();
      }
    },
    [onClose]
  );

  /* ─────────────────────────── image sources ─────────────────────────── */
  const resolvedSrc = resolveAsset(src);

  /* ────────────────────────── intrinsic dims ─────────────────────────── */
  /* Dimensions are looked up once per src & memoised in getIntrinsicSize. */
  const { width: origW, height: origH } = getIntrinsicSize(resolvedSrc) ?? { width: 0, height: 0 };

  const aspectRatio = origW && origH ? `${origW}/${origH}` : "16/9";

  /* ────────────────────────────── render ─────────────────────────────── */
  return (
    <div className="relative">
      <Modal
        role="button"
        aria-label={t("fullscreen.closeAria")}
        tabIndex={0}
        onKeyDown={handleKeyDown}
        onClick={onClose}
        className={clsx(
          "pointer-coarse:p-4",
          "fixed",
          "inset-0",
          "z-50",
          "grid",
          "cursor-zoom-out",
          "place-items-center",
          "bg-brand-outline/80",
          "px-6",
          "py-10",
          "backdrop-blur-sm",
          "dark:bg-brand-outline/80"
        )}
      >
        <div
          className={clsx(
            "relative",
            "flex",
            "max-h-dvh",
            "w-full",
            "justify-center"
          )}
        >
          <CfImage
            src={resolvedSrc}
            alt={alt}
            preset="hero"
            priority
            quality={90}
            data-aspect={aspectRatio}
            className={clsx("max-h-dvh", "w-full", "object-contain")}
          />

          <span
            className={clsx(
              "pointer-events-none",
              "absolute",
              "start-3",
              "top-3",
              "rounded",
              "bg-brand-outline/70",
              "px-2",
              "py-1",
              "text-xs",
              "text-brand-bg",
              "dark:bg-brand-outline/80"
            )}
          >
            {t("fullscreen.clickToClose")}
          </span>
        </div>
      </Modal>
    </div>
  );
}

export default memo(FullscreenImage);

