// src/components/seo/GuideSectionsItemListStructuredData.tsx
import { memo } from "react";
import { useTranslation } from "react-i18next";
import { usePathname } from "next/navigation";

import { buildCanonicalUrl } from "@acme/ui/lib/seo";

import { BASE_URL } from "@/config/site";
import { useCurrentLanguage } from "@/hooks/useCurrentLanguage";
import type { GuideKey } from "@/routes.guides-helpers";
import getGuideResource from "@/routes/guides/utils/getGuideResource";
import { serializeJsonLdValue } from "@/utils/seo/jsonld";

type ListItem = { "@type": "ListItem"; position: number; name: string; description?: string };

interface Props {
  guideKey: GuideKey;
  name?: string;
  canonicalUrl?: string;
}

function GuideSectionsItemListStructuredData({ guideKey, name, canonicalUrl }: Props): JSX.Element | null {
  const lang = useCurrentLanguage();
  const pathname = usePathname() ?? "";
  const { t, ready } = useTranslation("guides", { lng: lang });

  const json = (() => {
    if (!ready) return "";
    // Prefer raw structured sections from the translator
    const raw = t(`content.${guideKey}.sections`, { returnObjects: true }) as unknown;
    let arr = Array.isArray(raw) ? (raw as Array<{ title?: unknown; body?: unknown }>) : [];

    let items: ListItem[] = arr.reduce((acc: ListItem[], s) => {
      const rawTitle = typeof s?.title === "string" ? s.title : String(s?.title ?? "");
      const itemName = rawTitle.trim();
      if (!itemName) return acc;
      let description: string | undefined;
      if (Array.isArray(s?.body)) {
        const first = (s.body as unknown[])
          .map((v) => (v == null ? "" : String(v)))
          .map((v) => v.trim())
          .find((v) => v.length > 0);
        description = first && first.length > 0 ? first : undefined;
      }
      const base: ListItem = { "@type": "ListItem", position: acc.length + 1, name: itemName };
      if (description) base.description = description;
      acc.push(base);
      return acc;
    }, []);

    // Fallback A: when sections are empty, try runtime guide resources
    if (items.length === 0) {
      try {
        const fromStore = getGuideResource<unknown>(lang, `content.${guideKey}.sections`);
        const fromStoreArr = Array.isArray(fromStore)
          ? (fromStore as Array<{ title?: unknown; body?: unknown }>)
          : [];
        if (fromStoreArr.length === 0 && lang !== "en") {
          const fromEn = getGuideResource<unknown>("en", `content.${guideKey}.sections`);
          arr = Array.isArray(fromEn) ? (fromEn as Array<{ title?: unknown; body?: unknown }>) : [];
        } else {
          arr = fromStoreArr;
        }
        if (arr.length > 0) {
          items = arr.reduce((acc: ListItem[], s) => {
            const rawTitle = typeof s?.title === "string" ? s.title : String(s?.title ?? "");
            const itemName = rawTitle.trim();
            if (!itemName) return acc;
            let description: string | undefined;
            if (Array.isArray(s?.body)) {
              const first = (s.body as unknown[])
                .map((v) => (v == null ? "" : String(v)))
                .map((v) => v.trim())
                .find((v) => v.length > 0);
              description = first && first.length > 0 ? first : undefined;
            }
            const base: ListItem = { "@type": "ListItem", position: acc.length + 1, name: itemName };
            if (description) base.description = description;
            acc.push(base);
            return acc;
          }, []);
        }
      } catch {
        // ignore store fallback errors; continue to schema fallback
      }
    }

    // Fallback B: when sections are still empty, attempt to build from a compact
    // schema definition under content.{guideKey}.schema with {title, description}
    if (items.length === 0) {
      try {
        const schemaRaw = t(`content.${guideKey}.schema`, { returnObjects: true }) as unknown;
        const schemaArr = Array.isArray(schemaRaw)
          ? (schemaRaw as Array<{ title?: unknown; description?: unknown }>)
          : [];
        const fromSchema: ListItem[] = [];
        for (const s of schemaArr) {
          const title = typeof s?.title === "string" ? s.title.trim() : String(s?.title ?? "").trim();
          if (!title) continue;
          const descRaw = typeof s?.description === "string" ? s.description : (s?.description as unknown);
          const description = descRaw == null ? "" : String(descRaw).trim();
          fromSchema.push({
            "@type": "ListItem",
            position: fromSchema.length + 1,
            name: title,
            // Always include description for schema fallback, even when blank,
            // to match test expectations.
            description,
          });
        }
        items = fromSchema;
      } catch {
        // ignore schema fallback errors; keep items empty
      }
    }

    if (items.length === 0) return "";

    const url =
      typeof canonicalUrl === "string" && canonicalUrl.length > 0
        ? canonicalUrl
        : buildCanonicalUrl(BASE_URL, pathname);
    const title = typeof name === "string" && name.trim().length > 0 ? name : "";

    return serializeJsonLdValue({
      "@context": "https://schema.org",
      "@type": "ItemList",
      inLanguage: lang,
      url,
      name: title,
      itemListElement: items,
    });
  })();

  if (!json) return null;

  return <script type="application/ld+json" dangerouslySetInnerHTML={{ __html: json }} />;
}

export default memo(GuideSectionsItemListStructuredData);


// src/components/seo/TravelHelpStructuredData.tsx
import { useTranslation } from "react-i18next";

import { buildCanonicalUrl } from "@acme/ui/lib/seo";

import { BASE_URL } from "@/config/site";
import { useCurrentLanguage } from "@/hooks/useCurrentLanguage";
import { guideSlug } from "@/routes.guides-helpers";
import NEARBY from "@/schema/travel-help/en-nearby.jsonld?raw";
import { buildFaqJsonLd } from "@/utils/buildFaqJsonLd";
import { HOTEL_ID } from "@/utils/schema";
import { serializeJsonLdValue } from "@/utils/seo/jsonld";
import { getSlug } from "@/utils/slug";
import { parseTravelHelpResource, type TravelHelpResource } from "@/utils/travelHelp";

import FaqJsonLdScript from "./FaqJsonLdScript";

export default function TravelHelpStructuredData() {
  const lang = useCurrentLanguage();
  const assistanceSlug = getSlug("assistance", lang);
  const slug = guideSlug(lang, "travelHelp");
  const pagePath = `/${lang}/${assistanceSlug}/${slug}`;
  const pageUrl = buildCanonicalUrl(BASE_URL, pagePath);

  const translation = useTranslation("travelHelp", { lng: lang });
  const i18nInstance = translation?.i18n;
  const fallbackLangOption = i18nInstance?.options?.fallbackLng;
  const fallbackLang = Array.isArray(fallbackLangOption)
    ? fallbackLangOption[0]
    : typeof fallbackLangOption === "string"
      ? fallbackLangOption
      : "en";

  const getResourceBundle = (lng: string): TravelHelpResource | undefined => {
    const fromDataByLanguage = (() => {
      if (typeof i18nInstance?.getDataByLanguage !== "function") {
        return undefined;
      }
      const data = i18nInstance.getDataByLanguage(lng);
      if (!data) return undefined;
      return data["travelHelp"] as TravelHelpResource | undefined;
    })();

    if (fromDataByLanguage) {
      return fromDataByLanguage;
    }

    if (typeof i18nInstance?.getFixedT !== "function") {
      return undefined;
    }

    const fixedT = i18nInstance.getFixedT(lng, "travelHelp");
    if (typeof fixedT !== "function") {
      return undefined;
    }

    const slugResult = fixedT("slug", { defaultValue: "" }) as unknown;
    const headings = fixedT("headings", { returnObjects: true }) as unknown;
    const content = fixedT("content", { returnObjects: true }) as unknown;

    const resource: TravelHelpResource = {};

    if (typeof slugResult === "string") {
      resource.slug = slugResult;
    }

    if (isRecord(headings)) {
      resource.headings = headings;
    }

    if (isRecord(content)) {
      resource.content = content;
    }

    if (resource.slug || resource.headings || resource.content) {
      return resource;
    }

    return undefined;
  };

  const resource = getResourceBundle(lang);
  const fallbackResource = getResourceBundle(fallbackLang);

  const travelHelp = parseTravelHelpResource(resource, fallbackResource);

  const faqPayload = buildFaqJsonLd(
    lang,
    pageUrl,
    travelHelp.items.map((item) => ({ question: item.question, answer: item.answer })),
  );
  const faqData = faqPayload
    ? {
        ...faqPayload,
        "@id": `${pageUrl}#faq`,
        isPartOf: { "@id": `${BASE_URL}#website` },
        mainEntityOfPage: pageUrl,
      }
    : null;
  // Parse and normalize NEARBY to use the canonical HOTEL_ID and inLanguage
  let nearbyJson = "";
  let nearbyParsed: NearbyGraph | undefined;
  try {
    const parsed = JSON.parse(NEARBY) as unknown;
    if (isNearbyGraph(parsed)) {
      const graphNodes = parsed["@graph"];
      if (Array.isArray(graphNodes) && areGraphNodes(graphNodes)) {
        parsed["@graph"] = graphNodes.map((node) => {
          if (hasIsLocatedInRecord(node)) {
            return { ...node, isLocatedIn: { "@id": HOTEL_ID } };
          }
          return node;
        });
      }

      parsed.inLanguage = lang;
      parsed.url = pageUrl;
      nearbyParsed = parsed;
      nearbyJson = serializeJsonLdValue(parsed);
    }
  } catch {
    nearbyJson = serializeJsonLdValue(NEARBY);
  }

  return (
    <>
      <FaqJsonLdScript data={faqData} />
      <script type="application/ld+json" dangerouslySetInnerHTML={{ __html: nearbyJson }} />
    </>
  );
}

type GraphNode = Record<string, unknown>;

type NearbyGraph = Record<string, unknown> & {
  "@graph"?: GraphNode[];
  inLanguage?: string;
  url?: string;
};

function isRecord(value: unknown): value is Record<string, unknown> {
  return typeof value === "object" && value !== null;
}

function isNearbyGraph(value: unknown): value is NearbyGraph {
  return isRecord(value);
}

function isGraphNode(value: unknown): value is GraphNode {
  return isRecord(value);
}

function areGraphNodes(nodes: unknown[]): nodes is GraphNode[] {
  return nodes.every(isGraphNode);
}

function hasIsLocatedInRecord(node: GraphNode): node is GraphNode & {
  isLocatedIn: Record<string, unknown>;
} {
  if (!("isLocatedIn" in node)) {
    return false;
  }

  const { isLocatedIn } = node;
  return isRecord(isLocatedIn);
}

// src/context/modal/global-modals/LanguageModal.tsx
/* -------------------------------------------------------------------------- */
/*  Language modal container                                                  */
/* -------------------------------------------------------------------------- */

import { useCallback } from "react";
import { useTranslation } from "react-i18next";
import { usePathname, useRouter, useSearchParams } from "next/navigation";

import type { LanguageModalCopy, LanguageOption } from "@acme/ui/organisms/modals";

import { IS_DEV } from "@/config/env";
import { useTheme } from "@/hooks/useTheme";
import enModals from "@/locales/en/modals.json";
import { type GuideKey,guideSlug, resolveGuideKeyFromSlug } from "@/routes.guides-helpers";
import { INTERNAL_SEGMENT_BY_KEY } from "@/routing/sectionSegments";
import { SLUG_KEYS, type SlugMap,SLUGS } from "@/slug-map";
import { preloadI18nNamespaces } from "@/utils/loadI18nNs";
import { translatePath } from "@/utils/translate-path";

import { type AppLanguage,CORE_LAYOUT_NAMESPACES, i18nConfig } from "../constants";
import { useModal } from "../hooks";
import { LanguageModal } from "../lazy-modals";

type SlugMapKey = keyof SlugMap;

function resolveSlugKeyFromSegment(segment: string | undefined, currentLang: AppLanguage): SlugMapKey | null {
  if (!segment) return null;
  const normalized = segment.toLowerCase();

  const currentLangKey =
    SLUG_KEYS.find((key) => SLUGS[key][currentLang].toLowerCase() === normalized) ?? null;
  if (currentLangKey) return currentLangKey;

  const anyLocalizedKey =
    SLUG_KEYS.find((key) =>
      i18nConfig.supportedLngs.some((lng) => SLUGS[key][lng].toLowerCase() === normalized),
    ) ?? null;
  if (anyLocalizedKey) return anyLocalizedKey;

  const internalEntry =
    (Object.entries(INTERNAL_SEGMENT_BY_KEY) as Array<[SlugMapKey, string]>).find(
      ([, internalSegment]) => internalSegment.toLowerCase() === normalized,
    ) ?? null;

  return internalEntry?.[0] ?? null;
}

function resolveGuideKeyFromPathSegment(
  segment: string | undefined,
  preferredLang: AppLanguage,
): GuideKey | null {
  if (!segment) return null;
  const candidateLangs = [
    preferredLang,
    i18nConfig.fallbackLng as AppLanguage,
    ...((i18nConfig.supportedLngs ?? []) as AppLanguage[]),
  ];
  const visited = new Set<AppLanguage>();

  for (const candidateLang of candidateLangs) {
    if (visited.has(candidateLang)) continue;
    visited.add(candidateLang);
    const key = resolveGuideKeyFromSlug(segment, candidateLang);
    if (key) return key;
  }

  return null;
}

function isGuidesTagSegment(segment: string | undefined): boolean {
  if (!segment) return false;
  const normalized = segment.toLowerCase();
  if (normalized === INTERNAL_SEGMENT_BY_KEY.guidesTags.toLowerCase()) {
    return true;
  }
  return i18nConfig.supportedLngs.some(
    (lng) => SLUGS.guidesTags[lng].toLowerCase() === normalized,
  );
}

function resolveAlternatePathForLanguage(nextLang: AppLanguage): string | null {
  if (typeof document === "undefined") return null;

  const selectors = [
    `link[rel="alternate"][hreflang="${nextLang}"]`,
    `link[rel="alternate"][hreflang="${nextLang.toLowerCase()}"]`,
  ];

  for (const selector of selectors) {
    const candidate = document.head.querySelector(selector) as HTMLLinkElement | null;
    const href = candidate?.getAttribute("href");
    if (!href) continue;
    try {
      const parsed = new URL(href, window.location.origin);
      const path = parsed.pathname;
      const normalizedPath = path === "/" ? path : path.replace(/\/+$/, "");
      if (normalizedPath === `/${nextLang}` || normalizedPath.startsWith(`/${nextLang}/`)) {
        return normalizedPath;
      }
    } catch {
      continue;
    }
  }

  return null;
}

function resolveModalCopy(
  t: (key: string, options?: Record<string, unknown>) => unknown,
  key: string,
  fallback: string,
): string {
  const localized = t(key, { defaultValue: "" });
  if (typeof localized === "string") {
    const trimmed = localized.trim();
    if (trimmed && trimmed !== key) return trimmed;
  }

  const fallbackValue = t(key, { lng: i18nConfig.fallbackLng, defaultValue: fallback });
  if (typeof fallbackValue === "string") {
    const trimmed = fallbackValue.trim();
    if (trimmed && trimmed !== key) return trimmed;
  }

  return fallback;
}

export function LanguageGlobalModal(): JSX.Element | null {
  const { closeModal } = useModal();
  const { isDark } = useTheme();
  const router = useRouter();
  const pathname = usePathname();
  const searchParams = useSearchParams();
  // Build a location-like object for compatibility
  const search = searchParams?.toString();
  const location = {
    pathname: pathname ?? "/",
    search: search ? `?${search}` : "",
    hash: typeof window !== "undefined" ? window.location.hash : "",
  };
  const navigate = useCallback((path: string, options?: { replace?: boolean }) => {
    if (options?.replace) {
      router.replace(path);
    } else {
      router.push(path);
    }
  }, [router]);

  const pathSegments = location.pathname.split("/").filter(Boolean);

  const curLang: AppLanguage = (() => {
    const first = pathSegments[0];
    return i18nConfig.supportedLngs.includes(first as AppLanguage)
      ? (first as AppLanguage)
      : (i18nConfig.fallbackLng as AppLanguage);
  })();

  const { t: tModals, i18n } = useTranslation("modals", { lng: curLang, useSuspense: false });

  const slugKey = resolveSlugKeyFromSegment(pathSegments[1], curLang);

  const candidates = [
    i18nConfig.fallbackLng as AppLanguage,
    ...((i18nConfig.supportedLngs ?? []) as AppLanguage[]),
  ];
  if (!candidates.includes(curLang)) {
    candidates.unshift(curLang);
  }
  const languageOptions: LanguageOption[] = Array.from(new Set(candidates)).map((lng) => {
    let label = lng.toUpperCase();
    if (typeof Intl !== "undefined" && typeof Intl.DisplayNames === "function") {
      try {
        const display = new Intl.DisplayNames([lng], { type: "language" });
        const resolved = display.of(lng);
        if (resolved) {
          const [firstGrapheme, ...rest] = Array.from(resolved);
          if (firstGrapheme) {
            label = `${firstGrapheme.toLocaleUpperCase(lng)}${rest.join("")}`;
          }
        }
      } catch {
        label = lng.toUpperCase();
      }
    }
    return { code: lng, label };
  });

  const languageCopy: LanguageModalCopy = {
    title: resolveModalCopy(tModals, "language.title", enModals.language.title),
    closeLabel: resolveModalCopy(tModals, "language.close", enModals.language.close),
  };

  const warmLayoutNamespaces = useCallback(async (target: AppLanguage): Promise<void> => {
    try {
      await preloadI18nNamespaces(target, CORE_LAYOUT_NAMESPACES);
    } catch (error) {
      if (IS_DEV) {
        console.error({ scope: "LanguageModal", event: "preloadLayoutNamespacesFailure", error });
      }
    }
  }, []);

  const changeLanguage = useCallback(
    async (nextLang: AppLanguage): Promise<void> => {
      await Promise.all([i18n.changeLanguage(nextLang), warmLayoutNamespaces(nextLang)]);
      if (nextLang === curLang) {
        closeModal();
        return;
      }

      const basePath = (() => {
        const alternatePath = resolveAlternatePathForLanguage(nextLang);
        if (alternatePath) {
          return alternatePath;
        }

        const rawSegments = location.pathname.split("/").filter(Boolean);
        const hasLangPrefix = i18nConfig.supportedLngs.includes(rawSegments[0] as AppLanguage);
        const remainder = hasLangPrefix ? rawSegments.slice(1) : rawSegments;

        const nextSegments: string[] = [nextLang];

        if (slugKey) {
          const translatedSlug = translatePath(slugKey, nextLang);
          nextSegments.push(translatedSlug);

          const trailingSegments = remainder.slice(1);

          if (slugKey === "experiences" && trailingSegments.length > 0) {
            const currentSecond = trailingSegments[0] ?? "";
            if (isGuidesTagSegment(currentSecond)) {
              nextSegments.push(translatePath("guidesTags", nextLang));
              nextSegments.push(...trailingSegments.slice(1));
              return nextSegments.length > 0 ? `/${nextSegments.filter(Boolean).join("/")}` : "/";
            }
            const matchedGuideKey = resolveGuideKeyFromPathSegment(currentSecond, curLang);
            if (matchedGuideKey) {
              nextSegments.push(guideSlug(nextLang, matchedGuideKey));
              nextSegments.push(...trailingSegments.slice(1));
            } else {
              nextSegments.push(...trailingSegments);
            }
          } else if (
            (slugKey === "assistance" || slugKey === "guides" || slugKey === "howToGetHere") &&
            trailingSegments.length > 0
          ) {
            const currentSecond = trailingSegments[0] ?? "";
            const matchedGuideKey = resolveGuideKeyFromPathSegment(currentSecond, curLang);
            if (matchedGuideKey) {
              nextSegments.push(guideSlug(nextLang, matchedGuideKey));
              nextSegments.push(...trailingSegments.slice(1));
            } else {
              nextSegments.push(...trailingSegments);
            }
          } else {
            // Assistance index or other pages — keep trailing segments as-is
            nextSegments.push(...trailingSegments);
          }
        } else {
          nextSegments.push(...remainder);
        }

        return nextSegments.length > 0 ? `/${nextSegments.filter(Boolean).join("/")}` : "/";
      })();

      navigate(`${basePath}${location.search}${location.hash}`, { replace: true });
      closeModal();
    },
    [closeModal, curLang, i18n, location, navigate, slugKey, warmLayoutNamespaces],
  );

  return (
    <LanguageModal
      isOpen
      onClose={closeModal}
      options={languageOptions}
      currentCode={curLang}
      onSelect={(code) => changeLanguage(code as AppLanguage)}
      copy={languageCopy}
      theme={isDark ? "dark" : "light"}
    />
  );
}

// apps/brikette/src/hooks/usePagePreload.ts
// Replaces duplicated useEffect patterns for page-level namespace preloading.

import { useEffect } from "react";

import { IS_DEV } from "@/config/env";
import i18n from "@/i18n";
import type { AppLanguage } from "@/i18n.config";
import { preloadI18nNamespaces, preloadNamespacesWithFallback } from "@/utils/loadI18nNs";

type UsePagePreloadOptions = {
  /** Primary language to load */
  lang: AppLanguage;
  /** Required namespaces (loaded with fallback) */
  namespaces: readonly string[];
  /** Optional namespaces (failures are silently ignored) */
  optionalNamespaces?: readonly string[];
  /** Mark required namespaces as optional too (default: false) */
  optional?: boolean;
};

/**
 * Preloads i18n namespaces on mount and syncs the i18n language.
 *
 * Extracts the repeated pattern found across all page content components:
 * ```
 * useEffect(() => {
 *   await preloadNamespacesWithFallback(lang, [...]);
 *   await i18n.changeLanguage(lang);
 * }, [lang]);
 * ```
 */
export function usePagePreload({
  lang,
  namespaces,
  optionalNamespaces,
  optional = false,
}: UsePagePreloadOptions): void {
  useEffect(() => {
    let cancelled = false;

    const load = async () => {
      try {
        await preloadNamespacesWithFallback(lang, namespaces, { optional });
        if (optionalNamespaces?.length) {
          await preloadI18nNamespaces(lang, optionalNamespaces, { optional: true });
        }
        if (!cancelled) {
          await i18n.changeLanguage(lang);
        }
      } catch (err) {
        if (IS_DEV) console.debug("[usePagePreload] failed", lang, namespaces, err);
      }
    };

    void load();
    return () => { cancelled = true; };
    // eslint-disable-next-line react-hooks/exhaustive-deps -- namespaces arrays are stable per component
  }, [lang]);
}

