// (blog namespace removed)

// react-i18next must not be imported in React Server Components (the server
// runtime can expose a React build without createContext). Guard on the React
// API surface before requiring the plugin.
type I18nextModule = import("i18next").Module;
let initReactI18next: I18nextModule | null = null;
try {

  const react = require("react") as { createContext?: unknown };
  if (typeof react.createContext === "function") {

    const reactI18next = require("react-i18next") as { initReactI18next?: unknown };
    const candidate = reactI18next.initReactI18next as unknown;
    if (
      candidate &&
      typeof candidate === "object" &&
      "type" in (candidate as Record<string, unknown>) &&
      "init" in (candidate as Record<string, unknown>) &&
      typeof (candidate as { init?: unknown }).init === "function"
    ) {
      initReactI18next = candidate as I18nextModule;
    }
  }
} catch {
  initReactI18next = null;
}

/*---------------------------------------------------------------*
 | Environment detection                                          |
 *---------------------------------------------------------------*/
// In Vitest/JSDOM, `window` exists but Node APIs are still available. Prefer this
// more permissive check when deciding if we can use Node FS helpers.
const canUseNodeFs = typeof process !== "undefined" && !!process.versions?.node;
/*---------------------------------------------------------------*
 | Node helpers are resolved lazily inside the backend branch     |
 | to avoid client-bundle warnings about node:module.             |
 *---------------------------------------------------------------*/

const TRANSPORT_TAG_LABELS: Record<string, string> = {
  en: "Transport",
  de: "Verkehr",
  es: "Transporte",
  fr: "Transports",
  it: "Trasporti",
  pt: "Transporte",
  ru: "Транспорт",
  zh: "交通",
  ja: "交通",
  ko: "교통",
};

const createGuidesTagsSeed = (label: string) =>
  ({
    tags: {
      transport: {
        label,
      },
    },
  }) satisfies Record<string, unknown>;

/*---------------------------------------------------------------*
 | Backend: load JSON by (lng, ns) in any runtime                 |
 *---------------------------------------------------------------*/
// Dynamic imports are handled by Next/webpack; Node contexts fall back to fs.

i18n.use(
  resourcesToBackend(async (lng, ns, cb: ReadCallback) => {
    if (ns === "guides") {
      const overrides = (globalThis as {
        __GUIDES_BACKEND_OVERRIDES__?: Record<string, unknown>;
      }).__GUIDES_BACKEND_OVERRIDES__;
      const overrideBundle = overrides?.[lng];
      if (overrideBundle) {
        if (process.env["DEBUG_GUIDE_TRANSLATIONS"] === "1") {
          // eslint-disable-next-line no-console -- Localised debug helper for guide coverage tests
          console.log(`[i18n] using guides override for ${lng}`);
        }
        cb(null, asResourceKey(overrideBundle));
        return;
      }
    }

    // Prefer the Node FS loader for the guides namespace when running under Node
    // (tests/scripts). This avoids a race where the runtime module discovery
    // hasn’t warmed yet and getGuidesBundle() returns a stub.
    if (ns === "guides") {
      try {
        const { loadGuidesNamespace } = await import("./locales/guides.backend");
        const bundle = await loadGuidesNamespace(lng, { canUseNodeFs });
        if (bundle) {
          cb(null, asResourceKey(bundle));
          return;
        }
      } catch {
        // fall through to other strategies
      }
    }

    // 1) Try bundle-aware import (browser/Next bundles)
    try {
      const data = await loadLocaleResource(lng, ns);
      if (typeof data !== "undefined") {
        cb(null, asResourceKey(data));
        return;
      }
    } catch {
      /* fall through */
    }

    // 2) Plain Node fallback via fs (prioritised under Node/vitest)
    if (canUseNodeFs) {
      try {
        const mod = await import("module");
        const { createRequire } = mod as unknown as typeof import("module");
        const req = createRequire(import.meta.url);
        const fs = req("fs") as typeof import("fs");
        const path = req("path") as typeof import("path");
        const candidates: string[] = [
          path.resolve(process.cwd(), "src/locales", lng, `${ns}.json`),
          path.resolve(process.cwd(), "apps/brikette/src/locales", lng, `${ns}.json`),
        ];
        try {
          // __dirname is not available in ESM – guard access so we don't throw
          // in environments where it is undefined.
          if (typeof __dirname === "string") {
            // eslint-disable-next-line @typescript-eslint/ban-ts-comment -- LINT-1007 [ttl=2026-12-31] __dirname is CJS-only and guarded at runtime
            // @ts-ignore __dirname only exists in CJS; guarded at runtime
            candidates.push(path.resolve(__dirname as unknown as string, "locales", lng, `${ns}.json`));
          }
        } catch {
          // ignore when __dirname is not defined under ESM
        }
        for (const file of candidates) {
          try {
            const data = fs.readFileSync(file, "utf8");
            cb(null, JSON.parse(data));
            return;
          } catch {
            /* try next */
          }
        }
      } catch (err) {
        cb(err as Error, undefined);
        return;
      }
    }

    // 3) Graceful empty object
    if (process.env.NODE_ENV !== "production") {
      console.warn(`[i18n] ${lng}/${ns}.json not found – falling back to empty object`);
    }
    cb(null, {});
  })
)
  // Some tests mock `react-i18next` without exporting `initReactI18next`.
  // Guard by falling back to a no-op plugin so i18n can still initialise.
  .use(initReactI18next ?? ({ type: "3rdParty", init() {} } as import("i18next").Module))
  .init({
    ...i18nConfig,
    // Ensure components re-render both when the language changes
    // and when lazy namespaces finish loading for that language.
    // This aligns runtime behavior with dev navigation where we
    // change the URL first and load namespaces after.
    react: {
      useSuspense: false,
      bindI18n: "languageChanged loaded",
      bindI18nStore: "added removed",
    },
    // Synchronous init for tests; namespaces still lazy‑load via backend
    initImmediate: false,
    // Seed a minimal dealsPage perks list for en so components depending on it
    // can render immediately in unit tests. Other namespaces load lazily.
    resources: (() => {
      const base: Record<string, import("i18next").ResourceLanguage> = {
        en: {
          translation: asResourceKey(EN_TRANSLATION),
          footer: asResourceKey(EN_FOOTER),
          // (legacy article namespaces removed - now served via guides)
          dealsPage: {
            // eslint-disable ds/no-hardcoded-copy -- LINT-1007 [ttl=2026-12-31] Non-UI seed values for tests; real copy lives in locales JSON
            // i18n-exempt -- LINT-1007 [ttl=2026-12-31]
            perksList: [
              {
                title: "Complimentary breakfast voucher",
                subtitle: "Included daily",
              },
              {
                title: "Exclusive direct booking discount",
                subtitle: "Auto-applied",
              },
              {
                title: "Welcome drink at the bar",
                subtitle: "House selection",
              },
            ],
            // eslint-enable ds/no-hardcoded-copy
          },
        },
      };

      for (const [lang, label] of Object.entries(TRANSPORT_TAG_LABELS)) {
        const seed = asResourceKey(createGuidesTagsSeed(label));
        const target = (base[lang] ??= {});
        (target as Record<string, import("i18next").ResourceKey>)["guides.tags"] = seed;
      }

      return base;
    })(),
  });

// ---------------------------------------------------------------------------
// Compatibility: i18next v24 no longer exposes `loadNamespaces` on the
// instance in some builds. A few tests and utilities rely on this helper to
// eagerly ensure a namespace is available before rendering assertions. To keep
// call‑sites stable across versions, provide a lightweight shim that defers to
// the backend connector when present.
// ---------------------------------------------------------------------------
const appI18n = i18n as typeof i18n & {
  loadNamespaces: (ns: string | string[]) => Promise<void>;
};

// Normalise the loadNamespaces helper across i18next versions so tests and
// runtime code can rely on it existing. i18next v24 removed it from some
// builds which caused callers like `i18n.loadNamespaces()` to throw.
appI18n.loadNamespaces = (ns: string | string[]): Promise<void> => {
  const namespaces = Array.isArray(ns) ? ns : [ns];
  return new Promise<void>((resolve, reject) => {
    // Prefer backendConnector.load which triggers our resourcesToBackend loader
    type BackendConnector = {
      load?: (
        languages: readonly string[],
        namespaces: readonly string[],
        callback: (error: unknown) => void,
      ) => void;
    };
    type I18nWithBackend = typeof appI18n & {
      services?: {
        backendConnector?: BackendConnector;
      };
    };

    const services = (appI18n as I18nWithBackend).services;
    const backend = services?.backendConnector;
    const fallback = i18n.options?.fallbackLng;
    const fallbackLang = Array.isArray(fallback) ? fallback[0] : (fallback as string | undefined);
    const lng = i18n.language || fallbackLang || "en";
    const languages = [lng];
    const namespaceList = namespaces as readonly string[];

    const emitLoaded = () => {
      if (typeof i18n.emit !== "function") return;
      const payload: Record<string, readonly string[]> = {};
      for (const language of languages) {
        if (language) {
          payload[language] = namespaceList;
        }
      }
      if (Object.keys(payload).length > 0) {
        i18n.emit("loaded", payload);
      }
    };
    if (backend && typeof backend.load === "function") {
      backend.load(languages, namespaceList, (err: unknown) => {
        if (err) {
          reject(err as Error);
          return;
        }
        emitLoaded();
        resolve();
      });
      return;
    }
    // Fallback: try reloading resources if available, otherwise resolve
    if (typeof i18n.reloadResources === "function") {
      i18n
        .reloadResources(languages, namespaceList)
        .then(() => {
          emitLoaded();
          resolve();
        })
        .catch(reject);
      return;
    }
    emitLoaded();
    resolve();
  });
};

export default appI18n;

/**
 * Smoothed Metrics - EWMA-based metric smoothing and forecasting
 *
 * Uses Exponentially Weighted Moving Average (EWMA) from @acme/lib for
 * smoothing noisy metrics and detecting anomalies. Ideal for dashboards,
 * monitoring, and trend detection.
 *
 * @example
 * ```typescript
 * const metric = new SmoothedMetric({ alpha: 0.3, name: "page-views" });
 *
 * // Update with raw values
 * metric.update(1250);
 * metric.update(1180);
 * metric.update(1420);
 *
 * // Get smoothed value (less noisy)
 * console.log(metric.smoothedValue);
 *
 * // Forecast future values
 * console.log(metric.forecast(5));
 * ```
 */

import {
  EWMA,
  HoltSmoothing,
  SimpleExponentialSmoothing,
} from "@acme/lib";

// ============================================================================
// SmoothedMetric - Simple EWMA wrapper
// ============================================================================

/**
 * Configuration for SmoothedMetric
 */
export interface SmoothedMetricConfig {
  /**
   * Smoothing factor (0-1).
   * - 0.1: Very smooth, slow to respond
   * - 0.3: Moderate smoothing (recommended)
   * - 0.5: Balanced
   * - 0.9: Very responsive, little smoothing
   */
  alpha?: number;
  /** Metric name/label */
  name?: string;
  /** Initial value (uses first observation if not set) */
  initialValue?: number;
}

/**
 * SmoothedMetric - Wrapper around EWMA for metric smoothing
 *
 * Provides a simple interface for smoothing noisy time-series data
 * with automatic anomaly detection thresholds.
 */
export class SmoothedMetric {
  private ewma: EWMA;
  private readonly name: string;
  private history: number[] = [];
  private readonly maxHistory: number = 100;

  constructor(config?: SmoothedMetricConfig) {
    this.ewma = new EWMA({
      alpha: config?.alpha ?? 0.3,
      initialValue: config?.initialValue,
    });
    this.name = config?.name ?? "metric";
  }

  /**
   * Update with a new raw value
   *
   * @param value Raw metric value
   * @returns Smoothed value after update
   */
  update(value: number): number {
    const smoothed = this.ewma.update(value);

    // Store in history for anomaly detection
    this.history.push(value);
    if (this.history.length > this.maxHistory) {
      this.history.shift();
    }

    return smoothed;
  }

  /**
   * Update with multiple values
   *
   * @param values Array of raw values (oldest first)
   * @returns Final smoothed value
   */
  updateAll(values: number[]): number {
    let last = this.ewma.value ?? 0;
    for (const value of values) {
      last = this.update(value);
    }
    return last;
  }

  /**
   * Current smoothed value
   */
  get smoothedValue(): number | null {
    return this.ewma.value;
  }

  /**
   * Raw value from last update
   */
  get lastRawValue(): number | undefined {
    return this.history[this.history.length - 1];
  }

  /**
   * Number of observations
   */
  get count(): number {
    return this.ewma.count;
  }

  /**
   * Metric name
   */
  getName(): string {
    return this.name;
  }

  /**
   * Check if a value is anomalous (outside expected range)
   *
   * Uses a simple threshold based on historical standard deviation.
   *
   * @param value Value to check
   * @param sigmas Number of standard deviations for threshold (default: 2)
   * @returns True if value is anomalous
   */
  isAnomaly(value: number, sigmas: number = 2): boolean {
    if (this.history.length < 10 || this.ewma.value === null) {
      return false;
    }

    const mean = this.history.reduce((a, b) => a + b, 0) / this.history.length;
    const variance =
      this.history.reduce((sum, v) => sum + (v - mean) ** 2, 0) /
      this.history.length;
    const std = Math.sqrt(variance);

    return Math.abs(value - mean) > sigmas * std;
  }

  /**
   * Forecast using current smoothed value (flat forecast)
   *
   * Note: For trend-aware forecasting, use TrendMetric instead.
   *
   * @param steps Number of steps to forecast
   * @returns Array of forecasted values (all same for simple EWMA)
   */
  forecast(steps: number): number[] {
    if (steps <= 0 || this.ewma.value === null) {
      return [];
    }
    return Array(steps).fill(this.ewma.value);
  }

  /**
   * Reset the metric
   */
  reset(): void {
    this.ewma.reset();
    this.history = [];
  }
}

// ============================================================================
// TrendMetric - Holt smoothing for trend-aware forecasting
// ============================================================================

/**
 * Configuration for TrendMetric
 */
export interface TrendMetricConfig {
  /** Level smoothing factor (default: 0.3) */
  alpha?: number;
  /** Trend smoothing factor (default: 0.1) */
  beta?: number;
  /** Metric name */
  name?: string;
}

/**
 * TrendMetric - Double exponential smoothing for trending data
 *
 * Uses Holt's linear smoothing to capture both level and trend,
 * providing more accurate forecasts for data with consistent trends.
 *
 * @example
 * ```typescript
 * const metric = new TrendMetric({ alpha: 0.3, beta: 0.1 });
 *
 * // Update with growing values
 * metric.fit([100, 110, 125, 140, 160]);
 *
 * // Forecast with trend continuation
 * console.log(metric.forecast(3)); // [~180, ~200, ~220]
 * ```
 */
export class TrendMetric {
  private holt: HoltSmoothing | null = null;
  private readonly alpha: number;
  private readonly beta: number;
  private readonly name: string;
  private data: number[] = [];

  constructor(config?: TrendMetricConfig) {
    this.alpha = config?.alpha ?? 0.3;
    this.beta = config?.beta ?? 0.1;
    this.name = config?.name ?? "trend-metric";
  }

  /**
   * Add a new data point and refit the model
   *
   * @param value New metric value
   */
  update(value: number): void {
    this.data.push(value);
    this.fit(this.data);
  }

  /**
   * Fit the model to historical data
   *
   * @param data Array of observations (oldest first)
   */
  fit(data: number[]): void {
    if (data.length < 2) {
      this.data = data;
      this.holt = null;
      return;
    }

    this.data = data;
    this.holt = new HoltSmoothing(this.alpha, this.beta);
    this.holt.fit(data);
  }

  /**
   * Forecast future values with trend
   *
   * @param steps Number of steps to forecast
   * @returns Array of forecasted values
   */
  forecast(steps: number): number[] {
    if (!this.holt) {
      return [];
    }
    return this.holt.forecast(steps);
  }

  /**
   * Get smoothed historical values
   */
  get fittedValues(): number[] {
    return this.holt?.fittedValues ?? [];
  }

  /**
   * Current level estimate
   */
  get level(): number | null {
    return this.holt?.level ?? null;
  }

  /**
   * Current trend estimate (change per period)
   */
  get trend(): number | null {
    return this.holt?.trend ?? null;
  }

  /**
   * Metric name
   */
  getName(): string {
    return this.name;
  }

  /**
   * Number of data points
   */
  get count(): number {
    return this.data.length;
  }

  /**
   * Reset the metric
   */
  reset(): void {
    this.data = [];
    this.holt = null;
  }
}

// ============================================================================
// Utility Functions
// ============================================================================

/**
 * Calculate a moving average for an array
 *
 * @param data Array of values
 * @param window Window size
 * @returns Array of moving averages
 */
export function movingAverage(data: number[], window: number): number[] {
  if (window <= 0 || window > data.length || data.length === 0) {
    return [];
  }

  const result: number[] = [];
  let sum = 0;

  // Initialize first window
  for (let i = 0; i < window; i++) {
    sum += data[i];
  }
  result.push(sum / window);

  // Slide window
  for (let i = window; i < data.length; i++) {
    sum += data[i] - data[i - window];
    result.push(sum / window);
  }

  return result;
}

/**
 * Detect trend direction from data
 *
 * @param data Array of values
 * @param minSamples Minimum samples for reliable detection (default: 5)
 * @returns "up", "down", "flat", or "insufficient"
 */
export function detectTrend(
  data: number[],
  minSamples: number = 5
): "up" | "down" | "flat" | "insufficient" {
  if (data.length < minSamples) {
    return "insufficient";
  }

  // Use simple linear regression slope
  const n = data.length;
  let sumX = 0;
  let sumY = 0;
  let sumXY = 0;
  let sumX2 = 0;

  for (let i = 0; i < n; i++) {
    sumX += i;
    sumY += data[i];
    sumXY += i * data[i];
    sumX2 += i * i;
  }

  const slope = (n * sumXY - sumX * sumY) / (n * sumX2 - sumX * sumX);
  const mean = sumY / n;

  // Threshold: slope is significant if it's > 5% of mean per period
  const threshold = Math.abs(mean) * 0.05;

  if (slope > threshold) {
    return "up";
  } else if (slope < -threshold) {
    return "down";
  } else {
    return "flat";
  }
}

// ============================================================================
// Singleton registry for named metrics
// ============================================================================

const smoothedMetrics = new Map<string, SmoothedMetric>();
const trendMetrics = new Map<string, TrendMetric>();

/**
 * Get or create a named smoothed metric
 */
export function getSmoothedMetric(
  name: string,
  config?: SmoothedMetricConfig
): SmoothedMetric {
  let metric = smoothedMetrics.get(name);
  if (!metric) {
    metric = new SmoothedMetric({ ...config, name });
    smoothedMetrics.set(name, metric);
  }
  return metric;
}

/**
 * Get or create a named trend metric
 */
export function getTrendMetric(
  name: string,
  config?: TrendMetricConfig
): TrendMetric {
  let metric = trendMetrics.get(name);
  if (!metric) {
    metric = new TrendMetric({ ...config, name });
    trendMetrics.set(name, metric);
  }
  return metric;
}

/**
 * SEO Audit Script for Guides v3.1.0
 *
 * Analyzes guide content for SEO quality and produces a 0-10 score with detailed recommendations.
 * Results are saved to guide-manifest-overrides.json.
 *
 * v3.1.0 changes:
 * - Includes section.list text in all analyses (words, links, readability, entities, facts)
 * - Fixes early-answer logic (checks within first N words, not intro length)
 * - Fixes title nearFront bug (no more -1 < 30)
 * - Adds focusKeyword support (manifest.focusKeyword / manifest.primaryQuery). Keyword checks skipped if absent.
 * - Scopes "local specifics / facts / year refs / first-hand" by template (prevents help pages being mis-graded)
 * - Softens FAQ thresholds (prevents filler FAQs)
 * - Introduces publish gate based on blockers + score threshold (blockers are criticalIssues)
 */
import fs from "fs/promises";
import path from "path";

import type { GuideKey } from "@/guides/slugs/keys";
import type { AppLanguage } from "@/i18n.config";
import {
  getGuideManifestEntry,
  type GuideManifestEntry,
  type GuideTemplate,
  type StructuredDataDeclaration,
} from "@/routes/guides/guide-manifest";
import type { SeoAuditResult } from "@/routes/guides/guide-manifest-overrides";

type GuideContent = {
  seo?: {
    title?: string;
    description?: string;
    image?: {
      width?: number;
      height?: number;
    };
  };
  lastUpdated?: string;
  intro?: string | string[];
  sections?: Array<{
    id?: string;
    title?: string;
    body?: string | string[];
    list?: string[];
    images?: Array<{
      src?: string;
      image?: string;
      alt?: string;
      caption?: string;
      width?: number;
      height?: number;
      sizeKB?: number;
      size?: number;
      format?: string;
      type?: string;
    }>;
  }>;
  faqs?: Array<{
    q?: string;
    a?: string | string[];
    question?: string;
    answer?: string | string[];
  }>;
  tips?: string[];
  warnings?: string[];
  essentialsSection?: { items?: string[] };
  costsSection?: { items?: string[] };
  callouts?: Record<string, string>;
};

type Template = GuideTemplate;

type TemplateThresholds = {
  wordCount: { min: number; low: number; optimal: [number, number]; high?: number };
  images: { min: number; low: number; optimal: [number, number]; high?: number };
  links: { min: number; low?: number; optimal: [number, number]; high?: number };
  faqs: { min: number; low: number; optimal: [number, number] };
};

// ============================================================================
// CONFIG
// ============================================================================

/**
 * Publish gating:
 * - Blockers (analysis.criticalIssues) always prevent publishing.
 * - Score threshold is a quality threshold (soft gate).
 */
const PUBLISH_SCORE_THRESHOLD = 7.5;
const FEATURE_SCORE_THRESHOLD = 9.0;

function escapeRegExp(input: string): string {
  return input.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
}

type TextSegment = { text: string; location: string };

function getTextSegments(content: GuideContent): TextSegment[] {
  const segments: TextSegment[] = [];

  const pushText = (text: unknown, location: string) => {
    if (typeof text !== "string") return;
    const trimmed = text.trim();
    if (trimmed.length === 0) return;
    segments.push({ text: trimmed, location });
  };

  const pushTextArray = (value: unknown, locationBase: string) => {
    if (typeof value === "string") {
      pushText(value, locationBase);
      return;
    }
    if (!Array.isArray(value)) return;
    value.forEach((p, i) => pushText(p, `${locationBase}[${i}]`));
  };

  pushTextArray(content.intro, "intro");

  if (Array.isArray(content.sections)) {
    content.sections.forEach((section, si) => {
      if (typeof section.title === "string" && section.title.trim().length > 0) {
        segments.push({ text: section.title, location: `sections[${si}].title` });
      }

      pushTextArray(section.body, `sections[${si}].body`);

      if (Array.isArray(section.list)) {
        section.list.forEach((li, lii) => {
          if (typeof li === "string" && li.trim().length > 0) {
            segments.push({ text: li, location: `sections[${si}].list[${lii}]` });
          }
        });
      }
    });
  }

  if (Array.isArray(content.faqs)) {
    content.faqs.forEach((faq, fi) => {
      pushText(faq.q ?? faq.question, `faqs[${fi}].question`);
      pushTextArray(faq.a ?? faq.answer, `faqs[${fi}].answer`);
    });
  }

  pushTextArray(content.tips, "tips");
  pushTextArray(content.warnings, "warnings");
  pushTextArray(content.essentialsSection?.items, "essentialsSection.items");
  pushTextArray(content.costsSection?.items, "costsSection.items");

  if (content.callouts && typeof content.callouts === "object") {
    Object.entries(content.callouts).forEach(([key, value]) => pushText(value, `callouts.${key}`));
  }

  return segments;
}

// ============================================================================
// HELPER FUNCTIONS
// ============================================================================

/**
 * Extract all text content from guide (for analysis purposes).
 */
function extractTextContent(content: GuideContent): string {
  return getTextSegments(content)
    .map((s) => s.text)
    .join(" ")
    .trim();
}

/**
 * Count words in guide content across intro, sections, lists, and FAQs.
 */
function countWords(content: GuideContent): number {
  const text = extractTextContent(content);
  return text.split(/\s+/).filter((w) => w.length > 0).length;
}

/**
 * Count headings in guide content (section titles are H2s).
 */
function countHeadings(content: GuideContent): number {
  if (!Array.isArray(content.sections)) return 0;
  return content.sections.filter((s) => s.title && s.title.trim().length > 0).length;
}

/**
 * Count H2 headings specifically.
 */
function countH2Headings(content: GuideContent): number {
  return countHeadings(content);
}

type InternalLink = { target: string; label: string; location: string };
const LINK_PATTERN = /%LINK:([^|%]+)\|([^%]+)%/g;

/**
 * Extract internal links from all text segments using %LINK:guideKey|label% pattern.
 */
function extractInternalLinks(content: GuideContent): InternalLink[] {
  const links: InternalLink[] = [];

  for (const seg of getTextSegments(content)) {
    for (const match of seg.text.matchAll(LINK_PATTERN)) {
      const target = (match[1] || "").trim();
      const label = (match[2] || "").trim();
      if (target.length > 0) {
        links.push({ target, label, location: seg.location });
      }
    }
  }

  return links;
}

/**
 * Validate internal link targets against the manifest.
 */
function validateInternalLinks(
  links: InternalLink[],
  currentGuideKey: GuideKey,
): { invalidTargets: string[]; invalidOccurrences: number; selfLinkCount: number } {
  const invalidTargets = new Set<string>();
  let invalidOccurrences = 0;
  let selfLinkCount = 0;

  for (const link of links) {
    if (link.target === currentGuideKey) {
      selfLinkCount++;
      continue;
    }

    const entry = getGuideManifestEntry(link.target as GuideKey);
    if (!entry) {
      invalidTargets.add(link.target);
      invalidOccurrences++;
    }
  }

  return { invalidTargets: Array.from(invalidTargets), invalidOccurrences, selfLinkCount };
}

/**
 * Count generic anchor text instances from %LINK labels.
 */
function countGenericAnchorsFromLinks(links: InternalLink[]): number {
  const generic = new Set([
    "here",
    "click here",
    "read more",
    "this link",
    "this page",
    "learn more",
  ]);

  let count = 0;
  for (const l of links) {
    const normalized = (l.label || "")
      .replace(/[\[\]().,!?]+/g, "")
      .trim()
      .toLowerCase();
    if (generic.has(normalized)) count++;
  }
  return count;
}

/**
 * Count images in guide content (gallery + inline images).
 */
type NormalizedGuideImage = {
  src: string;
  alt?: string;
  caption?: string;
  width?: number;
  height?: number;
  sizeKB?: number;
  size?: number;
  format?: string;
  type?: string;
};

function normalizeImageSrc(value: unknown): string | null {
  if (typeof value !== "string") return null;
  const trimmed = value.trim();
  return trimmed.length > 0 ? trimmed : null;
}

function getSectionImages(content: GuideContent): NormalizedGuideImage[] {
  const out: NormalizedGuideImage[] = [];
  if (!Array.isArray(content.sections)) return out;
  content.sections.forEach((section) => {
    if (!Array.isArray(section.images)) return;
    section.images.forEach((item) => {
      const src = normalizeImageSrc(item.src ?? item.image);
      if (!src) return;
      out.push({
        src,
        alt: item.alt,
        caption: item.caption,
        width: item.width,
        height: item.height,
        sizeKB: item.sizeKB,
        size: item.size,
        format: item.format,
        type: item.type,
      });
    });
  });
  return out;
}

function getAllImages(content: GuideContent): NormalizedGuideImage[] {
  const sectionImages = getSectionImages(content);

  // Also include top-level gallery images
  const galleryImages: NormalizedGuideImage[] = [];
  if (Array.isArray((content as any).gallery)) {
    (content as any).gallery.forEach((item: any) => {
      const src = normalizeImageSrc(item.src ?? item.image);
      if (!src) return;
      galleryImages.push({
        src,
        alt: item.alt,
        caption: item.caption,
        width: item.width,
        height: item.height,
        sizeKB: item.sizeKB,
        size: item.size,
        format: item.format,
        type: item.type,
      });
    });
  }

  return [...sectionImages, ...galleryImages];
}

function countImages(content: GuideContent): number {
  return getAllImages(content).length;
}

/**
 * Check if content includes year/date references for freshness.
 */
function checkYearReferences(content: GuideContent): boolean {
  const allText = extractTextContent(content);
  const currentYear = new Date().getFullYear();
  const nextYear = currentYear + 1;

  const hasYear = allText.includes(String(currentYear)) || allText.includes(String(nextYear));
  const hasSeason = /\b(spring|summer|fall|autumn|winter|seasonal)\b/i.test(allText);

  return hasYear || hasSeason;
}

/**
 * Get first paragraph word count.
 */
function getFirstParagraphWordCount(content: GuideContent): number {
  const firstPara =
    typeof content.intro === "string"
      ? content.intro
      : Array.isArray(content.intro)
        ? content.intro[0]
        : undefined;

  if (typeof firstPara !== "string") return 0;
  return firstPara.split(/\s+/).filter((w) => w.length > 0).length;
}

/**
 * Build a "lead text" slice (intro then earliest section content) capped at maxWords.
 */
function getLeadText(content: GuideContent, maxWords: number): string {
  const parts: string[] = [];
  let used = 0;

  const add = (chunk: string) => {
    const words = chunk.split(/\s+/).filter(Boolean);
    if (words.length === 0) return;

    const remaining = maxWords - used;
    if (remaining <= 0) return;

    if (words.length <= remaining) {
      parts.push(chunk);
      used += words.length;
    } else {
      parts.push(words.slice(0, remaining).join(" "));
      used += remaining;
    }
  };

  if (typeof content.intro === "string") {
    add(content.intro);
    if (used >= maxWords) return parts.join(" ").trim();
  } else if (Array.isArray(content.intro)) {
    for (const p of content.intro) {
      if (typeof p === "string") add(p);
      if (used >= maxWords) return parts.join(" ").trim();
    }
  }

  if (Array.isArray(content.sections)) {
    for (const section of content.sections) {
      if (typeof section.body === "string") {
        add(section.body);
        if (used >= maxWords) return parts.join(" ").trim();
      } else if (Array.isArray(section.body)) {
        for (const p of section.body) {
          if (typeof p === "string") add(p);
          if (used >= maxWords) return parts.join(" ").trim();
        }
      }
      if (Array.isArray(section.list)) {
        for (const li of section.list) {
          if (typeof li === "string") add(li);
          if (used >= maxWords) return parts.join(" ").trim();
        }
      }
      if (used >= maxWords) break;
    }
  }

  return parts.join(" ").trim();
}

/**
 * Check if answer appears early in content (within first 200 words).
 * Fixed: evaluates "presence within first N words" rather than requiring intro length <= N.
 */
function checkEarlyAnswer(content: GuideContent, template: Template): boolean {
  const lead = getLeadText(content, 200);
  if (!lead) return false;

  const lower = lead.toLowerCase();

  const patternsByTemplate: Record<Template, RegExp> = {
    help: /\b(how to|steps?|policy|rule|requirement|you can|you need|check[-\s]?in|check[-\s]?out|late|fee|deposit|passport|id|hours?)\b/i,
    experience: /\b(when|where|time|starts?|schedule|meet|join|cost|price|free|bring)\b/i,
    localGuide: /\b(how to get|getting there|minutes?|hours?|km|meters?|bus|train|ferry|metro|tram|ticket|price|€|\$|open|close)\b/i,
    pillar: /\b(itinerary|best|top|tips|how to get|getting there|minutes?|km|ticket|price|€|\$|open|close)\b/i,
  };

  const hasActionable = patternsByTemplate[template].test(lower);
  const hasConcrete =
    /\b\d{1,3}\s*(minutes?|hours?|km|meters?)\b/i.test(lower) ||
    /\b\d{1,2}:\d{2}\s*(am|pm)?\b/i.test(lower) ||
    /[€$£]/.test(lead);

  return hasActionable || hasConcrete;
}

/**
 * Detect placeholder content (TBD, lorem ipsum, empty sections).
 */
function detectPlaceholders(content: GuideContent): Array<{ type: string; location: string }> {
  const issues: Array<{ type: string; location: string }> = [];
  const allText = JSON.stringify(content);

  if (/lorem\s+ipsum/i.test(allText)) {
    issues.push({ type: "lorem", location: "content contains lorem ipsum placeholder text" });
  }

  const tbdMatches = allText.match(/\b(TBD|TODO|FIXME|XXX|PLACEHOLDER)\b/gi);
  if (tbdMatches) {
    tbdMatches.forEach((match) => {
      issues.push({ type: "tbd", location: `"${match}" marker found` });
    });
  }

  if (Array.isArray(content.sections)) {
    content.sections.forEach((section) => {
      const hasTitle = section.title && section.title.trim().length > 0;
      const hasBody =
        (typeof section.body === "string" && section.body.trim().length > 0) ||
        (Array.isArray(section.body) &&
          section.body.length > 0 &&
          section.body.some((item) => typeof item === "string" && item.trim().length > 0));

      const hasList =
        Array.isArray(section.list) &&
        section.list.length > 0 &&
        section.list.some((item) => typeof item === "string" && item.trim().length > 0);

      if (hasTitle && !hasBody && !hasList) {
        issues.push({ type: "empty", location: `Section "${section.title}" has no content` });
      }

      if (Array.isArray(section.list) && section.list.length === 0) {
        issues.push({ type: "empty", location: `Section "${section.title}" has empty list` });
      }
    });
  }

  if (/\b(coming soon|more info soon|details tba|to be announced)\b/i.test(allText)) {
    issues.push({ type: "comingSoon", location: '"Coming soon" or similar placeholder found' });
  }

  return issues;
}

/**
 * Check spelling and grammar (basic checks).
 * Scoped: English-only (avoids false penalties in other locales).
 */
function checkSpellingGrammar(
  content: GuideContent,
  locale: AppLanguage,
): {
  totalErrors: number;
  errorRate: number;
  errors: Array<{ type: string; example: string }>;
} {
  if (locale !== "en") {
    return { totalErrors: 0, errorRate: 0, errors: [] };
  }

  const allText = extractTextContent(content);
  const wordCount = allText.split(/\s+/).filter((w) => w.length > 0).length;
  const errors: Array<{ type: string; example: string }> = [];

  const commonMisspellings: Record<string, string> = {
    accomodation: "accommodation",
    seperate: "separate",
    definately: "definitely",
    occured: "occurred",
    recieve: "receive",
    beleive: "believe",
    existance: "existence",
    occassion: "occasion",
    goverment: "government",
    enviroment: "environment",
    restaraunt: "restaurant",
    begining: "beginning",
    transfered: "transferred",
    untill: "until",
    sucessful: "successful",
  };

  Object.entries(commonMisspellings).forEach(([wrong, correct]) => {
    const regex = new RegExp(`\\b${wrong}\\b`, "gi");
    const matches = allText.match(regex);
    if (matches) {
      matches.forEach(() => {
        errors.push({ type: "spelling", example: `"${wrong}" should be "${correct}"` });
      });
    }
  });

  const errorRate = wordCount > 0 ? (errors.length / wordCount) * 400 : 0;

  return {
    totalErrors: errors.length,
    errorRate,
    errors: errors.slice(0, 10),
  };
}

/**
 * Check image alt text coverage.
 */
function checkImageAltText(content: GuideContent): {
  coverage: number;
  total: number;
  withAlt: number;
} {
  let total = 0;
  let withAlt = 0;

  getAllImages(content).forEach((img) => {
    total++;
    if (typeof img.alt === "string" && (img.alt.trim().length > 0 || img.alt === "")) {
      withAlt++;
    }
  });

  const coverage = total > 0 ? (withAlt / total) * 100 : 100;
  return { coverage, total, withAlt };
}

/**
 * Check image file sizes.
 */
function checkImageSizes(content: GuideContent): { oversize: number; moderate: number; total: number } {
  let oversize = 0;
  let moderate = 0;
  let total = 0;

  getAllImages(content).forEach((img) => {
    const sizeKB = img.sizeKB || img.size || 0;
    if (!Number.isFinite(sizeKB) || sizeKB <= 0) return;
    total++;
    if (sizeKB > 500) oversize++;
    else if (sizeKB > 250) moderate++;
  });

  return { oversize, moderate, total };
}

/**
 * Check if modern image formats are used.
 */
function checkModernImageFormats(content: GuideContent): boolean {
  const images = getAllImages(content);
  if (images.length === 0) return true;

  return images.some((img) => {
    const format = (img.format || img.type || "").toLowerCase();
    const src = (img.src || "").toLowerCase().trim();
    return (
      format.includes("webp") ||
      format.includes("avif") ||
      src.endsWith(".webp") ||
      src.endsWith(".avif")
    );
  });
}

/**
 * Count local entities (places, landmarks).
 */
function countLocalEntities(content: GuideContent): number {
  const text = extractTextContent(content);

  const patterns = [
    /\b[A-Z][a-z]+\s+(Street|Road|Avenue|Piazza|Beach|Station|Stop|Port|Harbor)\b/g,
    /\b(Via|Piazza|Corso|Viale|Lungomare)\s+[A-Z][a-z]+/g,
    /\b[A-Z][a-z]+\s+(Hotel|Hostel|Restaurant|Bar|Café|Museum|Church|Cathedral)\b/g,
    /\b(SITA|bus\s+line|ferry\s+to|metro\s+line)\s+[A-Z0-9]+/gi,
  ];

  const uniqueMatches = new Set<string>();
  patterns.forEach((pattern) => {
    const matches = text.matchAll(pattern);
    for (const match of matches) {
      uniqueMatches.add(match[0]);
    }
  });

  return uniqueMatches.size;
}

/**
 * Count concrete facts per 500 words.
 */
function countConcreteFactsPer500Words(content: GuideContent): number {
  const text = extractTextContent(content);
  const wordCount = text.split(/\s+/).filter((w) => w.length > 0).length;

  let factCount = 0;

  const numbers = text.match(/\b\d{1,3}(,\d{3})*(\.\d+)?\s*(minutes?|hours?|km|meters?|€|\$|£)\b/gi);
  if (numbers) factCount += numbers.length;

  const times = text.match(/\b\d{1,2}:\d{2}\s*(am|pm)?\b/gi);
  if (times) factCount += times.length;

  const properNames = text.match(/\b[A-Z][a-z]+(?:\s+[A-Z][a-z]+)+\b/g);
  if (properNames) factCount += properNames.length * 0.5;

  const dates = text.match(
    /\b(January|February|March|April|May|June|July|August|September|October|November|December|spring|summer|fall|autumn|winter)\b/gi,
  );
  if (dates) factCount += dates.length * 0.5;

  const factsPer500 = wordCount > 0 ? (factCount / wordCount) * 500 : 0;
  return Math.round(factsPer500);
}

/**
 * Detect first-hand details.
 */
function detectFirstHandDetails(content: GuideContent): boolean {
  const text = extractTextContent(content).toLowerCase();

  const firstHandPatterns = [
    /\b(we tried|we visited|we recommend|from our experience|when we stayed)\b/i,
    /\b(from the hostel|our hostel|our staff|reception recommend)\b/i,
    /\b(I visited|I tried|I walked|I took)\b/i,
    /\b(tip from|according to our|based on our)\b/i,
  ];

  return firstHandPatterns.some((pattern) => pattern.test(text));
}

/**
 * Calculate average paragraph length.
 */
function calculateAvgParagraphLength(content: GuideContent): number {
  const paragraphs: string[] = [];

  const pushText = (value: unknown) => {
    if (typeof value !== "string") return;
    const trimmed = value.trim();
    if (trimmed.length === 0) return;
    paragraphs.push(trimmed);
  };

  const pushTextArray = (value: unknown) => {
    if (typeof value === "string") {
      pushText(value);
      return;
    }
    if (!Array.isArray(value)) return;
    value.forEach((v) => pushText(v));
  };

  pushTextArray(content.intro);

  if (Array.isArray(content.sections)) {
    content.sections.forEach((section) => {
      pushTextArray(section.body);
      if (Array.isArray(section.list)) section.list.forEach((li) => pushText(li));
    });
  }

  pushTextArray(content.tips);
  pushTextArray(content.warnings);
  pushTextArray(content.essentialsSection?.items);
  pushTextArray(content.costsSection?.items);

  if (Array.isArray(content.faqs)) {
    content.faqs.forEach((faq) => {
      pushText(faq.q ?? faq.question);
      pushTextArray(faq.a ?? faq.answer);
    });
  }

  if (content.callouts && typeof content.callouts === "object") {
    Object.values(content.callouts).forEach((value) => pushText(value));
  }

  if (paragraphs.length === 0) return 0;

  const wordCounts = paragraphs.map((p) => {
    if (typeof p !== "string") return 0;
    return p.split(/\s+/).filter((w) => w.length > 0).length;
  });

  const totalWords = wordCounts.reduce((sum, count) => sum + count, 0);
  return Math.round(totalWords / paragraphs.length);
}

/**
 * Calculate average sentence length.
 */
function calculateAvgSentenceLength(content: GuideContent): number {
  const text = extractTextContent(content);
  const sentences = text.split(/[.!?]+/).filter((s) => s.trim().length > 0);
  if (sentences.length === 0) return 0;

  const wordCounts = sentences.map((s) => s.split(/\s+/).filter((w) => w.length > 0).length);
  const totalWords = wordCounts.reduce((sum, count) => sum + count, 0);
  return Math.round(totalWords / sentences.length);
}

/**
 * Count syllables in a word (English-only heuristic).
 */
function countSyllables(word: string): number {
  const cleaned = word.toLowerCase().replace(/[^a-z]/g, "");
  if (cleaned.length <= 3) return 1;

  const vowels = "aeiouy";
  let count = 0;
  let previousWasVowel = false;

  for (let i = 0; i < cleaned.length; i++) {
    const isVowel = vowels.includes(cleaned[i]);
    if (isVowel && !previousWasVowel) count++;
    previousWasVowel = isVowel;
  }

  if (cleaned.endsWith("e")) count--;

  return Math.max(1, count);
}

/**
 * Calculate reading grade level (Flesch-Kincaid).
 * Scoped: English-only (returns undefined otherwise).
 */
function calculateReadingGrade(content: GuideContent, locale: AppLanguage): number | undefined {
  if (locale !== "en") return undefined;

  const text = extractTextContent(content);
  const words = text.split(/\s+/).filter((w) => w.length > 0);
  const sentences = text.split(/[.!?]+/).filter((s) => s.trim().length > 0);
  const syllables = words.reduce((count, word) => count + countSyllables(word), 0);

  if (words.length === 0 || sentences.length === 0) return 0;

  const avgWordsPerSentence = words.length / sentences.length;
  const avgSyllablesPerWord = syllables / words.length;

  const grade = 0.39 * avgWordsPerSentence + 11.8 * avgSyllablesPerWord - 15.59;
  return Math.max(0, Math.round(grade * 10) / 10);
}

/**
 * Get a focus keyword/primary query (preferred: manifest.focusKeyword).
 * If absent, returns null (keyword checks are skipped).
 */
function getFocusKeyword(manifest: Pick<GuideManifestEntry, "focusKeyword" | "primaryQuery">): string | null {
  const direct = [manifest.focusKeyword, manifest.primaryQuery].find(
    (v): v is string => typeof v === "string" && v.trim().length >= 3,
  );
  return direct?.trim() ?? null;
}

function checkFocusKeywordInTitle(
  title: string,
  focusKeyword: string,
): { present: boolean; nearFront: boolean } {
  const t = (title || "").toLowerCase();
  const k = focusKeyword.toLowerCase().trim();
  if (!k) return { present: false, nearFront: false };

  const idx = t.indexOf(k);
  return {
    present: idx >= 0,
    nearFront: idx >= 0 && idx < 30,
  };
}

function checkFocusKeywordInFirstNWords(
  content: GuideContent,
  focusKeyword: string,
  nWords: number,
): boolean {
  const slice = getLeadText(content, nWords).toLowerCase();
  const k = focusKeyword.toLowerCase().trim();
  if (!k) return false;
  return slice.includes(k);
}

/**
 * Calculate keyword stuffing rate (exact phrase) using focusKeyword when available.
 */
function calculateKeywordStuffingRate(content: GuideContent, focusKeyword: string | null): number {
  if (!focusKeyword) return 0;

  const text = extractTextContent(content).toLowerCase();
  const totalWords = text.split(/\s+/).filter((w) => w.length > 0).length;
  if (totalWords === 0) return 0;

  const phrase = focusKeyword.trim().toLowerCase();
  if (phrase.length < 3) return 0;

  const pattern = new RegExp(`(?:^|\\b)${escapeRegExp(phrase)}(?:\\b|$)`, "gi");
  const occurrences = (text.match(pattern) || []).length;

  const phraseWordCount = phrase.split(/\s+/).filter(Boolean).length || 1;
  const phraseRate = ((occurrences * phraseWordCount) / totalWords) * 100;

  return Math.round(phraseRate * 10) / 10;
}

function countMatches(text: string, pattern: RegExp): number {
  const flags = pattern.flags.includes("g") ? pattern.flags : `${pattern.flags}g`;
  const global = new RegExp(pattern.source, flags);
  return Array.from(text.matchAll(global)).length;
}

/**
 * Analyze CTAs (call-to-action elements).
 */
function analyzeCTAs(
  content: GuideContent,
): {
  hasPrimary: boolean;
  hasAboveFold: boolean;
  contextualCount: number;
} {
  const text = extractTextContent(content).toLowerCase();
  const intro = Array.isArray(content.intro) ? content.intro.join(" ").toLowerCase() : "";

  const ctaPatterns = [
    /\b(book now|check availability|reserve|join us|contact us|whatsapp|call reception)\b/i,
    /%LINK:[^|]+\|(book|reserve|contact|join)/i,
  ];

  const hasPrimary = ctaPatterns.some((pattern) => pattern.test(text));
  const hasAboveFold = ctaPatterns.some((pattern) => pattern.test(intro));

  const contextualPatterns = [
    /\b(from our hostel|from the hostel|walking distance from|our staff|ask reception)\b/i,
    /\b(book through us|we can arrange|we offer|available at reception)\b/i,
  ];

  const contextualCount = contextualPatterns.reduce((sum, pattern) => sum + countMatches(text, pattern), 0);
  return { hasPrimary, hasAboveFold, contextualCount };
}

// ============================================================================
// TEMPLATE THRESHOLDS (FAQ softened)
// ============================================================================

const TEMPLATE_THRESHOLDS: Record<Template, TemplateThresholds> = {
  help: {
    wordCount: { min: 600, low: 800, optimal: [800, 1400], high: 1800 },
    images: { min: 1, low: 2, optimal: [1, 4], high: 8 },
    links: { min: 3, optimal: [3, 8], high: 12 },
    faqs: { min: 0, low: 2, optimal: [2, 6] },
  },
  experience: {
    wordCount: { min: 1000, low: 1200, optimal: [1200, 2200] },
    images: { min: 6, low: 8, optimal: [8, 15] },
    links: { min: 3, low: 5, optimal: [5, 10] },
    faqs: { min: 0, low: 2, optimal: [2, 6] },
  },
  localGuide: {
    wordCount: { min: 1600, low: 2000, optimal: [2000, 3200] },
    images: { min: 8, low: 10, optimal: [10, 20] },
    links: { min: 5, low: 8, optimal: [8, 15] },
    faqs: { min: 2, low: 4, optimal: [4, 10] },
  },
  pillar: {
    wordCount: { min: 2500, low: 3000, optimal: [3000, 5000] },
    images: { min: 12, low: 15, optimal: [15, 30] },
    links: { min: 10, low: 15, optimal: [15, 25] },
    faqs: { min: 3, low: 6, optimal: [6, 12] },
  },
};

/**
 * Determine template type from manifest and content.
 * Improvement: prefer explicit manifest.template when available.
 */
function determineTemplate(manifest: GuideManifestEntry, wordCount: number): Template {
  if (manifest.template) return manifest.template;

  const isPillarGuide =
    wordCount > 2500 ||
    manifest.relatedGuides.length >= 8;

  if (isPillarGuide) return "pillar";
  if (manifest.primaryArea === "help") return "help";
  if (manifest.primaryArea === "experience") return "experience";
  if (manifest.primaryArea === "howToGetHere") return "localGuide";

  return "localGuide";
}

function getStructuredDataType(declaration: StructuredDataDeclaration): string {
  return typeof declaration === "string" ? declaration : declaration.type;
}

// ============================================================================
// METRICS EXTRACTION
// ============================================================================

function extractAuditMetrics(
  content: GuideContent,
  manifest: GuideManifestEntry,
  guideKey: GuideKey,
  wordCount: number,
  template: GuideTemplate,
  locale: AppLanguage,
  shouldCheckLocalSpecifics: boolean,
  shouldCheckFirstHand: boolean,
  shouldCheckEarlyAnswer: boolean,
  timeSensitive: boolean,
) {
  const focusKeyword = getFocusKeyword(manifest);

  const internalLinks = extractInternalLinks(content);
  const linkValidation = validateInternalLinks(internalLinks, guideKey);
  const genericAnchorCount = countGenericAnchorsFromLinks(internalLinks);

  const imageAltCoverage = checkImageAltText(content);
  const imageSizes = checkImageSizes(content);
  const placeholderIssues = detectPlaceholders(content);
  const spellingErrors = checkSpellingGrammar(content, locale);
  const h2Alignment = checkH2AlignmentToQueries(content);
  const ctaAnalysis = analyzeCTAs(content);
  const allImages = getAllImages(content);

  const title = content.seo?.title || "";
  const keywordInTitle = focusKeyword
    ? checkFocusKeywordInTitle(title, focusKeyword)
    : { present: false, nearFront: false };

  const readingGradeLevel = calculateReadingGrade(content, locale);

  const metrics: Record<string, number | undefined> = {
    // Meta tags
    metaTitleLength: content.seo?.title?.length ?? 0,
    metaDescriptionLength: content.seo?.description?.length ?? 0,
    hasTitleTag: content.seo?.title ? 1 : 0,
    hasMetaDescription: content.seo?.description ? 1 : 0,

    // Content
    contentWordCount: wordCount,
    headingCount: countHeadings(content),
    h2Count: countH2Headings(content),
    hasH1: content.seo?.title ? 1 : 0,

    // Links & Media
    internalLinkCount: internalLinks.length,
    invalidInternalLinkOccurrences: linkValidation.invalidOccurrences,
    faqCount: content.faqs?.length ?? 0,
    imageCount: allImages.length,

    // HTML hygiene (lightweight — still mostly content-only)
    urlSlugLength: manifest.slug.length,
    hasOgTitle: content.seo?.title ? 1 : 0,
    hasOgDescription: content.seo?.description ? 1 : 0,
    hasOgImage: content.seo?.image || allImages[0] ? 1 : 0,

    // Content completeness
    firstParagraphLength: getFirstParagraphWordCount(content),
    hasEarlyAnswer: shouldCheckEarlyAnswer && checkEarlyAnswer(content, template) ? 1 : 0,
    hasPlaceholders: placeholderIssues.length > 0 ? 1 : 0,

    // Spell/grammar (en-only)
    errorRate: locale === "en" ? spellingErrors.errorRate : undefined,

    // Image quality
    imageAltCoverage: imageAltCoverage.coverage,
    imageAltTotal: imageAltCoverage.total,
    imageAltWithAlt: imageAltCoverage.withAlt,
    imageOversizeCount: imageSizes.oversize,
    imageModerateCount: imageSizes.moderate,
    featuredImageWidth: content.seo?.image?.width || allImages[0]?.width || 0,
    usesModernFormats: checkModernImageFormats(content) ? 1 : 0,

    // Content strategy (scoped)
    localEntityCount: shouldCheckLocalSpecifics ? countLocalEntities(content) : undefined,
    concreteFactsDensity: shouldCheckLocalSpecifics ? countConcreteFactsPer500Words(content) : undefined,
    hasFirstHandDetails: shouldCheckFirstHand && detectFirstHandDetails(content) ? 1 : 0,

    // Readability
    avgParagraphLength: calculateAvgParagraphLength(content),
    avgSentenceLength: calculateAvgSentenceLength(content),
    readingGradeLevel,

    // Keyword optimization (scoped by focusKeyword presence)
    hasFocusKeyword: focusKeyword ? 1 : 0,
    focusKeywordInTitle: focusKeyword && keywordInTitle.present ? 1 : 0,
    focusKeywordNearFront: focusKeyword && keywordInTitle.nearFront ? 1 : 0,
    focusKeywordEarly: focusKeyword && checkFocusKeywordInFirstNWords(content, focusKeyword, 150) ? 1 : 0,
    h2AlignmentCount: h2Alignment.aligned,
    h2TotalCount: h2Alignment.total,
    keywordStuffingRate: calculateKeywordStuffingRate(content, focusKeyword),

    // Internal link quality
    genericAnchorCount,

    // Structured data (presence only; render-time validation should be a separate stage)
    hasBreadcrumbs: manifest.structuredData.some((s) => getStructuredDataType(s) === "BreadcrumbList") ? 1 : 0,
    hasArticleSchema: manifest.structuredData.some((s) => getStructuredDataType(s) === "Article") ? 1 : 0,

    // CTAs
    hasPrimaryCTA: ctaAnalysis.hasPrimary ? 1 : 0,
    hasAboveFoldCTA: ctaAnalysis.hasAboveFold ? 1 : 0,
    contextualCTACount: ctaAnalysis.contextualCount,

    // Freshness (scoped)
    hasLastUpdated: content.lastUpdated ? 1 : 0,
    hasYearReferences: timeSensitive && checkYearReferences(content) ? 1 : 0,
  };

  return {
    metrics,
    linkValidation,
    imageAltCoverage,
    imageSizes,
    placeholderIssues,
    spellingErrors,
    keywordInTitle,
    focusKeyword,
  };
}

// ============================================================================
// SCORING HELPER FUNCTIONS
// ============================================================================

type ScoringContext = {
  score: number;
  issues: Array<{ issue: string; impact: number }>;
  improvements: Array<{ issue: string; impact: number }>;
  strengths: string[];
};

function scoreLinkIntegrity(
  context: ScoringContext,
  linkValidation: ReturnType<typeof validateInternalLinks>,
): void {
  if (linkValidation.invalidOccurrences > 0) {
    const impact = 1.0;
    context.score -= impact;
    context.issues.push({
      issue: `Invalid internal links: ${linkValidation.invalidOccurrences} occurrence(s) pointing to unknown guide keys (${linkValidation.invalidTargets.join(", ")})`,
      impact,
    });
  } else {
    context.strengths.push("All internal links resolve to valid guide keys");
  }
}

function scoreMetaTags(
  context: ScoringContext,
  content: GuideContent,
  metrics: Record<string, number | undefined>,
): void {
  if (!content.seo?.title) {
    const impact = 1.0;
    context.score -= impact;
    context.issues.push({ issue: "Missing meta title", impact });
  } else {
    const len = metrics.metaTitleLength ?? 0;
    if (len < 35) {
      const impact = 0.4;
      context.score -= impact;
      context.improvements.push({ issue: `Meta title too short (${len} chars, target 35-65)`, impact });
    } else if (len > 65) {
      const impact = 0.4;
      context.score -= impact;
      context.improvements.push({ issue: `Meta title too long (${len} chars, target 35-65)`, impact });
    } else {
      context.strengths.push(`Good meta title length (${len} chars)`);
    }
  }

  if (!content.seo?.description) {
    const impact = 0.8;
    context.score -= impact;
    context.issues.push({ issue: "Missing meta description", impact });
  } else {
    const len = metrics.metaDescriptionLength ?? 0;
    if (len < 120) {
      const impact = 0.3;
      context.score -= impact;
      context.improvements.push({ issue: `Meta description short (${len} chars, target ~120-170)`, impact });
    } else if (len > 170) {
      const impact = 0.3;
      context.score -= impact;
      context.improvements.push({ issue: `Meta description long (${len} chars, target ~120-170)`, impact });
    } else {
      context.strengths.push(`Good meta description length (${len} chars)`);
    }
  }
}

function scorePlaceholders(
  context: ScoringContext,
  placeholderIssues: ReturnType<typeof detectPlaceholders>,
): void {
  if (placeholderIssues.length === 0) {
    context.strengths.push("All content is finalized (no placeholders)");
    return;
  }

  const hasLoremIpsum = placeholderIssues.some((p) => p.type === "lorem");
  if (hasLoremIpsum) {
    const impact = 1.0;
    context.score -= impact;
    context.issues.push({ issue: "Lorem ipsum placeholder text found - replace with real content", impact });
  }

  const tbdCount = placeholderIssues.filter((p) => p.type === "tbd").length;
  if (tbdCount > 0) {
    const impact = Math.min(tbdCount * 0.5, 1.0);
    context.score -= impact;
    context.issues.push({
      issue: `${tbdCount} TBD/TODO marker${tbdCount > 1 ? "s" : ""} found - complete all sections before publishing`,
      impact,
    });
  }

  const emptyCount = placeholderIssues.filter((p) => p.type === "empty").length;
  if (emptyCount > 0) {
    const impact = Math.min(emptyCount * 0.3, 0.6);
    context.score -= impact;
    context.improvements.push({
      issue: `${emptyCount} empty section${emptyCount > 1 ? "s" : ""} - add content or remove`,
      impact,
    });
  }

  const comingSoonCount = placeholderIssues.filter((p) => p.type === "comingSoon").length;
  if (comingSoonCount > 0) {
    const impact = 0.3;
    context.score -= impact;
    context.improvements.push({ issue: '"Coming soon" placeholder found - complete content before publishing', impact });
  }
}

function scoreWordCount(
  context: ScoringContext,
  wordCount: number,
  template: GuideTemplate,
  thresholds: typeof TEMPLATE_THRESHOLDS[GuideTemplate],
): void {
  if (wordCount < thresholds.wordCount.min) {
    const impact = 1.0;
    context.score -= impact;
    context.issues.push({
      issue: `Content too short (${wordCount} words, ${template} template needs ${thresholds.wordCount.min}+)`,
      impact,
    });
  } else if (wordCount < thresholds.wordCount.low) {
    const impact = 0.4;
    context.score -= impact;
    context.improvements.push({
      issue: `Content below optimal (${wordCount} words, target ${thresholds.wordCount.low}+ for ${template})`,
      impact,
    });
  } else if (thresholds.wordCount.high && wordCount > thresholds.wordCount.high) {
    const impact = 0.2;
    context.score -= impact;
    context.improvements.push({
      issue: `Content verbose (${wordCount} words, ${template} usually works best under ${thresholds.wordCount.high})`,
      impact,
    });
  } else {
    context.strengths.push(`${template === "help" ? "Concise" : "Comprehensive"} content at ${wordCount} words`);
  }
}

function scoreImages(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
  template: GuideTemplate,
  thresholds: typeof TEMPLATE_THRESHOLDS[GuideTemplate],
): void {
  if ((metrics.imageCount ?? 0) < thresholds.images.min) {
    const impact = template === "help" ? 0.3 : 0.5;
    context.score -= impact;
    context.improvements.push({
      issue: `Too few images (${metrics.imageCount}, ${template} template suggests ${thresholds.images.min}+)`,
      impact,
    });
  } else if ((metrics.imageCount ?? 0) < thresholds.images.low) {
    const impact = 0.2;
    context.score -= impact;
    context.improvements.push({
      issue: `Add ${thresholds.images.low - (metrics.imageCount ?? 0)} more images (currently ${metrics.imageCount}, target ${thresholds.images.low}+ for ${template})`,
      impact,
    });
  } else {
    context.strengths.push(`Good visual content with ${metrics.imageCount} images`);
  }
}

function scoreInternalLinks(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
  template: GuideTemplate,
  thresholds: typeof TEMPLATE_THRESHOLDS[GuideTemplate],
): void {
  if ((metrics.internalLinkCount ?? 0) < thresholds.links.min) {
    const impact = 0.4;
    context.score -= impact;
    context.improvements.push({
      issue: `Too few internal links (${metrics.internalLinkCount}, ${template} needs ${thresholds.links.min}+)`,
      impact,
    });
  } else {
    context.strengths.push(`Strong internal linking with ${metrics.internalLinkCount} links`);
  }
}

function scoreFAQs(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
  template: GuideTemplate,
  thresholds: typeof TEMPLATE_THRESHOLDS[GuideTemplate],
): void {
  if ((metrics.faqCount ?? 0) < thresholds.faqs.min) {
    const impact = 0.2;
    context.score -= impact;
    context.improvements.push({
      issue: `Add FAQs if users commonly ask follow-ups (currently ${metrics.faqCount}, suggested ${thresholds.faqs.min}+)`,
      impact,
    });
  } else if ((metrics.faqCount ?? 0) < thresholds.faqs.low) {
    const impact = 0.15;
    context.score -= impact;
    context.improvements.push({
      issue: `Consider adding ${thresholds.faqs.low - (metrics.faqCount ?? 0)} more FAQ(s) (currently ${metrics.faqCount}, target ${thresholds.faqs.low}+ for ${template})`,
      impact,
    });
  } else if ((metrics.faqCount ?? 0) > 0) {
    context.strengths.push(`FAQ section present with ${metrics.faqCount} questions`);
  }
}

function scoreHeadingStructure(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
  wordCount: number,
): void {
  if ((metrics.headingCount ?? 0) === 0) {
    const impact = 0.5;
    context.score -= impact;
    context.improvements.push({ issue: "No section headings found (add H2 sections for skimmability)", impact });
  } else if ((metrics.headingCount ?? 0) < 3 && wordCount > 1000) {
    const impact = 0.3;
    context.score -= impact;
    context.improvements.push({
      issue: `Add more headings (${metrics.headingCount} found, suggest 5+ for ${wordCount} words)`,
      impact,
    });
  } else {
    context.strengths.push(`Well-structured with ${metrics.headingCount} section headings`);
  }

  if ((metrics.h2Count ?? 0) === 0 && wordCount > 500) {
    const impact = 0.4;
    context.score -= impact;
    context.improvements.push({ issue: "No H2 headings (content needs section breaks)", impact });
  }
}

function scoreContentCompleteness(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
  shouldCheckEarlyAnswer: boolean,
  wordCount: number,
): void {
  if (shouldCheckEarlyAnswer && !(metrics.hasEarlyAnswer === 1) && wordCount > 300) {
    const impact = 0.4;
    context.score -= impact;
    context.improvements.push({ issue: "No clear actionable info in first ~200 words (tighten the lead)", impact });
  } else if (shouldCheckEarlyAnswer && metrics.hasEarlyAnswer === 1) {
    context.strengths.push("Lead contains actionable info early");
  }
}

function scoreSpellingGrammar(
  context: ScoringContext,
  spellingErrors: ReturnType<typeof checkSpellingGrammar>,
  locale: AppLanguage,
): void {
  if (locale !== "en") return;

  if (spellingErrors.errorRate > 1.0) {
    const impact = 0.5;
    context.score -= impact;
    context.improvements.push({
      issue: `High typo rate (${spellingErrors.totalErrors} issue(s), ${spellingErrors.errorRate.toFixed(2)} per 400 words)`,
      impact,
    });
  } else if (spellingErrors.totalErrors === 0) {
    context.strengths.push("No common misspellings detected");
  }
}

function scoreImageQuality(
  context: ScoringContext,
  imageAltCoverage: ReturnType<typeof checkImageAltText>,
  imageSizes: ReturnType<typeof checkImageSizes>,
  metrics: Record<string, number | undefined>,
): void {
  if (imageAltCoverage.coverage < 50) {
    const impact = 1.0;
    context.score -= impact;
    context.issues.push({
      issue: `Critical: Only ${imageAltCoverage.coverage.toFixed(0)}% of images have alt text`,
      impact,
    });
  } else if (imageAltCoverage.coverage < 100) {
    const impact = 0.3;
    context.score -= impact;
    context.improvements.push({
      issue: `${imageAltCoverage.coverage.toFixed(0)}% alt text coverage (target 100%)`,
      impact,
    });
  } else {
    context.strengths.push("All images have alt attributes");
  }

  if (imageSizes.oversize > 0) {
    const impact = 0.7;
    context.score -= impact;
    context.issues.push({
      issue: `${imageSizes.oversize} image${imageSizes.oversize > 1 ? "s" : ""} over 500KB (optimize before publishing)`,
      impact,
    });
  } else if (imageSizes.moderate > 2) {
    const impact = 0.25;
    context.score -= impact;
    context.improvements.push({
      issue: `${imageSizes.moderate} images over 250KB (optimize for better performance)`,
      impact,
    });
  }

  if ((metrics.featuredImageWidth ?? 0) > 0 && (metrics.featuredImageWidth ?? 0) < 1200) {
    const impact = 0.2;
    context.score -= impact;
    context.improvements.push({
      issue: `Featured image small (${metrics.featuredImageWidth}px, recommend ≥1200px for social sharing)`,
      impact,
    });
  }

  if ((metrics.usesModernFormats ?? 1) === 0 && imageAltCoverage.total > 0) {
    const impact = 0.1;
    context.score -= impact;
    context.improvements.push({ issue: "Consider using modern image formats (WebP/AVIF)", impact });
  }
}

function scoreContentStrategy(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
  shouldCheckLocalSpecifics: boolean,
  shouldCheckFirstHand: boolean,
): void {
  if (shouldCheckLocalSpecifics) {
    const localEntityCount = metrics.localEntityCount ?? 0;
    if (localEntityCount < 3) {
      const impact = 0.4;
      context.score -= impact;
      context.improvements.push({
        issue: `Add local specificity (${localEntityCount} entities found, target 5+)`,
        impact,
      });
    } else if (localEntityCount >= 5) {
      context.strengths.push(`Good local specificity (${localEntityCount} entities detected)`);
    }

    const facts = metrics.concreteFactsDensity ?? 0;
    if (facts < 5) {
      const impact = 0.4;
      context.score -= impact;
      context.improvements.push({
        issue: `Add concrete facts (${facts} per 500 words, target 8+)`,
        impact,
      });
    } else if (facts >= 8) {
      context.strengths.push(`Good density of concrete details (${facts} facts per 500 words)`);
    }
  }

  if (shouldCheckFirstHand && (metrics.hasFirstHandDetails ?? 0) === 0) {
    const impact = 0.25;
    context.score -= impact;
    context.improvements.push({
      issue: 'Add first-hand details (staff tips, "from the hostel", "we tried")',
      impact,
    });
  } else if (shouldCheckFirstHand && (metrics.hasFirstHandDetails ?? 0) === 1) {
    context.strengths.push("Includes first-hand / on-the-ground details");
  }
}

function scoreReadability(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
): void {
  if ((metrics.avgParagraphLength ?? 0) > 150) {
    const impact = 0.4;
    context.score -= impact;
    context.improvements.push({
      issue: `Paragraphs long (avg ${metrics.avgParagraphLength} words, target ≤120)`,
      impact,
    });
  } else if ((metrics.avgParagraphLength ?? 0) > 120) {
    const impact = 0.2;
    context.score -= impact;
    context.improvements.push({
      issue: `Break up paragraphs (avg ${metrics.avgParagraphLength} words, optimal ≤90-120)`,
      impact,
    });
  }

  if ((metrics.avgSentenceLength ?? 0) > 30) {
    const impact = 0.4;
    context.score -= impact;
    context.improvements.push({
      issue: `Sentences complex (avg ${metrics.avgSentenceLength} words, target ≤25)`,
      impact,
    });
  } else if ((metrics.avgSentenceLength ?? 0) > 25) {
    const impact = 0.2;
    context.score -= impact;
    context.improvements.push({
      issue: `Simplify sentences (avg ${metrics.avgSentenceLength} words, optimal ≤20-25)`,
      impact,
    });
  }

  if (typeof metrics.readingGradeLevel === "number") {
    if (metrics.readingGradeLevel > 12) {
      const impact = 0.4;
      context.score -= impact;
      context.improvements.push({
        issue: `Reading level high (grade ${metrics.readingGradeLevel}, target ~7-10)`,
        impact,
      });
    } else if (metrics.readingGradeLevel > 10) {
      const impact = 0.2;
      context.score -= impact;
      context.improvements.push({
        issue: `Simplify language (grade ${metrics.readingGradeLevel}, optimal ~7-10)`,
        impact,
      });
    }
  }
}

function scoreKeywordOptimization(
  context: ScoringContext,
  focusKeyword: string | null | undefined,
  keywordInTitle: { present: boolean; nearFront: boolean },
  metrics: Record<string, number | undefined>,
): void {
  if (!focusKeyword) {
    const impact = 0.1;
    context.score -= impact;
    context.improvements.push({
      issue: "Add manifest.focusKeyword (or manifest.primaryQuery) to enable keyword-targeting checks",
      impact,
    });
    return;
  }

  if (!keywordInTitle.present || !keywordInTitle.nearFront) {
    const impact = 0.3;
    context.score -= impact;
    context.improvements.push({ issue: `Focus keyword should appear near front of title ("${focusKeyword}")`, impact });
  }

  if (!(metrics.focusKeywordEarly === 1)) {
    const impact = 0.3;
    context.score -= impact;
    context.improvements.push({ issue: `Include focus keyword in first ~150 words ("${focusKeyword}")`, impact });
  }

  const stuffing = metrics.keywordStuffingRate ?? 0;
  if (stuffing > 3) {
    const impact = 0.5;
    context.score -= impact;
    context.improvements.push({
      issue: `Possible keyword stuffing (${stuffing}% repetition of "${focusKeyword}", target ≤2%)`,
      impact,
    });
  } else if (stuffing > 2) {
    const impact = 0.25;
    context.score -= impact;
    context.improvements.push({
      issue: `Reduce keyword repetition (${stuffing}% repetition, optimal ≤2%)`,
      impact,
    });
  } else {
    context.strengths.push("No obvious keyword stuffing");
  }
}

function scoreLinkQuality(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
): void {
  if ((metrics.genericAnchorCount ?? 0) > 3) {
    const impact = 0.3;
    context.score -= impact;
    context.improvements.push({
      issue: `${metrics.genericAnchorCount} generic link labels ("click here"/"here") - use descriptive anchors`,
      impact,
    });
  } else if ((metrics.genericAnchorCount ?? 0) > 0) {
    const impact = 0.15;
    context.score -= impact;
    context.improvements.push({
      issue: `Replace ${metrics.genericAnchorCount} generic link label(s) with descriptive anchors`,
      impact,
    });
  }
}

function scoreFreshness(
  context: ScoringContext,
  content: GuideContent,
  timeSensitive: boolean,
): void {
  if (timeSensitive) {
    if (!content.lastUpdated) {
      const impact = 0.25;
      context.score -= impact;
      context.improvements.push({ issue: "Add lastUpdated date (time-sensitive guide)", impact });
    }
    if (!checkYearReferences(content)) {
      const impact = 0.15;
      context.score -= impact;
      context.improvements.push({ issue: "Include year/season references (time-sensitive guide)", impact });
    }
  } else {
    if (!content.lastUpdated) {
      const impact = 0.05;
      context.score -= impact;
      context.improvements.push({ issue: "Optional: add lastUpdated date", impact });
    }
  }
}

function scoreStructuredData(
  context: ScoringContext,
  metrics: Record<string, number | undefined>,
): void {
  if (!(metrics.hasArticleSchema === 1 || metrics.hasBreadcrumbs === 1)) {
    const impact = 0.15;
    context.score -= impact;
    context.improvements.push({ issue: "Consider adding BreadcrumbList and/or Article schema in manifest", impact });
  } else {
    context.strengths.push("Structured data declared in manifest");
  }
}

// ============================================================================
// MAIN AUDIT FUNCTION
// ============================================================================

export async function auditGuideSeo(
  guideKey: GuideKey,
  locale: AppLanguage = "en",
): Promise<SeoAuditResult> {
  // 1. Validate guide exists
  const manifest = getGuideManifestEntry(guideKey);
  if (!manifest) {
    throw new Error(`Guide not found: ${guideKey}`);
  }

  // 2. Load guide content
  const cwd = process.cwd();
  const inAppDir = cwd.endsWith("/apps/brikette") || cwd.endsWith("\\apps\\brikette");
  const baseDir = inAppDir ? cwd : path.join(cwd, "apps/brikette");

  const contentPath = path.join(baseDir, "src/locales", locale, "guides/content", `${guideKey}.json`);

  let content: GuideContent;
  try {
    const raw = await fs.readFile(contentPath, "utf-8");
    content = JSON.parse(raw) as GuideContent;
  } catch (err) {
    if ((err as NodeJS.ErrnoException).code === "ENOENT") {
      throw new Error(
        `Content file not found: ${contentPath}\nEnsure the guide has content for locale "${locale}".`,
      );
    }
    throw new Error(`Failed to parse content file: ${(err as Error).message}`);
  }

  // 3. Determine template type
  const wordCount = countWords(content);
  const template = determineTemplate(manifest, wordCount);
  const thresholds = TEMPLATE_THRESHOLDS[template];

  // Template-scoped checks
  const timeSensitive = manifest.timeSensitive ?? (template === "localGuide" || template === "pillar");

  const shouldCheckLocalSpecifics = template === "localGuide" || template === "pillar";
  const shouldCheckFirstHand = template === "experience" || template === "localGuide" || template === "pillar";
  const shouldCheckEarlyAnswer = template !== "experience"; // experiences can be more narrative

  // 4. Extract comprehensive metrics
  const {
    metrics,
    linkValidation,
    imageAltCoverage,
    imageSizes,
    placeholderIssues,
    spellingErrors,
    keywordInTitle,
    focusKeyword,
  } = extractAuditMetrics(
    content,
    manifest,
    guideKey,
    wordCount,
    template,
    locale,
    shouldCheckLocalSpecifics,
    shouldCheckFirstHand,
    shouldCheckEarlyAnswer,
    timeSensitive,
  );

  // 5. Scoring rubric - using extracted helper functions
  const context: ScoringContext = {
    score: 10.0,
    issues: [],
    improvements: [],
    strengths: [],
  };

  scoreLinkIntegrity(context, linkValidation);
  scoreMetaTags(context, content, metrics);
  scorePlaceholders(context, placeholderIssues);
  scoreWordCount(context, wordCount, template, thresholds);
  scoreImages(context, metrics, template, thresholds);
  scoreInternalLinks(context, metrics, template, thresholds);
  scoreFAQs(context, metrics, template, thresholds);
  scoreHeadingStructure(context, metrics, wordCount);
  scoreContentCompleteness(context, metrics, shouldCheckEarlyAnswer, wordCount);
  scoreSpellingGrammar(context, spellingErrors, locale);
  scoreImageQuality(context, imageAltCoverage, imageSizes, metrics);
  scoreContentStrategy(context, metrics, shouldCheckLocalSpecifics, shouldCheckFirstHand);
  scoreReadability(context, metrics);
  scoreKeywordOptimization(context, focusKeyword, keywordInTitle, metrics);
  scoreLinkQuality(context, metrics);
  scoreFreshness(context, content, timeSensitive);
  scoreStructuredData(context, metrics);

  // Sort by impact
  context.issues.sort((a, b) => b.impact - a.impact);
  context.improvements.sort((a, b) => b.impact - a.impact);

  // Round score
  const score = Math.max(0, Math.min(10, Math.round(context.score * 10) / 10));

  return {
    timestamp: new Date().toISOString(),
    score,
    analysis: {
      strengths: context.strengths,
      criticalIssues: context.issues,
      improvements: context.improvements,
    },
    metrics,
    version: "3.1.0",
  };
}

/**
 * Check H2 alignment to queries.
 */
function checkH2AlignmentToQueries(content: GuideContent): { aligned: number; total: number } {
  if (!Array.isArray(content.sections)) return { aligned: 0, total: 0 };

  const h2s = content.sections.filter((s) => s.title).map((s) => s.title!);
  const total = h2s.length;

  const aligned = h2s.filter(
    (h) => h.match(/\b(how|what|where|when|why|which|best|top|guide|tips)\b/i) || h.split(/\s+/).length >= 3,
  ).length;

  return { aligned, total };
}

/**
 * Save audit results to guide-manifest-overrides.json.
 */
export async function saveAuditResults(guideKey: GuideKey, results: SeoAuditResult): Promise<void> {
  const cwd = process.cwd();
  const inAppDir = cwd.endsWith("/apps/brikette") || cwd.endsWith("\\apps\\brikette");
  const baseDir = inAppDir ? cwd : path.join(cwd, "apps/brikette");

  const overridesPath = path.join(baseDir, "src/data/guides/guide-manifest-overrides.json");

  let overrides: Record<string, unknown>;
  try {
    const raw = await fs.readFile(overridesPath, "utf-8");
    overrides = JSON.parse(raw) as Record<string, unknown>;
  } catch (err) {
    if ((err as NodeJS.ErrnoException).code === "ENOENT") {
      overrides = {};
    } else {
      throw new Error(`Failed to read overrides file: ${(err as Error).message}`);
    }
  }

  if (!overrides[guideKey] || typeof overrides[guideKey] !== "object") {
    overrides[guideKey] = {};
  }

  (overrides[guideKey] as Record<string, unknown>).auditResults = results;

  await fs.writeFile(overridesPath, JSON.stringify(overrides, null, 2), "utf-8");
}

/**
 * Format audit results as a user-facing summary report.
 */
export function formatAuditSummary(guideKey: GuideKey, results: SeoAuditResult): string {
  const { score, analysis } = results;
  const blockers = analysis.criticalIssues?.length ?? 0;

  const canPublish = blockers === 0 && score >= PUBLISH_SCORE_THRESHOLD;
  const canFeature = blockers === 0 && score >= FEATURE_SCORE_THRESHOLD;

  const emoji = canFeature ? "🟢" : canPublish ? "🟡" : "🔴";

  let summary = `## SEO Audit Results: ${guideKey}\n\n`;
  summary += `**Score: ${score.toFixed(1)}/10** ${emoji}\n\n`;

  if (analysis.strengths.length > 0) {
    summary += `### Strengths ✅\n`;
    for (const strength of analysis.strengths) summary += `- ${strength}\n`;
    summary += `\n`;
  }

  if (analysis.criticalIssues.length > 0) {
    summary += `### Publish Blockers ❌ (sorted by impact)\n`;
    for (const { issue, impact } of analysis.criticalIssues) {
      summary += `- **[-${impact.toFixed(1)}]** ${issue}\n`;
    }
    summary += `\n`;
  }

  if (analysis.improvements.length > 0) {
    summary += `### Improvements 💡 (sorted by impact)\n`;
    for (const { issue, impact } of analysis.improvements) {
      summary += `- **[-${impact.toFixed(1)}]** ${issue}\n`;
    }
    summary += `\n`;
  }

  summary += `### Next Steps\n`;
  if (analysis.criticalIssues.length > 0) {
    summary += `❌ Cannot publish: resolve all blockers first.\n\n`;
  } else if (canFeature) {
    summary += `✅ Can publish (high quality). Eligible for featured placement.\n\n`;
  } else if (canPublish) {
    summary += `✅ Can publish. Not yet “featured” quality (aim for ${FEATURE_SCORE_THRESHOLD.toFixed(1)}+).\n\n`;
  } else {
    summary += `⚠️ Improve score to ${PUBLISH_SCORE_THRESHOLD.toFixed(1)}+ to publish.\n\n`;
  }

  summary += `Results saved to guide-manifest-overrides.json\n`;
  return summary;
}

// src/locales/guides.backend.ts
// ---------------------------------------------------------------------------
// Guides namespace backend resolver.
//
// Import this module dynamically from i18n backends / loadI18nNs so the heavy
// guides module discovery (webpack contexts for guides/**) is not reachable
// from the always-on layout bundle.
// ---------------------------------------------------------------------------

import { getGuidesBundle, type GuidesNamespace } from "./guides";
import { loadGuidesNamespaceFromImports } from "./guides.imports";

type GuidesBackendOptions = {
  canUseNodeFs: boolean;
};

export async function loadGuidesNamespace(
  lng: string,
  options: GuidesBackendOptions,
): Promise<GuidesNamespace | undefined> {
  const { canUseNodeFs } = options;

  // Prefer the Node FS loader when running under Node (tests/scripts) to avoid
  // races where module discovery hasn't hydrated yet.
  if (canUseNodeFs) {
    try {
      const { loadGuidesNamespaceFromFs } = await import("@/locales/_guides/node-loader");
      const fromFs = loadGuidesNamespaceFromFs(lng);
      if (fromFs) return fromFs;
    } catch {
      // Fall through to other strategies below.
    }
  }

  const bundle = getGuidesBundle(lng);
  if (bundle) return bundle;

  try {
    const imported = await loadGuidesNamespaceFromImports(lng);
    if (imported) return imported;
  } catch {
    // ignore and return undefined
  }

  return undefined;
}

// file path: src/locales/guides.state.ts
// -----------------------------------------------------------------------------
// State building, caching and lifecycle management for guides bundles.
// -----------------------------------------------------------------------------

/* i18n-exempt file -- ABC-123 [ttl=2026-12-31] module loader patterns are not user-facing */
import { getWebpackContext, supportsWebpackGlob, webpackContextToRecord } from "../utils/webpackGlob";

import { loadGuidesModuleOverridesFromFs, loadGuidesModuleOverridesFromFsSync } from "./guides.fs";
import type {
  GlobalGuidesState,
  GuidesNamespace,
  GuidesState,
  JsonModule,
  ModuleOverrides,
  ModuleRecord,
  PartialGuidesNamespace,
} from "./guides.types";
import { assignNestedValue, finaliseSplitBundle, normalisePathSegments, readModule } from "./guides.util";

// Avoid the unsafe `Function` type; we only need to check presence
// of a callable webpack context at runtime.
export const supportsImportMetaGlob = supportsWebpackGlob;

// Typed accessor for globalThis storage keys (avoids repeated as unknown as casts)
const globalRecord = globalThis as unknown as Record<string, unknown>;

function buildGuidesState(overrides?: ModuleOverrides): GuidesState {
  // Only use import.meta.glob when it's available in this runtime
  // and no explicit module overrides were provided.
  const shouldUseRealModules = overrides === undefined && supportsImportMetaGlob;
  const hasLegacyOverride = overrides ? "legacy" in overrides : false;
  const hasSplitGlobalOverride = overrides ? "splitGlobal" in overrides : false;
  const hasSplitContentOverride = overrides ? "splitContent" in overrides : false;

  const legacyModules: ModuleRecord<GuidesNamespace> = hasLegacyOverride
    ? overrides?.legacy ?? ({} as ModuleRecord<GuidesNamespace>)
    : (shouldUseRealModules
      ? (webpackContextToRecord<GuidesNamespace>(
          getWebpackContext("./", true, /guides\\.json$/)
        ) as ModuleRecord<GuidesNamespace>)
        : ({} as ModuleRecord<GuidesNamespace>));

  const splitGlobalModules: ModuleRecord = hasSplitGlobalOverride
    ? overrides?.splitGlobal ?? ({} as ModuleRecord)
    : shouldUseRealModules
      ? webpackContextToRecord(
          getWebpackContext("./", true, /guides\/.*\\.json$/)
        )
      : ({} as ModuleRecord);

  const splitContentModules: ModuleRecord = hasSplitContentOverride
    ? overrides?.splitContent ?? ({} as ModuleRecord)
    : shouldUseRealModules
      ? webpackContextToRecord(
          getWebpackContext("./", true, /guides\/content\/[^/]+\\.json$/)
        )
      : ({} as ModuleRecord);

  const legacyBundles = new Map<string, GuidesNamespace>();
  const splitBundles = new Map<string, PartialGuidesNamespace>();

  function getSplitBundle(locale: string): PartialGuidesNamespace {
    const existing = splitBundles.get(locale);
    if (existing) return existing;
    const created: PartialGuidesNamespace = {
      content: {},
    };
    splitBundles.set(locale, created);
    return created;
  }

  for (const [path, mod] of Object.entries(legacyModules)) {
    const segments = normalisePathSegments(path);
    if (segments.length < 2) continue;
    const locale = segments[0];
    if (!locale) continue;
    const data = readModule<GuidesNamespace>(mod as JsonModule<GuidesNamespace>);
    legacyBundles.set(locale, data);
  }

  for (const [path, mod] of Object.entries(splitGlobalModules)) {
    const segments = normalisePathSegments(path);
    if (segments.length < 3) continue;
    const locale = segments[0];
    if (!locale) continue;
    if (segments[1] !== "guides") continue;
    const relativeSegments = segments.slice(2);
    if (relativeSegments.length === 0) continue;
    if (relativeSegments[0] === "content") continue;
    if (relativeSegments[0] === "content.json") continue;
    const bundle = getSplitBundle(locale);
    assignNestedValue(bundle, relativeSegments, readModule(mod));
  }

  for (const [path, mod] of Object.entries(splitContentModules)) {
    const segments = normalisePathSegments(path);
    if (segments.length < 4) continue;
    const locale = segments[0];
    const fileName = segments[3];
    if (!locale || !fileName) continue;
    const slug = fileName.replace(/\.json$/u, "");
    if (!slug) continue;
    const bundle = getSplitBundle(locale);
    bundle.content[slug] = readModule(mod);
  }

  const splitLocales = new Set(splitBundles.keys());
  const guidesBundles = new Map<string, GuidesNamespace>();

  for (const [locale, data] of legacyBundles.entries()) {
    guidesBundles.set(locale, data);
  }

  for (const [locale, bundle] of splitBundles.entries()) {
    guidesBundles.set(locale, finaliseSplitBundle(bundle));
  }

  return { guidesBundles, splitLocales } satisfies GuidesState;
}

// Persist expensive FS-derived data across Vitest module resets by caching on globalThis.
// This avoids re-walking the entire locales tree for every test case that calls
// vi.resetModules(), which otherwise causes significant overhead and timeouts.
const GLOBAL_STATE_KEY = "__app_guides_state_v1__";
const GLOBAL_OVERRIDES_KEY = "__app_guides_overrides_v1__";

let overridesActive = false;
let initialModuleOverrides: ModuleOverrides | undefined;
let guidesBundles: Map<string, GuidesNamespace>;
let splitLocales: Set<string>;

function setGlobalState(state: GlobalGuidesState): void {
  globalRecord[GLOBAL_STATE_KEY] = state;
}

function getGlobalState(): GlobalGuidesState | undefined {
  const value = globalRecord[GLOBAL_STATE_KEY];
  if (!value || typeof value !== "object") return undefined;
  const state = value as Partial<GlobalGuidesState>;
  if (!(state.guidesBundles instanceof Map) || !(state.splitLocales instanceof Set)) {
    return undefined;
  }
  return state as GlobalGuidesState;
}

// Compute initial overrides only once per process when import.meta.glob is unavailable.
// Avoid top-level await to keep the browser build target at "es2020".
if (!supportsImportMetaGlob) {
  const cached = globalRecord[GLOBAL_OVERRIDES_KEY] as ModuleOverrides | undefined;
  if (cached) {
    initialModuleOverrides = cached;
  } else {
    // Prefer a synchronous scan in Node/Vitest to avoid races where tests
    // access getGuidesBundle() before the async warmup completes.
    const syncOverrides = loadGuidesModuleOverridesFromFsSync();
    if (syncOverrides) {
      initialModuleOverrides = syncOverrides;
      globalRecord[GLOBAL_OVERRIDES_KEY] = syncOverrides;
    } else {
      // Warm the global cache asynchronously for non-Vite Node contexts (tests/scripts).
      // This does not block module initialisation in the browser bundle.
      void loadGuidesModuleOverridesFromFs().then((overrides) => {
        globalRecord[GLOBAL_OVERRIDES_KEY] = overrides;
        if (initialModuleOverrides === undefined) {
          initialModuleOverrides = overrides;
        }
      });
    }
  }
}

export function resetGuidesState(overrides?: ModuleOverrides): void {
  const base = overrides ?? initialModuleOverrides;
  const existing = !overrides ? getGlobalState() : undefined;
  if (existing) {
    guidesBundles = existing.guidesBundles;
    splitLocales = existing.splitLocales;
    overridesActive = Boolean(overrides);
    return;
  }
  const state = buildGuidesState(base);
  guidesBundles = state.guidesBundles;
  splitLocales = state.splitLocales;
  overridesActive = Boolean(overrides);
  setGlobalState({ guidesBundles, splitLocales });
}

// Initialise the state on module load.
resetGuidesState(initialModuleOverrides);

// Accessors used by the public API
export function getGuidesBundlesMap(): Map<string, GuidesNamespace> {
  return guidesBundles;
}

export function getSplitLocalesSet(): Set<string> {
  return splitLocales;
}

export function getOverridesActiveFlag(): boolean {
  return overridesActive;
}

// src/locales/locale-loader.guides.ts
// ---------------------------------------------------------------------------
// Guides-only locale loader.
//
// Uses filesystem access (fs.readFileSync) instead of webpack context to
// prevent bundling 20+ MiB of guide JSON into the server handler. Guide
// pages are pre-rendered at build time (SSG), so the JSON is loaded during
// build and embedded in the HTML output. The runtime Worker only serves the
// pre-rendered HTML.
// ---------------------------------------------------------------------------

import fs from "node:fs";
import path from "node:path";

const LOCALES_DIR = path.resolve(process.cwd(), "src/locales");

export const loadGuidesLocaleResource = async (
  lang: string,
  ns: string,
): Promise<unknown | undefined> => {
  try {
    const filePath = path.join(LOCALES_DIR, lang, `${ns}.json`);
    const content = fs.readFileSync(filePath, "utf-8");
    return JSON.parse(content);
  } catch {
    return undefined;
  }
};

// src/rooms/pricing.ts
// -----------------------------------------------------------------------------
// Live‑rate pricing helpers for rooms.
// -----------------------------------------------------------------------------

import dayjs from "dayjs";

import type { Room } from "@/data/roomsData";
import type { RateCalendar } from "@/types/rates";

/**
 * Priority lookup for nightly rate on a given date.
 * Returns `undefined` when calendar is missing or no applicable rate exists.
 */
export const getPriceForDate = (
  room: Room,
  date: Date,
  calendar: RateCalendar | null | undefined
): number | undefined => {
  if (!calendar) return undefined;

  const iso = dayjs(date).format("YYYY-MM-DD");

  const lookup = (code?: string): number | undefined => {
    if (!code?.trim()) return undefined;
    const entry = calendar[code]?.find(({ date }) => date === iso);
    return entry?.nr;
  };

  // 1. direct.nr
  const directPrice = lookup(room.rateCodes.direct?.nr);
  if (directPrice !== undefined) return directPrice;

  // 2. unified widget code (NR === Flex)
  if (
    room.widgetRateCodeNR &&
    room.widgetRateCodeFlex &&
    room.widgetRateCodeNR === room.widgetRateCodeFlex
  ) {
    const unifiedPrice = lookup(room.widgetRateCodeNR);
    if (unifiedPrice !== undefined) return unifiedPrice;
  }

  // 3. flex‑only widget code (NR empty, Flex present)
  if (!room.widgetRateCodeNR && room.widgetRateCodeFlex) {
    const flexOnlyPrice = lookup(room.widgetRateCodeFlex);
    if (flexOnlyPrice !== undefined) return flexOnlyPrice;
  }

  // 4. ota.nr (only if some widget code exists)
  const hasWidgetCodes =
    Boolean(room.widgetRateCodeNR?.trim()) || Boolean(room.widgetRateCodeFlex?.trim());

  if (hasWidgetCodes) {
    const otaPrice = lookup(room.rateCodes.ota?.nr);
    if (otaPrice !== undefined) return otaPrice;
  }

  // 5. room‑level fallback (only when no widget codes exist)
  if (!hasWidgetCodes) {
    const roomPrice = lookup(room.id);
    if (roomPrice !== undefined) return roomPrice;
  }

  // Safety net: try whichever codes remain
  const roomFallback = lookup(room.id);
  if (roomFallback !== undefined) return roomFallback;

  return lookup(room.rateCodes.ota?.nr);
};

import type { TFunction, TOptions } from "i18next";

import {
  FALLBACK_DEALS,
  PLACEHOLDER_TOKEN_REGEX,
  TEMPLATE_TOKEN_REGEX,
} from "./constants";

export type FallbackTranslator = (key: string, options?: TOptions) => string;

export function getFallbackValue(path: string): unknown {
  return path.split(".").reduce<unknown>((acc, segment) => {
    if (acc == null) {
      return undefined;
    }

    if (Array.isArray(acc)) {
      const index = Number(segment);
      return Number.isInteger(index) ? acc[index] : undefined;
    }

    if (typeof acc === "object") {
      return (acc as Record<string, unknown>)[segment];
    }

    return undefined;
  }, FALLBACK_DEALS as unknown);
}

export const isResolvedString = (value: unknown, key?: string): value is string =>
  typeof value === "string" &&
  value.trim().length > 0 &&
  (key == null || value !== key) &&
  !PLACEHOLDER_TOKEN_REGEX.test(value);

const interpolateTemplate = (
  template: string,
  values?: Record<string, unknown>
): { result: string; missingToken: boolean } => {
  let missingToken = false;
  const result = template.replace(TEMPLATE_TOKEN_REGEX, (_, token: string) => {
    const value = values?.[token];
    if (value == null) {
      missingToken = true;
      return "";
    }

    return String(value);
  });

  return { result, missingToken };
};

export function resolveMetaString(key: string, value: string): string {
  if (isResolvedString(value, key)) {
    return value;
  }

  const fallback = getFallbackValue(key);
  if (isResolvedString(fallback, key)) {
    return fallback;
  }

  if (typeof fallback === "string" && fallback.trim().length > 0) {
    return fallback;
  }

  return key;
}

export function createFallbackTranslator(t: TFunction, fallbackT: TFunction): FallbackTranslator {
  return (key, options) => {
    let encounteredMissingTokens = false;

    const translate = (translator: TFunction, k: string, opts?: TOptions) =>
      (opts ? translator(k, opts) : translator(k)) as unknown;

    const asRecord = (opts?: TOptions) => opts as unknown as Record<string, unknown> | undefined;

    const tryResolve = (raw: unknown, opts?: TOptions): string | undefined => {
      if (isResolvedString(raw, key)) {
        return raw;
      }

      if (typeof raw === "string" && raw !== key) {
        if (!PLACEHOLDER_TOKEN_REGEX.test(raw)) {
          const trimmed = raw.trim();
          if (trimmed.length > 0) {
            return trimmed;
          }
        }

        const { result: interpolatedResult, missingToken } = interpolateTemplate(raw, asRecord(opts));
        if (missingToken) {
          encounteredMissingTokens = true;
          return undefined;
        }

        // Avoid type-guard narrowing on a value already typed as `string` (would yield `never` in else-branch).
        const trimmedInterpolated = interpolatedResult.trim();
        const isMeaningful =
          trimmedInterpolated.length > 0 &&
          (key == null || interpolatedResult !== key) &&
          !PLACEHOLDER_TOKEN_REGEX.test(interpolatedResult);
        if (isMeaningful) {
          // Preserve original spacing if it otherwise qualifies as meaningful.
          return interpolatedResult;
        }

        if (trimmedInterpolated.length > 0) {
          return trimmedInterpolated;
        }
      }

      return undefined;
    };

    const initialOptions = options as TOptions | undefined;
    const primaryRaw = translate(t, key, initialOptions);
    const primaryResolved = tryResolve(primaryRaw, initialOptions);
    if (primaryResolved) {
      return primaryResolved;
    }

    const englishRaw = translate(fallbackT, key, options);
    const englishTemplate =
      typeof englishRaw === "string" && englishRaw.trim().length > 0 && englishRaw !== key
        ? englishRaw
        : undefined;
    const englishString =
      englishTemplate && !PLACEHOLDER_TOKEN_REGEX.test(englishTemplate) ? englishTemplate.trim() : undefined;

    const fallbackRaw = getFallbackValue(key);
    const fallbackTemplate =
      typeof fallbackRaw === "string" && fallbackRaw.trim().length > 0 && fallbackRaw !== key
        ? fallbackRaw
        : undefined;

    const defaultText = englishTemplate ?? fallbackTemplate;
    const shouldInjectDefault =
      typeof defaultText === "string" && (options == null || typeof options.defaultValue !== "string");
    const optionsWithDefault = shouldInjectDefault
      ? ({ ...options, defaultValue: defaultText } as TOptions)
      : (options as TOptions | undefined);

    if (shouldInjectDefault) {
      const rawWithDefault = translate(t, key, optionsWithDefault);
      const resolvedWithDefault = tryResolve(rawWithDefault, optionsWithDefault);
      if (resolvedWithDefault) {
        return resolvedWithDefault;
      }
    }

    if (englishString) {
      return englishString;
    }

    const interpolationOptions = optionsWithDefault;
    const tryTemplate = (template?: string): string | undefined => {
      if (typeof template !== "string") {
        return undefined;
      }

      const { result: interpolatedResult, missingToken } = interpolateTemplate(
        template,
        asRecord(interpolationOptions),
      );
      if (missingToken) {
        encounteredMissingTokens = true;
        return undefined;
      }

      const trimmed = interpolatedResult.trim();
      if (trimmed.length > 0 && !PLACEHOLDER_TOKEN_REGEX.test(trimmed)) {
        return trimmed;
      }

      if (trimmed.length > 0) {
        return trimmed;
      }

      return undefined;
    };

    const templateCandidates = [
      typeof optionsWithDefault?.defaultValue === "string" ? optionsWithDefault.defaultValue : undefined,
      englishTemplate,
      fallbackTemplate,
    ];

    for (const candidate of templateCandidates) {
      const resolved = tryTemplate(candidate);
      if (resolved) {
        return resolved;
      }
    }

    if (encounteredMissingTokens) {
      return "";
    }

    if (typeof fallbackRaw === "string" && fallbackRaw.trim().length > 0) {
      return fallbackRaw.trim();
    }

    if (fallbackRaw != null) {
      return String(fallbackRaw);
    }

    return key;
  };
}

export type TokenResolver = (key: string) => string;

export function createTokenResolver(tokensT: TFunction, fallbackTokensT: TFunction): TokenResolver {
  return (key) => {
    const value = tokensT(key) as unknown;
    if (isResolvedString(value, key)) {
      return value;
    }

    const fallback = fallbackTokensT(key) as unknown;
    if (isResolvedString(fallback, key)) {
      return fallback;
    }

    return key;
  };
}

import type { TFunction } from "i18next";

import { formatMonthDay, formatPercent, isoDateToLocalStart, shouldIncludeYear } from "./dates";
import type { DealConfig } from "./deals";
import { getDealStatus } from "./status";

type DealsMetadataState = "active" | "upcoming" | "expired";

export type DealsMetadataCopy = {
  title: string;
  description: string;
  state: DealsMetadataState;
};

type ReadCopyOptions = {
  defaultValue: string;
} & Record<string, unknown>;

const readCopy = (t: TFunction, key: string, options: ReadCopyOptions): string => {
  const resolved = t(key, options) as string;
  if (!resolved || resolved.trim().length === 0 || resolved === key) {
    return options.defaultValue;
  }
  return resolved;
};

const firstDealByStatus = (deals: readonly DealConfig[], now: Date, target: DealsMetadataState) =>
  deals.find((deal) => getDealStatus(deal, now) === target);

export const resolveDealsMetadataCopy = (
  t: TFunction,
  lang: string,
  deals: readonly DealConfig[],
  now: Date = new Date(),
): DealsMetadataCopy => {
  const activeDeal = firstDealByStatus(deals, now, "active");
  const upcomingDeal = firstDealByStatus(deals, now, "upcoming");
  const candidate = activeDeal ?? upcomingDeal ?? null;

  if (!candidate) {
    return {
      title: readCopy(t, "meta.defaultTitle", {
        defaultValue: "Deals & Offers | Hostel Brikette",
      }),
      description: readCopy(t, "meta.defaultDescription", {
        defaultValue: "Direct booking perks and seasonal offers at Hostel Brikette.",
      }),
      state: "expired",
    };
  }

  const percentLabel = formatPercent(lang, candidate.discountPct);
  const start = isoDateToLocalStart(candidate.startDate);
  const end = isoDateToLocalStart(candidate.endDate);
  const includeYear = shouldIncludeYear(now, start, end);
  const from = formatMonthDay(lang, start, { includeYear });
  const to = formatMonthDay(lang, end, { includeYear });

  const state: DealsMetadataState = activeDeal ? "active" : "upcoming";

  const title = readCopy(t, "meta.activeTitle", {
    percent: percentLabel,
    from,
    to,
    defaultValue: readCopy(t, "meta.title", {
      percent: percentLabel,
      from,
      to,
      defaultValue: "",
    }),
  });
  const description = readCopy(t, "meta.activeDescription", {
    percent: percentLabel,
    from,
    to,
    defaultValue: readCopy(t, "meta.description", {
      percent: percentLabel,
      from,
      to,
      defaultValue: "",
    }),
  });

  return { title, description, state };
};

