export function renderArticleLead(
  context: GuideSeoTemplateContext,
  extras: GuideExtras,
): JSX.Element {
  const {
    intro,
    sections,
    tocItems,
    tocTitle,
    beforeList,
    stepsList,
    kneesList,
    kneesDockPrefix,
    kneesDockLinkText,
    kneesPorterPrefix,
    kneesPorterLinkText,
    faqs,
    faqsTitle,
    labels,
  } = extras;

  const readLabel = createGuideLabelReader(context, labels);
  const tHowTo = i18n.getFixedT(context.lang, "howToGetHere");

  const renderBodyLine = (value: string, keyPrefix: string): ReactNode => {
    const match = value.match(FIGURE_PATTERN);
    if (!match) {
      return (
        <p key={keyPrefix} className="leading-relaxed">
          {renderInlineLinks(value, keyPrefix, context)}
        </p>
      );
    }

    const id = match[1]?.toLowerCase() as keyof typeof FIGURES | undefined;
    const figure = id ? FIGURES[id] : undefined;
    if (!figure) {
      return (
        <p key={keyPrefix} className="leading-relaxed">
          {renderInlineLinks(value, keyPrefix, context)}
        </p>
      );
    }

    return (
      <ZoomableFigure
        key={keyPrefix}
        t={tHowTo}
        src={figure.src}
        alt={figure.alt}
        caption={figure.caption}
        aspect={figure.aspect}
        className="max-w-3xl"
      />
    );
  };

  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);

  return (
    <div className="space-y-12">
      {intro.length > 0 ? (
        <div className="space-y-4">
          {intro.map((paragraph, index) => (
            <p key={index} className="leading-relaxed">
              {renderInlineLinks(paragraph, `intro-${index}`, context)}
            </p>
          ))}
        </div>
      ) : null}

      {tocItems.length > 0 ? (
        <TableOfContents className="mt-6" title={tocTitle} items={tocItems} />
      ) : null}

      {sections.map((section) => (
        <section key={section.id} id={section.id} className="space-y-5">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">{section.title}</h2>
          <div className="space-y-4">
            {section.body.map((line, index) =>
              renderBodyLine(line, `${section.id}-body-${index}`),
            )}
          </div>
        </section>
      ))}

      {beforeList.length > 0 ? (
        <section id="before" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">
            {readLabel("toc.before") ?? labels.before}
          </h2>
          <ul className="list-disc space-y-2 pl-5">
            {beforeList.map((item, index) => (
              <li key={index} className="leading-relaxed">
                {renderInlineLinks(item, `before-${index}`, context)}
              </li>
            ))}
          </ul>
        </section>
      ) : null}

      {stepsList.length > 0 ? (
        <section id="steps" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">
            {readLabel("toc.steps") ?? labels.steps}
          </h2>
          <ol className="list-decimal space-y-2 pl-5">
            {stepsList.map((item, index) => (
              <li key={index} className="leading-relaxed">
                {renderInlineLinks(item, `steps-${index}`, context)}
              </li>
            ))}
          </ol>
        </section>
      ) : null}

      {shouldRenderKnees ? (
        <section id="knees" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">
            {readLabel("toc.knees") ?? labels.knees}
          </h2>
          <ul className="list-disc space-y-2 pl-5">
            {kneesList.map((item, index) => (
              <li key={index} className="leading-relaxed">
                {renderInlineLinks(item, `knees-${index}`, context)}
              </li>
            ))}
            {kneesDockPrefix && kneesDockLinkText ? (
              <li className="leading-relaxed">
                {kneesDockPrefix}{" "}
                <Link
                  href={guideHref(context.lang, "chiesaNuovaArrivals")}
                  className={INLINE_LINK_CLASSES}
                >
                  {kneesDockLinkText}
                </Link>
                .
              </li>
            ) : null}
            {kneesPorterPrefix && kneesPorterLinkText ? (
              <li className="leading-relaxed">
                {kneesPorterPrefix}{" "}
                <Link
                  href={guideHref(context.lang, "porterServices")}
                  className={INLINE_LINK_CLASSES}
                >
                  {kneesPorterLinkText}
                </Link>
                .
              </li>
            ) : null}
          </ul>
        </section>
      ) : null}

      {faqs.length > 0 ? (
        <section id="faqs" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">{faqsTitle}</h2>
          <div className="space-y-3">
            {faqs.map((faq, index) => (
              <details
                key={index}
                className="rounded-lg border border-brand-outline/20 bg-brand-surface/80 p-4 shadow-sm transition hover:border-brand-primary/40 dark:border-brand-outline/40 dark:bg-brand-surface/30"
              >
                <summary className="cursor-pointer text-base font-semibold text-brand-heading outline-none transition focus-visible:ring-2 focus-visible:ring-brand-primary/60 focus-visible:ring-offset-2 dark:text-brand-text">
                  {faq.q}
                </summary>
                <div className="mt-3 space-y-3">
                  {faq.a.map((answer, answerIndex) => (
                    <p key={answerIndex} className="leading-relaxed">
                      {renderInlineLinks(answer, `faq-${index}-${answerIndex}`, context)}
                    </p>
                  ))}
                </div>
              </details>
            ))}
          </div>
        </section>
      ) : null}
    </div>
  );
}

import type { GuideSeoTemplateContext } from "@/routes/guides/_GuideSeoTemplate";
import { ensureStringArray } from "@/utils/i18nContent";

import { GUIDE_KEY } from "./constants";
import { normaliseFaqs, normaliseSections, normaliseToc } from "./content";
import {
  buildGuideFallbackLabels,
  buildTranslationKey,
  getGuidesFallbackTranslator,
} from "./i18n";
import type { GuideExtras } from "./types";

export function createGuideExtras(context: GuideSeoTemplateContext): GuideExtras {
  const translate = context.translateGuides;
  const guidesFallbackLocal = getGuidesFallbackTranslator(context.lang);
  const guidesFallbackEn = getGuidesFallbackTranslator("en");
  const fallbackLabels = buildGuideFallbackLabels(guidesFallbackLocal, guidesFallbackEn);
  const translationKeyFor = (suffix: string) => buildTranslationKey(suffix);
  const isMissingTranslationValue = (value: string, suffix: string) => {
    const trimmed = value.trim();
    if (trimmed.length === 0) return true;
    return trimmed === translationKeyFor(suffix) || trimmed === `${GUIDE_KEY}.${suffix}`;
  };
  const stripMissingTranslationValues = <T,>(values: T[], suffix: string): T[] =>
    values.filter((value) => (typeof value === "string" ? !isMissingTranslationValue(value, suffix) : true));

  const readArray = <T,>(suffix: string, normaliser: (value: unknown) => T[]): T[] => {
    const primary = stripMissingTranslationValues(
      normaliser(translate(buildTranslationKey(suffix), { returnObjects: true })),
      suffix,
    );
    if (primary.length > 0) return primary;
    const fallbackLocal = stripMissingTranslationValues(
      normaliser(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { returnObjects: true })),
      suffix,
    );
    if (fallbackLocal.length > 0) return fallbackLocal;
    return stripMissingTranslationValues(
      normaliser(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { returnObjects: true })),
      suffix,
    );
  };

  const readString = (suffix: string): string | undefined => {
    const translationKey = buildTranslationKey(suffix);
    const value = translate(translationKey);
    if (
      typeof value === "string" &&
      value.trim().length > 0 &&
      value !== translationKey &&
      value !== `${GUIDE_KEY}.${suffix}`
    ) {
      return value.trim();
    }
    const fallbackLocal = guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" });
    if (
      typeof fallbackLocal === "string" &&
      fallbackLocal.trim().length > 0 &&
      fallbackLocal !== `${GUIDE_KEY}.${suffix}`
    ) {
      return fallbackLocal.trim();
    }
    const fallbackEn = guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" });
    if (
      typeof fallbackEn === "string" &&
      fallbackEn.trim().length > 0 &&
      fallbackEn !== `${GUIDE_KEY}.${suffix}`
    ) {
      return fallbackEn.trim();
    }
    return undefined;
  };

  const intro = readArray("intro", ensureStringArray);
  const sections = readArray("sections", normaliseSections);
  const beforeList = readArray("beforeList", ensureStringArray);
  const stepsList = readArray("stepsList", ensureStringArray);
  const kneesList = readArray("kneesList", ensureStringArray);
  const kneesDockPrefix = readString("kneesDockPrefix");
  const kneesDockLinkText = readString("kneesDockLinkLabel");
  const kneesPorterPrefix = readString("kneesPorterPrefix");
  const kneesPorterLinkText = readString("kneesPorterLinkLabel");
  const faqs = readArray("faqs", normaliseFaqs);

  if (faqs.length === 0) {
    const legacyFaq = normaliseFaqs(translate(buildTranslationKey("faq"), { returnObjects: true }));
    if (legacyFaq.length > 0) {
      faqs.push(...legacyFaq);
    }
  }

  const faqsTitleFallbackRaw = guidesFallbackEn(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" });
  const faqsTitleFallback =
    typeof faqsTitleFallbackRaw === "string" && faqsTitleFallbackRaw.trim().length > 0
      ? faqsTitleFallbackRaw.trim()
      : fallbackLabels.faqs;
  const faqsTitle = readString("faqsTitle") ?? faqsTitleFallback;

  const explicitToc = normaliseToc(translate(buildTranslationKey("toc"), { returnObjects: true }));
  const fallbackToc =
    explicitToc.length > 0
      ? explicitToc
      : normaliseToc(guidesFallbackLocal(`${GUIDE_KEY}.toc`, { returnObjects: true }));
  const fallbackEnToc =
    fallbackToc.length > 0
      ? fallbackToc
      : normaliseToc(guidesFallbackEn(`${GUIDE_KEY}.toc`, { returnObjects: true }));

  const tocItems: GuideExtras["tocItems"] = [];
  const pushItem = (item: GuideExtras["tocItems"][number] | null | undefined) => {
    if (!item) return;
    if (tocItems.some((existing) => existing.href === item.href)) return;
    tocItems.push(item);
  };

  sections.forEach((section) => {
    pushItem({ href: `#${section.id}`, label: section.title });
  });
  if (beforeList.length > 0) {
    pushItem({ href: "#before", label: readString("toc.before") ?? fallbackLabels.before });
  }
  if (stepsList.length > 0) {
    pushItem({ href: "#steps", label: readString("toc.steps") ?? fallbackLabels.steps });
  }
  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);
  if (shouldRenderKnees) {
    pushItem({ href: "#knees", label: readString("toc.knees") ?? fallbackLabels.knees });
  }
  if (faqs.length > 0) {
    pushItem({ href: "#faqs", label: readString("toc.faqs") ?? faqsTitle ?? fallbackLabels.faqs });
  }

  const finalToc =
    explicitToc.length > 0
      ? explicitToc
      : fallbackToc.length > 0
        ? fallbackToc
        : fallbackEnToc.length > 0
          ? fallbackEnToc
          : tocItems;

  const howToSteps = readArray("howtoSteps", ensureStringArray);
  if (howToSteps.length === 0 && stepsList.length > 0) {
    howToSteps.push(...stepsList);
  }

  return {
    intro,
    sections,
    tocItems: finalToc,
    tocTitle: readString("tocTitle") ?? fallbackLabels.onThisPage,
    beforeList,
    stepsList,
    kneesList,
    faqs,
    faqsTitle: faqsTitle ?? fallbackLabels.faqs,
    howToSteps,
    labels: fallbackLabels,
    ...(kneesDockPrefix !== undefined ? { kneesDockPrefix } : {}),
    ...(kneesDockLinkText !== undefined ? { kneesDockLinkText } : {}),
    ...(kneesPorterPrefix !== undefined ? { kneesPorterPrefix } : {}),
    ...(kneesPorterLinkText !== undefined ? { kneesPorterLinkText } : {}),
  };
}

import type { HTMLAttributeReferrerPolicy } from "react";
import Link from "next/link";

import { CfImage } from "@acme/ui/atoms/CfImage";

import TableOfContents from "@/components/guides/TableOfContents";
import { guideHref } from "@/routes.guides-helpers";
import type { GuideSeoTemplateContext } from "@/routes/guides/_GuideSeoTemplate";

import { createGuideLabelReader } from "./labels";
import type { GuideExtras } from "./types";

const MAP_REFERRER_POLICY: HTMLAttributeReferrerPolicy = "no-referrer-when-downgrade";

export function renderArticleLead(context: GuideSeoTemplateContext, extras: GuideExtras): JSX.Element {
  const {
    intro,
    image,
    sections,
    beforeList,
    stepsList,
    stepsMapEmbedUrl,
    kneesList,
    kneesDockPrefix,
    kneesDockLinkText,
    kneesPorterPrefix,
    kneesPorterLinkText,
    faqs,
    faqsTitle,
    tocItems,
    tocTitle,
    labels,
  } = extras;

  const readLabel = createGuideLabelReader(context, labels);

  const stepsHeading = readLabel("toc.steps") ?? labels.steps;

  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);

  return (
    <div className="space-y-12">
      {intro.length > 0 || image ? (
        <div className="space-y-4">
          {intro.map((paragraph, index) => (
            <p key={index} className="leading-relaxed">
              {paragraph}
            </p>
          ))}
          {image ? (
            <figure className="overflow-hidden rounded-2xl border border-brand-outline/20 shadow-sm">
              <CfImage
                src={image.src}
                preset="gallery"
                alt={image.alt}
                className="size-full object-cover"
                data-aspect="4/3"
              />
              {image.caption ? (
                <figcaption className="bg-brand-surface px-4 py-3 text-sm text-brand-text/70 dark:bg-brand-surface/70 dark:text-brand-text/80">
                  {image.caption}
                </figcaption>
              ) : null}
            </figure>
          ) : null}
        </div>
      ) : null}

      {/* Render a local Table of Contents when items are provided. */}
      {Array.isArray(tocItems) && tocItems.length > 0 ? (
        <TableOfContents title={tocTitle} items={tocItems} />
      ) : null}

      {sections.map((section) => {
        const isDuplicateOfGeneric =
          (context.renderGenericContent ?? true) &&
          Array.isArray(context.sections) &&
          context.sections.some((s) => s?.id === section.id);
        if (isDuplicateOfGeneric) return null;

        return (
          <section key={section.id} id={section.id} className="space-y-5">
            {(() => {
              const isStubAnchor = typeof section.id === "string" && section.id.endsWith("-stub");
              if (isStubAnchor) {
                // Provide an anchor target without creating a second visible H2.
                return (
                  <div aria-hidden="true" className="sr-only">
                    {section.title}
                  </div>
                );
              }
              return (
                <h2 className="text-pretty text-3xl font-semibold tracking-tight">{section.title}</h2>
              );
            })()}
            <div className="space-y-4">
              {section.body.map((paragraph, index) => (
                <p key={index} className="leading-relaxed">
                  {paragraph}
                </p>
              ))}
            </div>
          </section>
        );
      })}

      {beforeList.length > 0 ? (
        <section id="before" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">
            {readLabel("toc.before") ?? labels.before}
          </h2>
          <ul className="list-disc space-y-2 pl-5">
            {beforeList.map((item, index) => (
              <li key={index} className="leading-relaxed">
                {item}
              </li>
            ))}
          </ul>
        </section>
      ) : null}

      {stepsList.length > 0 ? (
        <section id="steps" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">
            {stepsHeading}
          </h2>
          <ol className="list-decimal space-y-2 pl-5">
            {stepsList.map((item, index) => (
              <li key={index} className="leading-relaxed">
                {item}
              </li>
            ))}
          </ol>
          {stepsMapEmbedUrl ? (
            <div className="not-prose w-full overflow-hidden rounded-xl border border-brand-outline/20 shadow-sm">
              <div className="aspect-video">
                <iframe
                  title={`${stepsHeading} map`}
                  src={stepsMapEmbedUrl}
                  allowFullScreen
                  loading="lazy"
                  referrerPolicy={MAP_REFERRER_POLICY}
                  className="size-full border-0"
                  data-aspect="16/9"
                />
              </div>
            </div>
          ) : null}
        </section>
      ) : null}

      {shouldRenderKnees ? (
        <section id="knees" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">
            {readLabel("toc.knees") ?? labels.knees}
          </h2>
          <ul className="list-disc space-y-2 pl-5">
            {kneesList.map((item, index) => (
              <li key={index} className="leading-relaxed">
                {item}
              </li>
            ))}
            {kneesDockPrefix && kneesDockLinkText ? (
              <li className="leading-relaxed">
                {kneesDockPrefix}{" "}
                <Link
                  href={guideHref(context.lang, "ferryDockToBrikette")}
                  className="font-medium text-brand-primary underline-offset-4 hover:underline dark:text-brand-secondary"
                >
                  {kneesDockLinkText}
                </Link>
                .
              </li>
            ) : null}
            {kneesPorterPrefix && kneesPorterLinkText ? (
              <li className="leading-relaxed">
                {kneesPorterPrefix}{" "}
                <Link
                  href={guideHref(context.lang, "porterServices")}
                  className="font-medium text-brand-primary underline-offset-4 hover:underline dark:text-brand-secondary"
                >
                  {kneesPorterLinkText}
                </Link>
                .
              </li>
            ) : null}
          </ul>
        </section>
      ) : null}

      {faqs.length > 0 ? (
        <section id="faqs" className="space-y-4">
          <h2 className="text-pretty text-3xl font-semibold tracking-tight">{faqsTitle}</h2>
          <div className="space-y-3">
            {faqs.map((faq, index) => (
              <details
                key={index}
                className="rounded-lg border border-brand-outline/20 bg-brand-surface/80 p-4 shadow-sm transition hover:border-brand-primary/40 dark:border-brand-outline/40 dark:bg-brand-surface/30"
              >
                <summary className="cursor-pointer text-base font-semibold text-brand-heading outline-none transition focus-visible:ring-2 focus-visible:ring-brand-primary/60 focus-visible:ring-offset-2 dark:text-brand-text">
                  {faq.q}
                </summary>
                <div className="mt-3 space-y-3">
                  {faq.a.map((answer, answerIndex) => (
                    <p key={answerIndex} className="leading-relaxed">
                      {answer}
                    </p>
                  ))}
                </div>
              </details>
            ))}
          </div>
        </section>
      ) : null}
    </div>
  );
}

import type { GuideSeoTemplateContext } from "@/routes/guides/_GuideSeoTemplate";
import { ensureStringArray } from "@/utils/i18nContent";

import { GUIDE_KEY, STOP_IMAGE_SRC } from "./constants";
import { normaliseFaqs, normaliseSections, normaliseTocItems } from "./content";
import {
  buildGuideFallbackLabels,
  buildTranslationKey,
  getGuidesFallbackTranslator,
} from "./i18n";
import type { GuideExtras, TocEntry } from "./types";

export function createGuideExtras(context: GuideSeoTemplateContext): GuideExtras {
  const translate = context.translateGuides;
  const rawTranslate = context.translator;
  const guidesFallbackLocal = getGuidesFallbackTranslator(context.lang);
  const guidesFallbackEn = getGuidesFallbackTranslator("en");
  const fallbackLabels = buildGuideFallbackLabels(guidesFallbackLocal, guidesFallbackEn);

  const coerceString = (value: unknown) => {
    if (typeof value !== "string") return undefined;
    const trimmed = value.trim();
    return trimmed.length > 0 ? trimmed : undefined;
  };

  const readArray = <T,>(suffix: string, normaliser: (value: unknown) => T[]): T[] => {
    const primary = normaliser(translate(buildTranslationKey(suffix), { returnObjects: true }));
    if (primary.length > 0) return primary;
    const fallbackLocal = normaliser(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
    if (fallbackLocal.length > 0) return fallbackLocal;
    return normaliser(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
  };

  const readString = (suffix: string, options?: { allowBlank?: boolean }): string | undefined => {
    const { allowBlank = false } = options ?? {};
    const translationKey = buildTranslationKey(suffix);
    const consume = (input: unknown) => {
      if (typeof input !== "string") return undefined;
      if (input === translationKey) return undefined;
      const trimmed = input.trim();
      if (trimmed.length > 0) return trimmed;
      return allowBlank ? "" : undefined;
    };

    const rawValue = rawTranslate(translationKey);
    const primaryRaw = consume(rawValue);
    if (primaryRaw !== undefined) return primaryRaw;

    const fallbackLocal = consume(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" }));
    if (fallbackLocal !== undefined) return fallbackLocal;

    const primary = consume(translate(translationKey));
    if (primary !== undefined) return primary;

    const fallbackEn = consume(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" }));
    if (fallbackEn !== undefined) return fallbackEn;

    return undefined;
  };

  const intro = readArray("intro", ensureStringArray);
  const sections = readArray("sections", normaliseSections);
  const beforeList = readArray("beforeList", ensureStringArray);
  const stepsList = readArray("stepsList", ensureStringArray);
  const stepsMapEmbedUrl = readString("stepsMapEmbedUrl");
  const kneesList = readArray("kneesList", ensureStringArray);
  const kneesDockPrefix = readString("kneesDockPrefix", { allowBlank: true });
  const kneesDockLinkText = readString("kneesDockLinkLabel", { allowBlank: true });
  const kneesPorterPrefix = readString("kneesPorterPrefix", { allowBlank: true });
  const kneesPorterLinkText = readString("kneesPorterLinkLabel", { allowBlank: true });
  const imageAlt = readString("image.alt");
  const imageCaption = readString("image.caption");
  const faqs = readArray("faqs", normaliseFaqs);
  if (faqs.length === 0) {
    const legacyFaq = readArray("faq", normaliseFaqs);
    if (legacyFaq.length > 0) {
      faqs.push(...legacyFaq);
    }
  }

  const faqsTitle =
    readString("faqsTitle") ??
    coerceString(guidesFallbackLocal(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" })) ??
    coerceString(guidesFallbackEn(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" }));

  const howToSteps = readArray("howtoSteps", ensureStringArray);

  const tocTitle = readString("toc.onThisPage") ?? readString("tocTitle") ?? fallbackLabels.onThisPage;

  const explicitTocItems = normaliseTocItems(
    translate(buildTranslationKey("tocItems"), { returnObjects: true }),
  );
  const fallbackTocItems =
    explicitTocItems.length > 0
      ? explicitTocItems
      : normaliseTocItems(guidesFallbackLocal(`${GUIDE_KEY}.tocItems`, { returnObjects: true }));
  const fallbackEnTocItems =
    fallbackTocItems.length > 0
      ? fallbackTocItems
      : normaliseTocItems(guidesFallbackEn(`${GUIDE_KEY}.tocItems`, { returnObjects: true }));

  const tocItemsCandidates: TocEntry[] = [];
  const pushItem = (item: TocEntry | null | undefined) => {
    if (!item) return;
    if (tocItemsCandidates.some((candidate) => candidate.href === item.href)) return;
    tocItemsCandidates.push(item);
  };

  sections.forEach((section) => {
    pushItem({ href: `#${section.id}`, label: section.title });
  });
  if (beforeList.length > 0) {
    pushItem({ href: "#before", label: readString("toc.before") ?? fallbackLabels.before });
  }
  if (stepsList.length > 0) {
    pushItem({ href: "#steps", label: readString("toc.steps") ?? fallbackLabels.steps });
  }
  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);
  if (shouldRenderKnees) {
    pushItem({ href: "#knees", label: readString("toc.knees") ?? fallbackLabels.knees });
  }
  if (faqs.length > 0) {
    pushItem({ href: "#faqs", label: readString("toc.faqs") ?? faqsTitle ?? fallbackLabels.faqs });
  }

  const tocItems =
    explicitTocItems.length > 0
      ? explicitTocItems
      : fallbackTocItems.length > 0
        ? fallbackTocItems
        : fallbackEnTocItems.length > 0
          ? fallbackEnTocItems
          : tocItemsCandidates;

  return {
    intro,
    sections,
    beforeList,
    stepsList,
    kneesList,
    faqs,
    faqsTitle: faqsTitle ?? fallbackLabels.faqs,
    tocTitle,
    tocItems,
    howToSteps,
    labels: fallbackLabels,
    ...(imageAlt
      ? {
          image: {
            src: STOP_IMAGE_SRC,
            alt: imageAlt,
            ...(imageCaption !== undefined ? { caption: imageCaption } : {}),
          },
        }
      : {}),
    ...(stepsMapEmbedUrl !== undefined ? { stepsMapEmbedUrl } : {}),
    ...(kneesDockPrefix !== undefined ? { kneesDockPrefix } : {}),
    ...(kneesDockLinkText !== undefined ? { kneesDockLinkText } : {}),
    ...(kneesPorterPrefix !== undefined ? { kneesPorterPrefix } : {}),
    ...(kneesPorterLinkText !== undefined ? { kneesPorterLinkText } : {}),
  } satisfies GuideExtras;
}

import type { GuideSeoTemplateContext } from "@/routes/guides/_GuideSeoTemplate";
import { ensureStringArray } from "@/utils/i18nContent";

import { GUIDE_KEY, STOP_IMAGE_SRC } from "./constants";
import { normaliseFaqs, normaliseSections, normaliseTocItems } from "./content";
import {
  buildGuideFallbackLabels,
  buildTranslationKey,
  getGuidesFallbackTranslator,
} from "./i18n";
import type { GuideExtras, TocEntry } from "./types";

export function createGuideExtras(context: GuideSeoTemplateContext): GuideExtras {
  const translate = context.translateGuides;
  const rawTranslate = context.translator;
  const guidesFallbackLocal = getGuidesFallbackTranslator(context.lang);
  const guidesFallbackEn = getGuidesFallbackTranslator("en");
  const fallbackLabels = buildGuideFallbackLabels(guidesFallbackLocal, guidesFallbackEn);

  const coerceString = (value: unknown) => {
    if (typeof value !== "string") return undefined;
    const trimmed = value.trim();
    return trimmed.length > 0 ? trimmed : undefined;
  };

  const readArray = <T,>(
    suffix: string,
    normaliser: (value: unknown) => T[],
    options?: { skipEnglishFallback?: boolean },
  ): T[] => {
    const { skipEnglishFallback = false } = options ?? {};
    const primary = normaliser(translate(buildTranslationKey(suffix), { returnObjects: true }));
    if (primary.length > 0) return primary;
    const fallbackLocal = normaliser(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
    if (fallbackLocal.length > 0) return fallbackLocal;
    if (skipEnglishFallback) return [];
    return normaliser(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
  };

  const readString = (suffix: string, options?: { allowBlank?: boolean }): string | undefined => {
    const { allowBlank = false } = options ?? {};
    const translationKey = buildTranslationKey(suffix);
    const consume = (input: unknown) => {
      if (typeof input !== "string") return undefined;
      if (input === translationKey) return undefined;
      const trimmed = input.trim();
      if (trimmed.length > 0) return trimmed;
      if (allowBlank && input === "") return "";
      return undefined;
    };

    const rawValue = rawTranslate(translationKey);
    const primaryRaw = consume(rawValue);
    if (primaryRaw !== undefined) return primaryRaw;

    const fallbackLocal = consume(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" }));
    if (fallbackLocal !== undefined) return fallbackLocal;

    const primary = consume(translate(translationKey));
    if (primary !== undefined) return primary;

    const fallbackEn = consume(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" }));
    if (fallbackEn !== undefined) return fallbackEn;

    return undefined;
  };

  const intro = readArray("intro", ensureStringArray);
  const sections = readArray("sections", normaliseSections);
  const beforeList = readArray("beforeList", ensureStringArray);
  const stepsList = readArray("stepsList", ensureStringArray);
  const stepsMapEmbedUrl = readString("stepsMapEmbedUrl");
  const kneesList = readArray("kneesList", ensureStringArray);
  const kneesDockPrefix = readString("kneesDockPrefix", { allowBlank: true });
  const kneesDockLinkText = readString("kneesDockLinkLabel", { allowBlank: true });
  const kneesPorterPrefix = readString("kneesPorterPrefix", { allowBlank: true });
  const kneesPorterLinkText = readString("kneesPorterLinkLabel", { allowBlank: true });
  const imageAlt = readString("image.alt");
  const imageCaption = readString("image.caption");
  let faqs = readArray("faqs", normaliseFaqs, { skipEnglishFallback: true });
  if (faqs.length === 0) {
    const legacyFaq = readArray("faq", normaliseFaqs, { skipEnglishFallback: true });
    if (legacyFaq.length > 0) {
      faqs = legacyFaq;
    } else {
      const fallbackFaqs = normaliseFaqs(guidesFallbackEn(`${GUIDE_KEY}.faqs`, { returnObjects: true }));
      faqs = fallbackFaqs.length > 0 ? fallbackFaqs : normaliseFaqs(guidesFallbackEn(`${GUIDE_KEY}.faq`, { returnObjects: true }));
    }
  }

  const faqsTitle =
    readString("faqsTitle") ??
    coerceString(guidesFallbackLocal(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" })) ??
    coerceString(guidesFallbackEn(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" }));

  const howToSteps = readArray("howtoSteps", ensureStringArray);

  const tocTitle = readString("toc.onThisPage") ?? readString("tocTitle") ?? fallbackLabels.onThisPage;

  const explicitTocItems = normaliseTocItems(
    translate(buildTranslationKey("tocItems"), { returnObjects: true }),
  );
  const fallbackTocItems =
    explicitTocItems.length > 0
      ? explicitTocItems
      : normaliseTocItems(guidesFallbackLocal(`${GUIDE_KEY}.tocItems`, { returnObjects: true }));
  const fallbackEnTocItems =
    fallbackTocItems.length > 0
      ? fallbackTocItems
      : normaliseTocItems(guidesFallbackEn(`${GUIDE_KEY}.tocItems`, { returnObjects: true }));

  const tocItemsCandidates: TocEntry[] = [];
  const pushItem = (item: TocEntry | null | undefined) => {
    if (!item) return;
    if (tocItemsCandidates.some((candidate) => candidate.href === item.href)) return;
    tocItemsCandidates.push(item);
  };

  sections.forEach((section) => {
    pushItem({ href: `#${section.id}`, label: section.title });
  });
  if (beforeList.length > 0) {
    pushItem({ href: "#before", label: readString("toc.before") ?? fallbackLabels.before });
  }
  if (stepsList.length > 0) {
    pushItem({ href: "#steps", label: readString("toc.steps") ?? fallbackLabels.steps });
  }
  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);
  if (shouldRenderKnees) {
    pushItem({ href: "#knees", label: readString("toc.knees") ?? fallbackLabels.knees });
  }
  if (faqs.length > 0) {
    pushItem({ href: "#faqs", label: readString("toc.faqs") ?? faqsTitle ?? fallbackLabels.faqs });
  }

  const tocItems =
    explicitTocItems.length > 0
      ? explicitTocItems
      : fallbackTocItems.length > 0
        ? fallbackTocItems
        : fallbackEnTocItems.length > 0
          ? fallbackEnTocItems
          : tocItemsCandidates;

  return {
    intro,
    sections,
    beforeList,
    stepsList,
    kneesList,
    faqs,
    faqsTitle: faqsTitle ?? fallbackLabels.faqs,
    tocTitle,
    tocItems,
    howToSteps,
    labels: fallbackLabels,
    ...(imageAlt
      ? {
          image: {
            src: STOP_IMAGE_SRC,
            alt: imageAlt,
            ...(imageCaption !== undefined ? { caption: imageCaption } : {}),
          },
        }
      : {}),
    ...(stepsMapEmbedUrl !== undefined ? { stepsMapEmbedUrl } : {}),
    ...(kneesDockPrefix !== undefined ? { kneesDockPrefix } : {}),
    ...(kneesDockLinkText !== undefined ? { kneesDockLinkText } : {}),
    ...(kneesPorterPrefix !== undefined ? { kneesPorterPrefix } : {}),
    ...(kneesPorterLinkText !== undefined ? { kneesPorterLinkText } : {}),
  } satisfies GuideExtras;
}

