import type { GuideSeoTemplateContext } from "@/routes/guides/_GuideSeoTemplate";
import { ensureStringArray } from "@/utils/i18nContent";

import { GUIDE_KEY } from "./constants";
import { normaliseFaqs, normaliseSections, normaliseToc } from "./content";
import {
  buildGuideFallbackLabels,
  buildTranslationKey,
  getGuidesFallbackTranslator,
} from "./i18n";
import type { GuideExtras } from "./types";

export function createGuideExtras(context: GuideSeoTemplateContext): GuideExtras {
  const translate = context.translateGuides;
  const guidesFallbackLocal = getGuidesFallbackTranslator(context.lang);
  const guidesFallbackEn = getGuidesFallbackTranslator("en");
  const fallbackLabels = buildGuideFallbackLabels(guidesFallbackLocal, guidesFallbackEn);

  const readArray = <T,>(suffix: string, normaliser: (value: unknown) => T[]): T[] => {
    const primary = normaliser(translate(buildTranslationKey(suffix), { returnObjects: true }));
    if (primary.length > 0) return primary;
    const fallbackLocal = normaliser(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
    if (fallbackLocal.length > 0) return fallbackLocal;
    return normaliser(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
  };

  const readString = (suffix: string): string | undefined => {
    const translationKey = buildTranslationKey(suffix);
    const value = translate(translationKey);
    if (typeof value === "string" && value.trim().length > 0 && value !== translationKey) {
      return value.trim();
    }
    const fallbackLocal = guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" });
    if (typeof fallbackLocal === "string" && fallbackLocal.trim().length > 0) {
      return fallbackLocal.trim();
    }
    const fallbackEn = guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" });
    if (typeof fallbackEn === "string" && fallbackEn.trim().length > 0) {
      return fallbackEn.trim();
    }
    return undefined;
  };

  const intro = readArray("intro", ensureStringArray);
  const sections = readArray("sections", normaliseSections);
  const beforeList = readArray("beforeList", ensureStringArray);
  const stepsList = readArray("stepsList", ensureStringArray);
  const kneesList = readArray("kneesList", ensureStringArray);
  const kneesDockPrefix = readString("kneesDockPrefix");
  const kneesDockLinkText = readString("kneesDockLinkLabel");
  const kneesPorterPrefix = readString("kneesPorterPrefix");
  const kneesPorterLinkText = readString("kneesPorterLinkLabel");
  const faqs = readArray("faqs", normaliseFaqs);

  if (faqs.length === 0) {
    const legacyFaq = normaliseFaqs(translate(buildTranslationKey("faq"), { returnObjects: true }));
    if (legacyFaq.length > 0) {
      faqs.push(...legacyFaq);
    }
  }

  const faqsTitleFallbackRaw = guidesFallbackEn(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" });
  const faqsTitleFallback =
    typeof faqsTitleFallbackRaw === "string" && faqsTitleFallbackRaw.trim().length > 0
      ? faqsTitleFallbackRaw.trim()
      : fallbackLabels.faqs;
  const faqsTitle = readString("faqsTitle") ?? faqsTitleFallback;

  const explicitToc = normaliseToc(translate(buildTranslationKey("toc"), { returnObjects: true }));
  const fallbackToc =
    explicitToc.length > 0
      ? explicitToc
      : normaliseToc(guidesFallbackLocal(`${GUIDE_KEY}.toc`, { returnObjects: true }));
  const fallbackEnToc =
    fallbackToc.length > 0
      ? fallbackToc
      : normaliseToc(guidesFallbackEn(`${GUIDE_KEY}.toc`, { returnObjects: true }));

  const tocItems: GuideExtras["tocItems"] = [];
  const pushItem = (item: GuideExtras["tocItems"][number] | null | undefined) => {
    if (!item) return;
    if (tocItems.some((existing) => existing.href === item.href)) return;
    tocItems.push(item);
  };

  const sectionIds = new Set(sections.map((section) => section.id));
  const isDuplicateSectionId = (id: string): boolean => {
    const baseId = id.replace(/-\d+$/, "");
    return baseId !== id && sectionIds.has(baseId);
  };

  sections.forEach((section) => {
    if (isDuplicateSectionId(section.id)) return;
    pushItem({ href: `#${section.id}`, label: section.title });
  });
  if (beforeList.length > 0) {
    pushItem({ href: "#before", label: readString("toc.before") ?? fallbackLabels.before });
  }
  if (stepsList.length > 0) {
    pushItem({ href: "#steps", label: readString("toc.steps") ?? fallbackLabels.steps });
  }
  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);
  if (shouldRenderKnees) {
    pushItem({ href: "#knees", label: readString("toc.knees") ?? fallbackLabels.knees });
  }
  if (faqs.length > 0) {
    pushItem({ href: "#faqs", label: readString("toc.faqs") ?? faqsTitle ?? fallbackLabels.faqs });
  }

  const finalToc =
    explicitToc.length > 0
      ? explicitToc
      : fallbackToc.length > 0
        ? fallbackToc
        : fallbackEnToc.length > 0
          ? fallbackEnToc
          : tocItems;

  const howToSteps = readArray("howtoSteps", ensureStringArray);
  if (howToSteps.length === 0 && stepsList.length > 0) {
    howToSteps.push(...stepsList);
  }

  return {
    intro,
    sections,
    tocItems: finalToc,
    tocTitle: readString("tocTitle") ?? fallbackLabels.onThisPage,
    beforeList,
    stepsList,
    kneesList,
    faqs,
    faqsTitle: faqsTitle ?? fallbackLabels.faqs,
    howToSteps,
    labels: fallbackLabels,
    ...(kneesDockPrefix !== undefined ? { kneesDockPrefix } : {}),
    ...(kneesDockLinkText !== undefined ? { kneesDockLinkText } : {}),
    ...(kneesPorterPrefix !== undefined ? { kneesPorterPrefix } : {}),
    ...(kneesPorterLinkText !== undefined ? { kneesPorterLinkText } : {}),
  };
}

import type { GuideSeoTemplateContext } from "@/routes/guides/_GuideSeoTemplate";
import { ensureStringArray } from "@/utils/i18nContent";

import { GUIDE_KEY } from "./constants";
import { normaliseFaqs, normaliseSections, normaliseToc } from "./content";
import {
  buildGuideFallbackLabels,
  buildTranslationKey,
  getGuidesFallbackTranslator,
} from "./i18n";
import type { GuideExtras } from "./types";

export function createGuideExtras(context: GuideSeoTemplateContext): GuideExtras {
  const translate = context.translateGuides;
  const guidesFallbackLocal = getGuidesFallbackTranslator(context.lang);
  const guidesFallbackEn = getGuidesFallbackTranslator("en");
  const fallbackLabels = buildGuideFallbackLabels(guidesFallbackLocal, guidesFallbackEn);

  const readArray = <T,>(suffix: string, normaliser: (value: unknown) => T[]): T[] => {
    const primary = normaliser(translate(buildTranslationKey(suffix), { returnObjects: true }));
    if (primary.length > 0) return primary;
    const fallbackLocal = normaliser(guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
    if (fallbackLocal.length > 0) return fallbackLocal;
    return normaliser(guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { returnObjects: true }));
  };

  const readString = (suffix: string): string | undefined => {
    const translationKey = buildTranslationKey(suffix);
    const value = translate(translationKey);
    if (typeof value === "string" && value.trim().length > 0 && value !== translationKey) {
      return value.trim();
    }
    const fallbackLocal = guidesFallbackLocal(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" });
    if (typeof fallbackLocal === "string" && fallbackLocal.trim().length > 0) {
      return fallbackLocal.trim();
    }
    const fallbackEn = guidesFallbackEn(`${GUIDE_KEY}.${suffix}`, { defaultValue: "" });
    if (typeof fallbackEn === "string" && fallbackEn.trim().length > 0) {
      return fallbackEn.trim();
    }
    return undefined;
  };

  const intro = readArray("intro", ensureStringArray);
  const sections = readArray("sections", normaliseSections);
  const beforeList = readArray("beforeList", ensureStringArray);
  const stepsList = readArray("stepsList", ensureStringArray);
  const kneesList = readArray("kneesList", ensureStringArray);
  const kneesDockPrefix = readString("kneesDockPrefix");
  const kneesDockLinkText = readString("kneesDockLinkLabel");
  const kneesPorterPrefix = readString("kneesPorterPrefix");
  const kneesPorterLinkText = readString("kneesPorterLinkLabel");
  const faqs = readArray("faqs", normaliseFaqs);

  if (faqs.length === 0) {
    const legacyFaq = normaliseFaqs(translate(buildTranslationKey("faq"), { returnObjects: true }));
    if (legacyFaq.length > 0) {
      faqs.push(...legacyFaq);
    }
  }

  const faqsTitleFallbackRaw = guidesFallbackEn(`${GUIDE_KEY}.faqsTitle`, { defaultValue: "" });
  const faqsTitleFallback =
    typeof faqsTitleFallbackRaw === "string" && faqsTitleFallbackRaw.trim().length > 0
      ? faqsTitleFallbackRaw.trim()
      : fallbackLabels.faqs;
  const faqsTitle = readString("faqsTitle") ?? faqsTitleFallback;

  const explicitToc = normaliseToc(translate(buildTranslationKey("toc"), { returnObjects: true }));
  const fallbackToc =
    explicitToc.length > 0
      ? explicitToc
      : normaliseToc(guidesFallbackLocal(`${GUIDE_KEY}.toc`, { returnObjects: true }));
  const fallbackEnToc =
    fallbackToc.length > 0
      ? fallbackToc
      : normaliseToc(guidesFallbackEn(`${GUIDE_KEY}.toc`, { returnObjects: true }));

  const tocItems: GuideExtras["tocItems"] = [];
  const pushItem = (item: GuideExtras["tocItems"][number] | null | undefined) => {
    if (!item) return;
    if (tocItems.some((existing) => existing.href === item.href)) return;
    tocItems.push(item);
  };

  sections.forEach((section) => {
    pushItem({ href: `#${section.id}`, label: section.title });
  });
  if (beforeList.length > 0) {
    pushItem({ href: "#before", label: readString("toc.before") ?? fallbackLabels.before });
  }
  if (stepsList.length > 0) {
    pushItem({ href: "#steps", label: readString("toc.steps") ?? fallbackLabels.steps });
  }
  const shouldRenderKnees =
    kneesList.length > 0 ||
    (kneesDockPrefix && kneesDockLinkText) ||
    (kneesPorterPrefix && kneesPorterLinkText);
  if (shouldRenderKnees) {
    pushItem({ href: "#knees", label: readString("toc.knees") ?? fallbackLabels.knees });
  }
  if (faqs.length > 0) {
    pushItem({ href: "#faqs", label: readString("toc.faqs") ?? faqsTitle ?? fallbackLabels.faqs });
  }

  const finalToc =
    explicitToc.length > 0
      ? explicitToc
      : fallbackToc.length > 0
        ? fallbackToc
        : fallbackEnToc.length > 0
          ? fallbackEnToc
          : tocItems;

  const howToSteps = readArray("howtoSteps", ensureStringArray);
  if (howToSteps.length === 0 && stepsList.length > 0) {
    howToSteps.push(...stepsList);
  }

  return {
    intro,
    sections,
    tocItems: finalToc,
    tocTitle: readString("tocTitle") ?? fallbackLabels.onThisPage,
    beforeList,
    stepsList,
    kneesList,
    faqs,
    faqsTitle: faqsTitle ?? fallbackLabels.faqs,
    howToSteps,
    labels: fallbackLabels,
    ...(kneesDockPrefix !== undefined ? { kneesDockPrefix } : {}),
    ...(kneesDockLinkText !== undefined ? { kneesDockLinkText } : {}),
    ...(kneesPorterPrefix !== undefined ? { kneesPorterPrefix } : {}),
    ...(kneesPorterLinkText !== undefined ? { kneesPorterLinkText } : {}),
  };
}

// src/routes/how-to-get-here/pickBestLink.ts
// Utility for finding the best route link based on user preferences

import type { RoutePickerSelection } from "./components/RoutePicker";
import type { AugmentedDestinationLink, TransportMode } from "./types";

/**
 * Scores a transport mode based on user preference.
 * Lower scores are better.
 */
export function scoreTransportMode(
  mode: TransportMode,
  preference: RoutePickerSelection["preference"]
): number {
  switch (preference) {
    case "fastest":
      return mode === "car" ? 0 : mode === "ferry" ? 1 : mode === "train" ? 2 : mode === "bus" ? 3 : 4;
    case "cheapest":
      return mode === "walk" ? 0 : mode === "bus" ? 1 : mode === "train" ? 2 : mode === "ferry" ? 3 : 4;
    case "least-walking":
      return mode === "car" ? 0 : mode === "train" ? 1 : mode === "bus" ? 2 : mode === "ferry" ? 3 : 4;
    case "luggage-friendly":
      return mode === "car" ? 0 : mode === "train" ? 1 : mode === "bus" ? 2 : mode === "ferry" ? 3 : 4;
    default:
      return 0;
  }
}

/**
 * Picks the best route link from a list based on the user's selection criteria.
 * Prefers "to" direction routes. Applies penalties for transfers and late-night ferry.
 */
export function pickBestLink(
  links: AugmentedDestinationLink[],
  selection: RoutePickerSelection
): AugmentedDestinationLink | null {
  // Prefer "to" direction routes (going to the hostel)
  const candidates = links.filter((link) => link.direction === "to");
  const pool = candidates.length > 0 ? candidates : links;

  let best: AugmentedDestinationLink | null = null;
  let bestScore = Number.POSITIVE_INFINITY;

  for (const link of pool) {
    const modeScore = link.transportModes.reduce(
      (sum, mode) => sum + scoreTransportMode(mode, selection.preference),
      0
    );
    // Penalty for transfers (each additional mode adds complexity)
    const transferPenalty = Math.max(0, link.transportModes.length - 1) * 2;
    // Late-night ferry is unreliable, add penalty
    const lateNightPenalty =
      selection.arrival === "late-night" && link.transportModes.includes("ferry") ? 10 : 0;
    const score = modeScore + transferPenalty + lateNightPenalty;

    if (score < bestScore) {
      bestScore = score;
      best = link;
    }
  }

  return best;
}

import type { ReactNode } from "react";

import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@acme/design-system/primitives";
import { CfImage } from "@acme/ui/atoms/CfImage";
import { CfResponsiveImage } from "@acme/ui/atoms/CfResponsiveImage";

import { ZoomIn } from "@/icons";
import { findPlaceholderBinding, type RouteDefinition } from "@/lib/how-to-get-here/definitions";
import type { RouteContent } from "@/lib/how-to-get-here/schema";

import { renderCallout } from "./callouts";
import { getValueAtPath, isPlainObject } from "./content-utils";
import { isLinkedCopy, renderLink, renderLinkedCopy, renderRichText, resolveLinkTarget } from "./linking";
import type { RenderContext } from "./types";

export function getSections(
  definition: RouteDefinition,
  content: RouteContent,
): [string, unknown][] {
  if (definition.sectionPaths && definition.sectionPaths.length > 0) {
    return definition.sectionPaths
      .map((path) => {
        const value = getValueAtPath(content, path);
        return [path, value] as [string, unknown];
      })
      .filter(([, value]) => value !== undefined && value !== null);
  }

  const rootKey = definition.sectionsRoot ?? "sections";
  const root = content[rootKey as keyof RouteContent];
  if (root && typeof root === "object") {
    return Object.entries(root as Record<string, unknown>).filter(
      ([, value]) => value !== undefined && value !== null,
    );
  }
  return [];
}

export function renderSection(
  sectionKey: string,
  data: unknown,
  ctx: RenderContext,
  pathPrefix: string,
): ReactNode | null {
  if (!isPlainObject(data)) return null;

  const path = pathPrefix ? `${pathPrefix}.${sectionKey}` : sectionKey;
  const record = data as Record<string, unknown>;
  const title =
    (record["title"] as string | undefined) ??
    (record["heading"] as string | undefined) ??
    (record["name"] as string | undefined) ??
    undefined;

  const paragraphs: ReactNode[] = [];
  const lists: ReactNode[][] = [];
  const subSections: ReactNode[] = [];
  let figure: { src: string; alt: string; caption?: string } | null = null;

  for (const [key, value] of Object.entries(data)) {
    if (key === "title" || key === "heading" || key === "name") continue;
    const currentPath = `${path}.${key}`;

    if (key === "aside" || key === "tip" || key === "cta") {
      const calloutNode = renderCallout(currentPath, value, ctx);
      if (calloutNode) {
        paragraphs.push(calloutNode);
        continue;
      }
    }

    if (Array.isArray(value)) {
      const items: ReactNode[] = [];
      for (let index = 0; index < value.length; index += 1) {
        const entry = value[index];
        if (typeof entry === "string") {
          const itemPath = `${currentPath}.${index}`;
          const binding =
            findPlaceholderBinding(ctx.definition, itemPath) ??
            findPlaceholderBinding(ctx.definition, currentPath);
          items.push(<li key={itemPath}>{renderRichText(entry, itemPath, binding, ctx)}</li>);
        } else if (isPlainObject(entry) && (entry["title"] || entry["heading"])) {
          subSections.push(renderSection(String(index), entry, ctx, currentPath));
        }
      }
      if (items.length > 0) {
        lists.push(items);
      }
      continue;
    }

    if (typeof value === "string") {
      const binding = findPlaceholderBinding(ctx.definition, currentPath);
      paragraphs.push(
        <p key={currentPath} className="text-base leading-relaxed">
          {renderRichText(value, currentPath, binding, ctx)}
        </p>,
      );
      continue;
    }

    if (isLinkedCopy(value)) {
      const binding = findPlaceholderBinding(ctx.definition, currentPath);
      paragraphs.push(
        <p key={currentPath} className="text-base leading-relaxed">
          {renderLinkedCopy(value, currentPath, binding, ctx, currentPath)}
        </p>,
      );
      continue;
    }

    if (key === "figure" || key === "image") {
      figure = getFigure(currentPath, value, ctx.definition) ?? figure;
      continue;
    }

    if (isPlainObject(value)) {
      const linkListNode = renderLinkListEntry(
        ctx.definition,
        ctx,
        currentPath,
        value as Record<string, unknown>,
      );
      if (linkListNode) {
        paragraphs.push(linkListNode);
        continue;
      }
    }

    if (isPlainObject(value) && (value["title"] || value["heading"] || value["name"])) {
      const rendered = renderSection(key, value, ctx, path);
      if (rendered) {
        subSections.push(rendered);
      }
      continue;
    }

    if (typeof value === "object" && value !== null) {
      const binding = findPlaceholderBinding(ctx.definition, currentPath);
      const entries: ReactNode[] = [];
      for (const [nestedKey, nestedVal] of Object.entries(value)) {
        if (typeof nestedVal === "string") {
          entries.push(
            <p key={`${currentPath}-${nestedKey}`} className="text-base leading-relaxed">
              {renderRichText(nestedVal, `${currentPath}.${nestedKey}`, binding, ctx)}
            </p>,
          );
        }
      }
      if (entries.length > 0) {
        paragraphs.push(
          <div key={currentPath} className="space-y-2">
            {entries}
          </div>,
        );
      }
    }
  }

  if (!title && paragraphs.length === 0 && lists.length === 0 && !figure && subSections.length === 0) {
    return null;
  }

  const figureCaptionId =
    figure?.caption && path
      ? `${path.replace(/[^a-zA-Z0-9_-]+/g, "-")}-caption`
      : undefined;

  return (
    <section
      key={path}
      className="space-y-6 rounded-3xl border border-brand-outline/30 bg-brand-surface p-6 shadow-sm dark:border-brand-outline/20 dark:bg-brand-surface/80"
    >
      {title ? (
        <header>
          <h2 className="text-2xl font-semibold text-brand-heading dark:text-brand-text">{title}</h2>
        </header>
      ) : null}
      {paragraphs}
      {lists.map((items, index) => (
        <ul key={`${path}-list-${index}`} className="list-disc space-y-3 pl-5 text-base leading-relaxed">
          {items}
        </ul>
      ))}
      {renderInlineGallery(path, ctx)}
      {figure ? (
        <figure
          className="overflow-hidden rounded-2xl border border-brand-outline/20 shadow-sm"
          aria-labelledby={figureCaptionId}
        >
          <CfImage
            src={figure.src}
            preset="gallery"
            alt={figure.alt}
            className="size-full object-cover"
            data-aspect="4/3"
          />
          {figure.caption ? (
              <figcaption
                id={figureCaptionId}
                className="bg-brand-surface px-4 py-3 text-sm text-brand-text/70 dark:bg-brand-surface/70 dark:text-brand-text/80"
              >
              {figure.caption}
            </figcaption>
          ) : null}
        </figure>
      ) : null}
      {subSections}
    </section>
  );
}

function getFigure(
  sectionPath: string,
  value: unknown,
  definition: RouteDefinition,
): { src: string; alt: string; caption?: string } | null {
  if (!value || typeof value !== "object") return null;
  const mediaBinding = definition.media?.find(
    (entry) => entry.key === sectionPath || entry.key === `${sectionPath}.image`,
  );
  if (mediaBinding) {
    const captionRaw = (value as Record<string, unknown>)["caption"];
    return {
      src: mediaBinding.src,
      alt: String((value as Record<string, unknown>)["alt"] ?? ""),
      ...(typeof captionRaw === "string" ? { caption: captionRaw } : {}),
    };
  }

  const record = value as Record<string, unknown>;
  if (typeof record["src"] === "string") {
    const captionRaw = record["caption"];
    return {
      src: record["src"],
      alt: String(record["alt"] ?? ""),
      ...(typeof captionRaw === "string" ? { caption: captionRaw } : {}),
    };
  }
  return null;
}

function renderLinkListEntry(
  definition: RouteDefinition,
  ctx: RenderContext,
  path: string,
  value: Record<string, unknown>,
): ReactNode | null {
  const config = definition.linkLists?.find((entry) => entry.key === path);
  if (!config) return null;

  const links: ReactNode[] = [];
  for (const item of config.items) {
    const label = value[item.id];
    if (typeof label !== "string") continue;
    const target = resolveLinkTarget(item.target, ctx.context);
    links.push(renderLink(target, label, `${path}-${item.id}`));
  }

  if (links.length === 0) {
    return null;
  }

  const intro = typeof value["linksIntro"] === "string" ? value["linksIntro"] : undefined;
  const separator = typeof value["linksSeparator"] === "string" ? value["linksSeparator"] : "·";
  const suffix = typeof value["linksSuffix"] === "string" ? value["linksSuffix"] : "";

  const parts: ReactNode[] = [];

  if (intro) {
    parts.push(intro);
    parts.push(" ");
  }

  links.forEach((linkNode, index) => {
    if (index > 0) {
      parts.push(` ${separator} `);
    }
    parts.push(linkNode);
  });

  if (suffix) {
    const needsSpace = !/^[.,!?]/.test(suffix);
    if (needsSpace && parts.length > 0) {
      const last = parts[parts.length - 1];
      const lastHasTrailingSpace = typeof last === "string" && /\s$/.test(last);
      if (!lastHasTrailingSpace) {
        parts.push(" ");
      }
    }
    parts.push(suffix);
  }

  return (
    <p key={path} className="text-base leading-relaxed">
      {parts}
    </p>
  );
}

function renderInlineGallery(path: string, ctx: RenderContext): ReactNode | null {
  const gallery = ctx.definition.galleries.find(
    (entry) => entry.inline && matchesGalleryPath(entry.key, path),
  );
  if (!gallery) {
    return null;
  }

  const galleryMeta = getValueAtPath(ctx.content, gallery.key);
  const itemsMeta = isPlainObject(galleryMeta)
    ? (galleryMeta as Record<string, { alt?: string; caption?: string }>)
    : undefined;

  return (
    <div className="space-y-4">
      <ul className="grid grid-cols-1 gap-6 list-none p-0 sm:grid-cols-1 md:grid-cols-2">
        {gallery.items.map((item) => {
          const meta = itemsMeta?.[item.id];
          const altText = meta?.alt ?? meta?.caption ?? item.id;
          return (
            <li key={item.id} className="m-0">
              <Dialog>
                <DialogTrigger asChild>
                  <button type="button" className="group w-full min-h-11 min-w-11 text-start">
                    <figure className="relative overflow-hidden rounded-2xl border border-brand-outline/20 bg-brand-surface shadow-sm dark:border-brand-outline/30">
                      <CfResponsiveImage
                        src={item.src}
                        alt={altText}
                        preset="gallery"
                        className="size-full object-cover"
                        data-aspect={item.aspectRatio ?? "4/3"}
                      />
                      <span
                        aria-hidden
                        className="pointer-events-none absolute end-3 top-3 inline-flex size-9 items-center justify-center rounded-full bg-brand-surface/80 text-brand-heading shadow-sm backdrop-blur transition group-hover:bg-brand-surface dark:bg-brand-surface/60 dark:text-brand-text"
                      >
                        <ZoomIn className="size-4" />
                      </span>
                      {meta?.caption ? (
                        <figcaption className="bg-brand-surface px-4 py-3 text-sm text-brand-text/80 dark:bg-brand-surface/70 dark:text-brand-text/80">
                          {meta.caption}
                        </figcaption>
                      ) : null}
                    </figure>
                  </button>
                </DialogTrigger>

                <DialogContent>
                  <DialogHeader>
                    <DialogTitle>{meta?.caption ?? altText}</DialogTitle>
                  </DialogHeader>
                  <div className="overflow-hidden rounded-2xl border border-brand-outline/20 bg-brand-surface shadow-sm dark:border-brand-outline/30 dark:bg-brand-surface/40">
                    <CfResponsiveImage
                      src={item.src}
                      alt={altText}
                      preset="hero"
                      className="size-full object-cover"
                      data-aspect={item.aspectRatio ?? "4/3"}
                      priority
                    />
                  </div>
                </DialogContent>
              </Dialog>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

function matchesGalleryPath(galleryKey: string, sectionPath: string): boolean {
  const normalizedKey = galleryKey.replace(/\.items$/, "");
  return normalizedKey === sectionPath;
}

/**
 * Transform legacy route content to guide content format.
 *
 * Implements the schema mapping documented in TASK-05:
 * - meta → seo
 * - header or hero → intro (supports both patterns)
 * - tip/aside/cta → callouts with link tokens
 * - sections object → sections array with stable IDs
 * - linkBindings (linkObject + placeholders) → guide tokens
 * - galleries → gallery metadata
 */

import type { LinkBinding, LinkTarget } from "@/lib/how-to-get-here/definitions";
import type { LinkedCopy, RouteContent } from "@/lib/how-to-get-here/schema";

interface GuideContent {
  seo: {
    title: string;
    description: string;
  };
  intro: {
    title: string;
    body: string;
  };
  callouts?: {
    tip?: string;
    aside?: string;
    cta?: string;
  };
  sections?: Array<{
    id: string;
    title?: string;
    body?: string;
    list?: string[];
  }>;
  galleries?: Array<{
    heading?: string;
    items: Array<{
      src: string;
      caption?: string;
      alt?: string;
      aspectRatio?: string;
      preset?: string;
    }>;
  }>;
}

interface RouteDefinition {
  contentKey?: string;
  linkBindings?: LinkBinding[];
  galleries?: Array<{
    key: string;
    items: Array<{
      id: string;
      src: string;
      aspectRatio?: string;
      preset?: string;
    }>;
  }>;
}

/**
 * Convert link target to guide token format.
 */
function linkTargetToToken(target: LinkTarget, label: string): string {
  switch (target.type) {
    case "howToOverview":
      return `%HOWTO:how-to-get-here|${label}%`;
    case "guide":
      return `%LINK:${target.guideKey}|${label}%`;
    case "directions":
      return `%HOWTO:${target.slug}|${label}%`;
    case "guidesSlug":
      return `%LINK:${target.slug}|${label}%`;
    case "external":
      return `%URL:${target.href}|${label}%`;
    default:
      return label;
  }
}

/**
 * Convert LinkedCopy (split text) to string with guide token.
 */
function linkedCopyToString(linkedCopy: LinkedCopy, target: LinkTarget): string {
  const { before = "", linkLabel, after = "" } = linkedCopy;
  const token = linkTargetToToken(target, linkLabel);
  return `${before}${token}${after}`;
}

/**
 * Replace placeholder tags like <link>Label</link> with guide tokens.
 */
function replacePlaceholders(
  text: string,
  placeholders: Record<string, LinkTarget | null>,
): string {
  let result = text;

  for (const [placeholderName, target] of Object.entries(placeholders)) {
    if (!target) continue;

    // Match <placeholderName>Label</placeholderName> (case-sensitive)
    const regex = new RegExp(`<${placeholderName}>([^<]+)</${placeholderName}>`, "g");
    result = result.replace(regex, (_match, label: string) => {
      return linkTargetToToken(target, label);
    });
  }

  return result;
}

/**
 * Find link binding for a given key path.
 */
function findBinding(bindings: LinkBinding[], keyPath: string): LinkBinding | undefined {
  // Direct match
  const direct = bindings.find((b) => b.key === keyPath);
  if (direct) return direct;

  // Wildcard match (e.g., "sections.*.list" matches "sections.foo.list")
  for (const binding of bindings) {
    if (binding.key.includes("*")) {
      const pattern = binding.key.replace(/\*/g, "[^.]+");
      const regex = new RegExp(`^${pattern}$`);
      if (regex.test(keyPath)) {
        return binding;
      }
    }
  }

  return undefined;
}

/**
 * Process callout content with link bindings.
 */
function processCallout(
  callout: any,
  variantKey: string,
  linkBindings: LinkBinding[],
): string | undefined {
  const bodyKey = `${variantKey}.body`;
  const copyKey = `${variantKey}.copy`;
  const binding = findBinding(linkBindings, bodyKey) || findBinding(linkBindings, copyKey);

  if (!binding) {
    // No bindings - return plain text
    if (typeof callout.copy === "string") return callout.copy;
    if (typeof callout.body === "string") return callout.body;
    if (typeof callout.body === "object" && "linkLabel" in callout.body) {
      // LinkedCopy without binding - just concatenate
      const { before = "", linkLabel, after = "" } = callout.body;
      return `${before}${linkLabel}${after}`;
    }
    return undefined;
  }

  // Has bindings
  if (binding.linkObject) {
    // linkObject pattern (split text)
    const body = callout.body;
    if (typeof body === "object" && "linkLabel" in body) {
      return linkedCopyToString(body, binding.linkObject);
    }
  }

  if (binding.placeholders) {
    // placeholders pattern (template-based)
    const text = callout.copy || callout.body;
    if (typeof text === "string") {
      return replacePlaceholders(text, binding.placeholders);
    }
  }

  return undefined;
}

/**
 * Process section field with link bindings.
 */
function processSectionField(
  value: any,
  keyPath: string,
  linkBindings: LinkBinding[],
): string | undefined {
  const binding = findBinding(linkBindings, keyPath);

  if (!binding) {
    // No binding - return plain text
    if (typeof value === "string") return value;
    if (typeof value === "object" && "linkLabel" in value) {
      const { before = "", linkLabel, after = "" } = value;
      return `${before}${linkLabel}${after}`;
    }
    return undefined;
  }

  // Has binding
  if (binding.linkObject) {
    if (typeof value === "object" && "linkLabel" in value) {
      return linkedCopyToString(value, binding.linkObject);
    }
  }

  if (binding.placeholders) {
    if (typeof value === "string") {
      return replacePlaceholders(value, binding.placeholders);
    }
  }

  return undefined;
}

/**
 * Transform route content to guide content.
 */
export function transformRouteToGuide(
  routeDefinition: RouteDefinition,
  routeContent: RouteContent,
  guideKey: string,
): GuideContent {
  const linkBindings = routeDefinition.linkBindings || [];

  // Extract meta
  const meta = routeContent.meta as any;
  if (!meta || typeof meta !== "object") {
    throw new Error("Route content missing meta block");
  }

  // Extract header or hero (fallback support for both patterns)
  const header = routeContent.header as any;
  const hero = routeContent.hero as any;
  const headerBlock = header || hero;

  if (!headerBlock || typeof headerBlock !== "object") {
    throw new Error("Route content missing header or hero block");
  }

  // Build guide content
  const guide: GuideContent = {
    seo: {
      title: meta.title as string,
      description: meta.description as string,
    },
    intro: {
      title: headerBlock.title as string,
      body: headerBlock.description as string,
    },
  };

  // Process callouts
  const callouts: Record<string, string> = {};
  for (const variant of ["tip", "aside", "cta"]) {
    const callout = routeContent[variant];
    if (callout && typeof callout === "object") {
      const processed = processCallout(callout, variant, linkBindings);
      if (processed) {
        callouts[variant] = processed;
      }
    }
  }
  if (Object.keys(callouts).length > 0) {
    guide.callouts = callouts as any;
  }

  // Process sections
  const sections = routeContent.sections;
  if (sections && typeof sections === "object" && !Array.isArray(sections)) {
    guide.sections = Object.entries(sections).map(([id, section]: [string, any]) => {
      const guideSection: any = { id };

      if (section.title) {
        guideSection.title = section.title;
      }

      // Body field
      if (section.body) {
        const processed = processSectionField(section.body, `sections.${id}.body`, linkBindings);
        if (processed) guideSection.body = processed;
      }

      // Link field (merge into body)
      if (section.link) {
        const processed = processSectionField(section.link, `sections.${id}.link`, linkBindings);
        if (processed) {
          guideSection.body = guideSection.body
            ? `${guideSection.body} ${processed}`
            : processed;
        }
      }

      // CTA field (merge into body)
      if (section.cta) {
        const processed = processSectionField(section.cta, `sections.${id}.cta`, linkBindings);
        if (processed) {
          guideSection.body = guideSection.body
            ? `${guideSection.body} ${processed}`
            : processed;
        }
      }

      // Intro field (merge into body)
      if (section.intro) {
        const processed = processSectionField(section.intro, `sections.${id}.intro`, linkBindings);
        if (processed) {
          guideSection.body = processed;
        }
      }

      // List fields (points or list)
      const list = section.points || section.list;
      if (Array.isArray(list)) {
        guideSection.list = list.map((item: any) => {
          if (typeof item === "string") return item;
          return item;
        });
      }

      return guideSection;
    });
  }

  // Process galleries
  if (routeDefinition.galleries && routeDefinition.galleries.length > 0) {
    guide.galleries = routeDefinition.galleries.map((galleryDef) => {
      const keyParts = galleryDef.key.split(".");
      let contentGallery: any = routeContent;
      for (const part of keyParts.slice(0, -1)) {
        contentGallery = contentGallery?.[part];
      }

      const gallery: any = {};

      if (contentGallery?.heading) {
        gallery.heading = contentGallery.heading;
      }

      gallery.items = galleryDef.items.map((item, index) => {
        const contentItem = contentGallery?.items?.[index] || {};
        return {
          src: item.src,
          caption: contentItem.caption,
          alt: contentItem.alt,
          aspectRatio: item.aspectRatio,
          preset: item.preset,
        };
      });

      return gallery;
    });
  }

  return guide;
}

