import { execSync } from "node:child_process";
import { existsSync, mkdirSync, readFileSync, rmSync, writeFileSync } from "node:fs";
import path from "node:path";

import { describe, expect, it } from "@jest/globals";

const APP_ROOT = path.resolve(__dirname, "../../../..");
const SCRIPT_PATH = path.join(APP_ROOT, "scripts", "check-i18n-coverage.ts");
const TEST_OUTPUT_DIR = path.join(APP_ROOT, ".test-output", "i18n-coverage");
const TEST_LOCALES_DIR = path.join(TEST_OUTPUT_DIR, "locales");

type CoverageReportJson = {
  schemaVersion: number;
  baselineLocale: string;
  locales: string[];
  summary: {
    totalMissingFiles: number;
    totalMissingKeys: number;
  };
  reports: Array<{
    locale: string;
    missingFiles: string[];
    missingKeys: Record<string, string[]>;
  }>;
};

function setupTestLocales(): void {
  if (existsSync(TEST_OUTPUT_DIR)) {
    rmSync(TEST_OUTPUT_DIR, { recursive: true, force: true });
  }
  mkdirSync(TEST_LOCALES_DIR, { recursive: true });

  const enDir = path.join(TEST_LOCALES_DIR, "en");
  mkdirSync(path.join(enDir, "guides", "content"), { recursive: true });
  writeFileSync(
    path.join(enDir, "guides", "content", "testGuide.json"),
    JSON.stringify(
      {
        seo: { title: "Test Guide", description: "Test description" },
        intro: { title: "Welcome", body: "Introduction text" },
        sections: [{ id: "sec1", title: "Section 1", body: "Content here" }],
      },
      null,
      2,
    ),
  );
  writeFileSync(
    path.join(enDir, "common.json"),
    JSON.stringify({ greeting: "Hello", farewell: "Goodbye" }, null, 2),
  );

  const deDir = path.join(TEST_LOCALES_DIR, "de");
  mkdirSync(path.join(deDir, "guides", "content"), { recursive: true });
  writeFileSync(
    path.join(deDir, "common.json"),
    JSON.stringify({ greeting: "Hallo" }, null, 2),
  );

  const frDir = path.join(TEST_LOCALES_DIR, "fr");
  mkdirSync(path.join(frDir, "guides", "content"), { recursive: true });
  writeFileSync(
    path.join(frDir, "guides", "content", "testGuide.json"),
    JSON.stringify(
      {
        seo: { title: "Guide de test", description: "Description du test" },
        intro: { title: "Bienvenue", body: "Texte d'introduction" },
        sections: [{ id: "sec1", title: "Section 1", body: "Contenu ici" }],
      },
      null,
      2,
    ),
  );
  writeFileSync(
    path.join(frDir, "common.json"),
    JSON.stringify({ greeting: "Bonjour", farewell: "Au revoir" }, null, 2),
  );
}

function cleanupTestLocales(): void {
  if (existsSync(TEST_OUTPUT_DIR)) {
    rmSync(TEST_OUTPUT_DIR, { recursive: true, force: true });
  }
}

function runCoverageJson(args: string[], outputFile: string): CoverageReportJson {
  execSync(
    `pnpm exec tsx "${SCRIPT_PATH}" --json --output="${outputFile}" ${args.join(" ")}`,
    { cwd: APP_ROOT, encoding: "utf8", stdio: "pipe" },
  );
  return JSON.parse(readFileSync(outputFile, "utf8")) as CoverageReportJson;
}

describe("check-i18n-coverage CLI", () => {
  describe("JSON output format", () => {
    it("outputs valid JSON with correct schema structure", () => {
      setupTestLocales();
      try {
        const outputPath = path.join(TEST_OUTPUT_DIR, "report.json");
        const report = runCoverageJson(
          [`--locales-root="${TEST_LOCALES_DIR}"`, "--locales=de,fr"],
          outputPath,
        );

        expect(report.schemaVersion).toBe(1);
        expect(report.baselineLocale).toBe("en");
        expect(report.locales).toEqual(["de", "fr"]);
        expect(report.summary).toEqual({ totalMissingFiles: 1, totalMissingKeys: 1 });
        expect(report.reports.map((r) => r.locale)).toEqual(["de", "fr"]);

        const de = report.reports.find((r) => r.locale === "de");
        expect(de?.missingFiles).toEqual(["guides/content/testGuide.json"]);
        expect(de?.missingKeys).toEqual({ "common.json": ["farewell"] });

        const fr = report.reports.find((r) => r.locale === "fr");
        expect(fr?.missingFiles).toEqual([]);
        expect(fr?.missingKeys).toEqual({});
      } finally {
        cleanupTestLocales();
      }
    });

    it("includes per-locale reports with correct structure", () => {
      setupTestLocales();
      try {
        const outputPath = path.join(TEST_OUTPUT_DIR, "report2.json");
        const report = runCoverageJson(
          [`--locales-root="${TEST_LOCALES_DIR}"`, "--locales=de,fr"],
          outputPath,
        );

        for (const localeReport of report.reports) {
          expect(typeof localeReport.locale).toBe("string");
          expect(Array.isArray(localeReport.missingFiles)).toBe(true);
          expect(typeof localeReport.missingKeys).toBe("object");
          expect(localeReport.missingKeys).not.toBeNull();
        }
      } finally {
        cleanupTestLocales();
      }
    });

    it("includes locales from i18nConfig by default", () => {
      setupTestLocales();
      try {
        const outputPath = path.join(TEST_OUTPUT_DIR, "report3.json");
        const report = runCoverageJson([`--locales-root="${TEST_LOCALES_DIR}"`], outputPath);

        expect(report.locales).toContain("de");
        expect(report.locales).toContain("fr");
        expect(report.locales).toContain("it");
        expect(report.locales).toContain("es");
        expect(report.locales).not.toContain("en");
      } finally {
        cleanupTestLocales();
      }
    });
  });

  describe("--fail-on-missing flag", () => {
    it("exits with code 1 when missing keys found and flag is set", () => {
      setupTestLocales();
      try {
        execSync(
          `pnpm exec tsx "${SCRIPT_PATH}" --fail-on-missing --locales-root="${TEST_LOCALES_DIR}" --locales=de,fr`,
          { cwd: APP_ROOT, encoding: "utf8", stdio: "pipe" },
        );
        throw new Error("Expected script to exit non-zero in --fail-on-missing mode");
      } catch (error) {
        const exitCode = (error as { status?: number }).status;
        expect(exitCode).toBe(1);
      } finally {
        cleanupTestLocales();
      }
    });
  });

  describe("summary statistics", () => {
    it("calculates correct totals across all locales", () => {
      setupTestLocales();
      try {
        const outputPath = path.join(TEST_OUTPUT_DIR, "report4.json");
        const report = runCoverageJson(
          [`--locales-root="${TEST_LOCALES_DIR}"`, "--locales=de,fr"],
          outputPath,
        );

        let expectedMissingFiles = 0;
        let expectedMissingKeys = 0;
        for (const localeReport of report.reports) {
          expectedMissingFiles += localeReport.missingFiles.length;
          for (const keys of Object.values(localeReport.missingKeys)) {
            expectedMissingKeys += keys.length;
          }
        }

        expect(report.summary.totalMissingFiles).toBe(expectedMissingFiles);
        expect(report.summary.totalMissingKeys).toBe(expectedMissingKeys);
      } finally {
        cleanupTestLocales();
      }
    });
  });
});

/* eslint-disable security/detect-non-literal-fs-filename -- TEST-1001: Reads repo-local locale fixtures under src/locales. [ttl=2026-12-31] */
import { existsSync, readdirSync, readFileSync, statSync } from "node:fs";
import path from "node:path";

import { PLACEHOLDER_PHRASES } from "@tests/utils/detectRenderedI18nPlaceholders";

import { type AppLanguage,i18nConfig } from "@/i18n.config";

import { resolveGuideContentFileAllowlist } from "../helpers/guideFilters";

const LOCALES_ROOT = path.resolve(__dirname, "../../../locales");
const BASELINE_LOCALE: AppLanguage = "en";
const GUIDE_CONTENT_ALLOWLIST = resolveGuideContentFileAllowlist();

const STRICT_MODE =
  process.env.CONTENT_READINESS_MODE === "fail" ||
  process.env.I18N_PARITY_MODE === "fail";

const SKIP_FILE_PREFIXES: string[] = [
  // Guide long-form content has its own dedicated audits (schema, placeholders, EN fallbacks).
  "guides/content/",
];

const RAW_KEY_PREFIXES = [
  "content.",
  "pages.",
  "components.",
  "common.",
  "footer.",
  "header.",
  "guides.",
  "assistance.",
  "translation.",
  "modals.",
  "seo.",
  "meta.",
];

const RAW_KEY_TOKEN_PATTERN = new RegExp(
  // Examples: pages.rooms.title, content.cheapEats.intro
  `\\b(?:${RAW_KEY_PREFIXES.map((p) => p.replace(".", "\\.")).join("|")})` +
    `[a-zA-Z0-9_-]+(?:\\.[a-zA-Z0-9_-]+)+\\b`,
  "g",
);

const PLACEHOLDER_PHRASE_PATTERNS = PLACEHOLDER_PHRASES.map((phrase) => {
  const escaped = phrase.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
  return new RegExp(`${escaped}[.!?…]*`, "i");
});

const NON_LATIN_LOCALES = new Set<AppLanguage>(["ar", "hi", "ja", "ko", "ru", "zh"]);
const ENGLISH_STOPWORD_PATTERN =
  /\b(the|and|or|to|from|with|without|for|of|in|on|at|your|you|we|our|this|that|is|are|be|a|an)\b/u;

type IssueKind =
  | "missingFile"
  | "extraFile"
  | "missingKey"
  | "extraKey"
  | "typeMismatch"
  | "emptyString"
  | "placeholderKey"
  | "placeholderPhrase"
  | "rawKeyToken"
  | "englishDuplicate"
  | "tooShort"
  | "scriptMismatch"
  | "arrayLengthMismatch";

type Issue = {
  locale: string;
  file: string;
  keyPath: string;
  kind: IssueKind;
  message: string;
};

function isRecord(value: unknown): value is Record<string, unknown> {
  return Boolean(value) && typeof value === "object" && !Array.isArray(value);
}

function listJsonFiles(rootDir: string, relativeDir = ""): string[] {
  const out: string[] = [];
  const fullDir = path.join(rootDir, relativeDir);
  for (const entry of readdirSync(fullDir, { withFileTypes: true })) {
    const nextRelative = relativeDir ? path.join(relativeDir, entry.name) : entry.name;
    const fullPath = path.join(rootDir, nextRelative);
    if (entry.isDirectory()) {
      out.push(...listJsonFiles(rootDir, nextRelative));
      continue;
    }
    if (entry.isFile() && entry.name.endsWith(".json")) {
      out.push(nextRelative);
    }
  }
  return out.sort();
}

function shouldSkipFile(relativeFile: string): boolean {
  return SKIP_FILE_PREFIXES.some((prefix) => relativeFile.startsWith(prefix));
}

function isUrlLike(value: string): boolean {
  const trimmed = value.trim();
  return (
    /^https?:\/\/\S+/iu.test(trimmed) ||
    trimmed.startsWith("www.") ||
    trimmed.startsWith("/") ||
    /^mailto:\S+/iu.test(trimmed)
  );
}

function isFilePathLike(value: string): boolean {
  const trimmed = value.trim();
  return (
    /^[./\\]/u.test(trimmed) ||
    /\.(?:js|ts|tsx|jsx|json|css|scss|png|jpe?g|svg|webp|gif|md)(\?|#|$)/iu.test(
      trimmed,
    )
  );
}

function isMostlyPunctuation(value: string): boolean {
  const trimmed = value.trim();
  if (!trimmed) return true;
  const lettersOrNumbers = (trimmed.match(/[\p{L}\p{N}]/gu) ?? []).length;
  return lettersOrNumbers === 0;
}

function detectPlaceholderPhrase(value: string): boolean {
  const trimmed = value.trim();
  if (!trimmed) return false;
  return PLACEHOLDER_PHRASE_PATTERNS.some((pattern) => pattern.test(trimmed));
}

function hasRawKeyToken(value: string): string[] {
  const matches = value.match(RAW_KEY_TOKEN_PATTERN);
  return matches ? Array.from(new Set(matches)) : [];
}

function minLengthRatioForLocale(locale: string): number {
  // CJK can be much shorter than English; other scripts usually track closer.
  if (locale === "ja" || locale === "ko" || locale === "zh") return 0.08;
  return 0.18;
}

function minAbsoluteLengthForEnglishSource(englishLength: number): number {
  if (englishLength >= 140) return 24;
  if (englishLength >= 80) return 14;
  if (englishLength >= 40) return 8;
  return 1;
}

function isSlugLike(value: string): boolean {
  const trimmed = value.trim();
  if (trimmed.length < 4 || trimmed.length > 160) return false;
  if (trimmed.includes(" ")) return false;
  // Kebab-case slugs (canonical across locales).
  return /^[a-z0-9]+(?:-[a-z0-9]+)*$/u.test(trimmed);
}

function isLikelyNonTranslatableKeyPath(keyPath: string): boolean {
  // Keys that are expected to be stable identifiers (not translated).
  return /(^|\.)(slug|id|href|url|src)$/u.test(keyPath);
}

function isLikelyEnglishSentence(value: string): boolean {
  const trimmed = value.trim();
  if (trimmed.length < 25) return false;

  const lower = trimmed.toLowerCase();
  const words = lower.match(/\b[a-z]+\b/gu) ?? [];
  const wordCount = words.length;

  if (wordCount >= 10) return true;
  if (ENGLISH_STOPWORD_PATTERN.test(lower) && wordCount >= 4) return true;

  return false;
}

function isLikelyNonTranslatableString(value: string): boolean {
  const trimmed = value.trim();
  if (!trimmed) return true;
  if (isUrlLike(trimmed)) return true;
  if (isFilePathLike(trimmed)) return true;
  if (isSlugLike(trimmed)) return true;
  // IDs / codes / very short fragments are often shared across locales.
  if (trimmed.length <= 3) return true;
  return false;
}

function truncate(value: string, max = 120): string {
  const trimmed = value.trim();
  if (trimmed.length <= max) return trimmed;
  return `${trimmed.slice(0, max - 3)}...`;
}

function compareValues(args: {
  locale: string;
  file: string;
  keyPath: string;
  base: unknown;
  target: unknown;
  issues: Issue[];
}): void {
  const { locale, file, keyPath, base, target, issues } = args;
  const isGuideContent = file.startsWith("guides/content/");

  if (typeof base === "string") {
    if (typeof target !== "string") {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "typeMismatch",
        message: `Expected string, got ${target === null ? "null" : typeof target}`,
      });
      return;
    }

    const baseTrimmed = base.trim();
    const targetTrimmed = target.trim();

    if (targetTrimmed.length === 0) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "emptyString",
        message: "Value is empty/whitespace",
      });
      return;
    }

    // Key-as-value placeholder (most common runtime-missing symptom when values are stubbed)
    if (targetTrimmed === keyPath) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "placeholderKey",
        message: `Value equals key path (${JSON.stringify(keyPath)})`,
      });
    }

    if (!isGuideContent && detectPlaceholderPhrase(targetTrimmed)) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "placeholderPhrase",
        message: `Contains placeholder phrase (${JSON.stringify(truncate(targetTrimmed))})`,
      });
    }

    const rawTokens = hasRawKeyToken(targetTrimmed);
    if (rawTokens.length > 0) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "rawKeyToken",
        message: `Contains raw i18n key token(s): ${rawTokens.slice(0, 3).join(", ")}`,
      });
    }

    // Heuristics relative to English baseline
    if (
      baseTrimmed.length >= 25 &&
      baseTrimmed === targetTrimmed &&
      isLikelyEnglishSentence(baseTrimmed) &&
      !isLikelyNonTranslatableKeyPath(keyPath) &&
      !isLikelyNonTranslatableString(baseTrimmed)
    ) {
      if (!isGuideContent) {
        issues.push({
          locale,
          file,
          keyPath,
          kind: "englishDuplicate",
          message: `Value matches EN exactly (${JSON.stringify(truncate(targetTrimmed))})`,
        });
      }
    }

    if (
      !isLikelyNonTranslatableString(baseTrimmed) &&
      baseTrimmed.length >= 40 &&
      isLikelyEnglishSentence(baseTrimmed)
    ) {
      const minRatio = minLengthRatioForLocale(locale);
      const minAbs = minAbsoluteLengthForEnglishSource(baseTrimmed.length);
      const isTooShort = targetTrimmed.length < minAbs || targetTrimmed.length < baseTrimmed.length * minRatio;
      if (isTooShort && !isMostlyPunctuation(targetTrimmed)) {
        issues.push({
          locale,
          file,
          keyPath,
          kind: "tooShort",
          message: `Suspiciously short vs EN (en=${baseTrimmed.length}, ${locale}=${targetTrimmed.length})`,
        });
      }
    }

    // Script sanity: for non-Latin locales, long strings should include some non-ASCII text.
    if (
      NON_LATIN_LOCALES.has(locale as AppLanguage) &&
      baseTrimmed.length >= 40 &&
      isLikelyEnglishSentence(baseTrimmed)
    ) {
      if (!isLikelyNonTranslatableString(targetTrimmed)) {
        const nonAsciiCount = (targetTrimmed.match(/[^\x00-\x7f]/gu) ?? []).length;
        if (nonAsciiCount === 0) {
          issues.push({
            locale,
            file,
            keyPath,
            kind: "scriptMismatch",
            message: `Expected non-ASCII characters for locale "${locale}" (looks like Latin/EN text)`,
          });
        }
      }
    }

    return;
  }

  if (Array.isArray(base)) {
    if (!Array.isArray(target)) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "typeMismatch",
        message: `Expected array, got ${target === null ? "null" : typeof target}`,
      });
      return;
    }

    if (base.length !== target.length) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "arrayLengthMismatch",
        message: `Array length differs (en=${base.length}, ${locale}=${target.length})`,
      });
    }

    const minLen = Math.min(base.length, target.length);
    for (let i = 0; i < minLen; i++) {
      compareValues({
        locale,
        file,
        keyPath: keyPath ? `${keyPath}.${i}` : String(i),
        base: base[i],
        target: target[i],
        issues,
      });
    }
    return;
  }

  if (isRecord(base)) {
    if (!isRecord(target)) {
      issues.push({
        locale,
        file,
        keyPath,
        kind: "typeMismatch",
        message: `Expected object, got ${target === null ? "null" : typeof target}`,
      });
      return;
    }

    for (const [key, value] of Object.entries(base)) {
      if (!(key in target)) {
        const pathKey = keyPath ? `${keyPath}.${key}` : key;
        issues.push({
          locale,
          file,
          keyPath: pathKey,
          kind: "missingKey",
          message: "Missing key",
        });
        continue;
      }

      const nextPath = keyPath ? `${keyPath}.${key}` : key;
      compareValues({
        locale,
        file,
        keyPath: nextPath,
        base: value,
        target: target[key],
        issues,
      });
    }

    for (const extraKey of Object.keys(target)) {
      if (extraKey in base) continue;
      if (extraKey === "_schemaValidation") continue; // metadata opt-out, allowed
      const pathKey = keyPath ? `${keyPath}.${extraKey}` : extraKey;
      issues.push({
        locale,
        file,
        keyPath: pathKey,
        kind: "extraKey",
        message: "Extra key (not present in EN baseline)",
      });
    }

    return;
  }

  // Non-string primitives are unusual in i18n JSON, but if EN has them,
  // locales should match type to avoid runtime surprises.
  if (typeof base !== typeof target) {
    issues.push({
      locale,
      file,
      keyPath,
      kind: "typeMismatch",
      message: `Type differs (en=${typeof base}, ${locale}=${typeof target})`,
    });
  }
}

function formatIssueSummary(issues: Issue[]): string {
  const byLocale = new Map<string, Issue[]>();
  for (const issue of issues) {
    const list = byLocale.get(issue.locale) ?? [];
    list.push(issue);
    byLocale.set(issue.locale, list);
  }

  const lines: string[] = [];
  lines.push(`Total issues: ${issues.length}`);
  lines.push("");

  for (const [locale, list] of Array.from(byLocale.entries()).sort((a, b) => b[1].length - a[1].length)) {
    const byKind = list.reduce<Record<IssueKind, number>>((acc, issue) => {
      acc[issue.kind] = (acc[issue.kind] ?? 0) + 1;
      return acc;
    }, {} as Record<IssueKind, number>);

    const kinds = Object.entries(byKind)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 6)
      .map(([kind, count]) => `${kind}=${count}`)
      .join(", ");

    lines.push(`${locale}: ${list.length} issues (${kinds})`);
  }

  return lines.join("\n");
}

function formatIssueSample(issues: Issue[], maxItems = 40): string {
  return issues
    .slice(0, maxItems)
    .map((issue) => `${issue.locale} :: ${issue.file} :: ${issue.keyPath} :: ${issue.kind} :: ${issue.message}`)
    .join("\n");
}

function collectIssuesForFileFilter(fileFilter: (file: string) => boolean): Issue[] {
  const baselineDir = path.join(LOCALES_ROOT, BASELINE_LOCALE);
  const baselineFiles = listJsonFiles(baselineDir).filter(fileFilter);
  const baselineSet = new Set(baselineFiles);

  const locales = (i18nConfig.supportedLngs ?? []).filter(
    (locale) => locale !== BASELINE_LOCALE,
  ) as string[];

  const issues: Issue[] = [];

  for (const locale of locales) {
    const localeDir = path.join(LOCALES_ROOT, locale);
    if (!existsSync(localeDir) || !statSync(localeDir).isDirectory()) {
      issues.push({
        locale,
        file: "(locale)",
        keyPath: "",
        kind: "missingFile",
        message: "Locale directory is missing",
      });
      continue;
    }

    const localeFiles = listJsonFiles(localeDir).filter(fileFilter);
    const localeSet = new Set(localeFiles);

    // Missing / extra files
    for (const file of baselineFiles) {
      if (!localeSet.has(file)) {
        issues.push({
          locale,
          file,
          keyPath: "",
          kind: "missingFile",
          message: "Missing file (present in EN baseline)",
        });
      }
    }
    for (const file of localeFiles) {
      if (!baselineSet.has(file)) {
        issues.push({
          locale,
          file,
          keyPath: "",
          kind: "extraFile",
          message: "Extra file (not present in EN baseline)",
        });
      }
    }

    // Deep compare values for files that exist in both
    for (const file of baselineFiles) {
      if (!localeSet.has(file)) continue;

      const baseJson = JSON.parse(readFileSync(path.join(baselineDir, file), "utf8")) as unknown;
      const localeJson = JSON.parse(readFileSync(path.join(localeDir, file), "utf8")) as unknown;

      compareValues({
        locale,
        file,
        keyPath: "",
        base: baseJson,
        target: localeJson,
        issues,
      });
    }
  }

  return issues;
}

describe("i18n parity & quality audit (non-guide content)", () => {
  it("reports missing keys/files and obvious placeholders vs EN", () => {
    expect(statSync(LOCALES_ROOT).isDirectory()).toBe(true);
    const issues = collectIssuesForFileFilter((file) => !shouldSkipFile(file));

    if (issues.length === 0) {
      expect(issues).toEqual([]);
      return;
    }

    const summary = formatIssueSummary(issues);
    const sample = formatIssueSample(issues);

    const message = [
      "i18n parity/quality issues found vs EN baseline.",
      "This suite cannot guarantee translation quality, but it flags strong indicators of low-quality or incomplete translations (missing keys/files, empty strings, placeholder values, raw key tokens, etc.).",
      "",
      summary,
      "",
      "Sample:",
      sample,
      "",
      "To enforce: set CONTENT_READINESS_MODE=fail (or I18N_PARITY_MODE=fail).",
    ].join("\n");

    if (STRICT_MODE) {
      throw new Error(message);
    }


    console.warn("[WARN] " + message);
    expect(true).toBe(true);
  });
});

describe("i18n parity & quality audit (guide content structure)", () => {
  it("reports missing files/keys and structural mismatches vs EN", () => {
    expect(statSync(LOCALES_ROOT).isDirectory()).toBe(true);
    const issues = collectIssuesForFileFilter((file) => {
      if (!file.startsWith("guides/content/")) return false;
      if (!GUIDE_CONTENT_ALLOWLIST) return true;
      return GUIDE_CONTENT_ALLOWLIST.has(file);
    });

    if (issues.length === 0) {
      expect(issues).toEqual([]);
      return;
    }

    const summary = formatIssueSummary(issues);
    const sample = formatIssueSample(issues);

    const message = [
      "Guide content parity/quality issues found vs EN baseline.",
      "This audit focuses on structural parity (missing files/keys, type mismatches, empty strings, raw key tokens). Placeholder phrases and EN-copy checks are covered by dedicated guide audits.",
      "",
      summary,
      "",
      "Sample:",
      sample,
      "",
      "To enforce: set CONTENT_READINESS_MODE=fail (or I18N_PARITY_MODE=fail).",
    ].join("\n");

    if (STRICT_MODE) {
      throw new Error(message);
    }


    console.warn("[WARN] " + message);
    expect(true).toBe(true);
  });
});

