/* eslint-disable security/detect-non-literal-fs-filename -- TEST-1001: Reads repo-local locale fixtures under src/locales. [ttl=2026-12-31] */

import path from "node:path";

import {
  auditLocaleJsonForRawKeys,
  formatRawKeyAuditIssues,
} from "~test/i18n/localeRawKeyAudit";

import { type AppLanguage, i18nConfig } from "@/i18n.config";

describe("i18n raw key enforcement (non-guide content)", () => {
  it("fails if any locale JSON value contains raw i18n key placeholders", () => {
    const localesRoot = path.resolve(__dirname, "../../../locales");
    const locales = (i18nConfig.supportedLngs ?? []) as AppLanguage[];

    const issues = auditLocaleJsonForRawKeys({
      localesRoot,
      baselineLocale: "en",
      locales,
      // Long-form guide content has its own suites; keep this enforcement fast and focused.
      skipFilePrefixes: ["guides/content/"],
    });

    if (issues.length > 0) {
      throw new Error(formatRawKeyAuditIssues(issues));
    }
  });
});

/**
 * i18n Render Audit Test Suite
 *
 * Scans locale JSON files for placeholder phrases that should not appear in translated content.
 *
 * Default behaviour: warn-only (test passes; writes report to stdout).
 * Strict mode: set I18N_MISSING_KEYS_MODE=fail to make the suite fail if findings exist.
 *
 * @see docs/plans/i18n-missing-key-detection-plan.md
 */

import { existsSync,readdirSync, readFileSync } from "node:fs";
import path from "node:path";

import {
  detectRenderedI18nPlaceholders,
  type PlaceholderFinding,
} from "@tests/utils/detectRenderedI18nPlaceholders";

import { i18nConfig } from "@/i18n.config";

import { resolveGuideContentFileAllowlist } from "../helpers/guideFilters";

// ----------------------------------------------------------------------------
// Configuration
// ----------------------------------------------------------------------------

const LOCALES_ROOT = path.resolve(__dirname, "../../../locales");
const BASELINE_LOCALE = "en";
const GUIDE_CONTENT_ALLOWLIST = resolveGuideContentFileAllowlist();

// Strict mode: when set to "fail", the test fails on any findings
const STRICT_MODE =
  process.env.I18N_MISSING_KEYS_MODE === "fail" ||
  process.env.CONTENT_READINESS_MODE === "fail";

// Subdirectories containing guide-specific content
const GUIDES_CONTENT_DIR = "guides/content";

// ----------------------------------------------------------------------------
// Types
// ----------------------------------------------------------------------------

interface LocaleAuditResult {
  locale: string;
  namespace: string;
  findings: PlaceholderFinding[];
}

interface AuditReport {
  totalFindings: number;
  localesWithIssues: number;
  results: LocaleAuditResult[];
}

// ----------------------------------------------------------------------------
// Helpers
// ----------------------------------------------------------------------------

/**
 * Recursively extract all string values from a JSON object.
 */
function extractStrings(
  obj: unknown,
  prefix = ""
): Array<{ key: string; value: string }> {
  const strings: Array<{ key: string; value: string }> = [];

  if (typeof obj === "string") {
    strings.push({ key: prefix, value: obj });
    return strings;
  }

  if (Array.isArray(obj)) {
    for (let i = 0; i < obj.length; i++) {
      strings.push(...extractStrings(obj[i], `${prefix}[${i}]`));
    }
    return strings;
  }

  if (obj && typeof obj === "object") {
    for (const [key, value] of Object.entries(obj)) {
      const nextPrefix = prefix ? `${prefix}.${key}` : key;
      strings.push(...extractStrings(value, nextPrefix));
    }
  }

  return strings;
}

/**
 * List all guide content files for a locale.
 */
function listGuideContentFiles(locale: string): string[] {
  const guidesDir = path.join(LOCALES_ROOT, locale, GUIDES_CONTENT_DIR);
  if (!existsSync(guidesDir)) {
    return [];
  }

  try {
    const files = readdirSync(guidesDir);
    return files
      .filter((f) => f.endsWith(".json"))
      .filter((f) => {
        if (!GUIDE_CONTENT_ALLOWLIST) return true;
        return GUIDE_CONTENT_ALLOWLIST.has(path.join(GUIDES_CONTENT_DIR, f));
      })
      .map((f) => path.join(GUIDES_CONTENT_DIR, f));
  } catch {
    return [];
  }
}

/**
 * Audit guide content files for a locale.
 */
function auditGuideContent(locale: string): LocaleAuditResult[] {
  const results: LocaleAuditResult[] = [];
  const guideFiles = listGuideContentFiles(locale);

  for (const relPath of guideFiles) {
    const filePath = path.join(LOCALES_ROOT, locale, relPath);
    try {
      const content = readFileSync(filePath, "utf8");
      const json = JSON.parse(content);
      const strings = extractStrings(json);
      const findings: PlaceholderFinding[] = [];

      for (const { value } of strings) {
        const detected = detectRenderedI18nPlaceholders(value, {
          keyPrefixes: [],
          minDotSegments: 999,
        });
        const phraseFindings = detected.filter((f) => f.kind === "placeholderPhrase");
        findings.push(...phraseFindings);
      }

      if (findings.length > 0) {
        results.push({
          locale,
          namespace: relPath.replace(/\.json$/, ""),
          findings,
        });
      }
    } catch {
      // Skip files that can't be parsed
    }
  }

  return results;
}

/**
 * Run the full audit across all locales and namespaces.
 */
function runFullAudit(): AuditReport {
  const results: LocaleAuditResult[] = [];
  const locales = (i18nConfig.supportedLngs ?? []).filter(
    (l) => l !== BASELINE_LOCALE
  );

  for (const locale of locales) {
    // Audit guide content files
    const guideResults = auditGuideContent(locale);
    results.push(...guideResults);
  }

  const totalFindings = results.reduce((sum, r) => sum + r.findings.length, 0);
  const localesWithIssues = new Set(results.map((r) => r.locale)).size;

  return { totalFindings, localesWithIssues, results };
}

/**
 * Format the audit report for console output.
 */
function formatAuditReport(report: AuditReport): string {
  const lines: string[] = [];

  lines.push("=".repeat(60));
  lines.push("Guide Content Placeholder Audit Report");
  lines.push("=".repeat(60));
  lines.push("");

  if (report.totalFindings === 0) {
    lines.push("No placeholder phrases detected in locale files.");
    return lines.join("\n");
  }

  lines.push(`Total findings: ${report.totalFindings}`);
  lines.push(`Locales with issues: ${report.localesWithIssues}`);
  lines.push("");

  // Group by locale
  const byLocale = new Map<string, LocaleAuditResult[]>();
  for (const result of report.results) {
    const existing = byLocale.get(result.locale) ?? [];
    existing.push(result);
    byLocale.set(result.locale, existing);
  }

  for (const [locale, results] of byLocale) {
    const localeTotal = results.reduce((sum, r) => sum + r.findings.length, 0);
    lines.push(`${locale} (${localeTotal} issues):`);

    for (const result of results) {
      lines.push(`  ${result.namespace}: ${result.findings.length}`);
      // Show first few findings
      const sample = result.findings.slice(0, 3);
      for (const finding of sample) {
        lines.push(`    - "${finding.value}"`);
      }
      if (result.findings.length > 3) {
        lines.push(`    ... and ${result.findings.length - 3} more`);
      }
    }
    lines.push("");
  }

  return lines.join("\n");
}

// ----------------------------------------------------------------------------
// Tests
// ----------------------------------------------------------------------------

describe("guide content placeholder audit", () => {
  it("scans non-en guide content files for placeholder phrases", () => {
    const report = runFullAudit();
    const formattedReport = formatAuditReport(report);

    // Always log the report for visibility
    // eslint-disable-next-line no-console
    console.log("\n" + formattedReport);

    if (STRICT_MODE) {
      expect(report.totalFindings).toBe(0);
      return;
    }

    if (report.totalFindings > 0) {

      console.warn(
        `[WARN] Found ${report.totalFindings} placeholder phrase(s) in non-en guide content.`
      );

      console.warn("Run with CONTENT_READINESS_MODE=fail to enforce.");
    }
    expect(true).toBe(true);
  });
});

/**
 * Hydration test utilities for detecting server/client rendering mismatches
 *
 * These utilities help catch hydration errors that `suppressHydrationWarning` masks.
 * They simulate the SSR → client hydration cycle and capture React hydration errors.
 *
 * Usage:
 * ```tsx
 * const result = renderWithHydration({
 *   server: <MyComponent />,
 *   client: <MyComponent />,
 * });
 * expectNoHydrationErrors(result);
 * ```
 */

import type { ReactElement } from "react";
import { act } from "react";
import { hydrateRoot } from "react-dom/client";
import { renderToString } from "react-dom/server";

export interface HydrationTestResult {
  /** Server-rendered HTML string */
  serverHTML: string;
  /** Array of hydration errors captured during hydration */
  hydrationErrors: Array<Error>;
  /** DOM container element after hydration (for queries) */
  container: HTMLElement;
}

export interface RenderWithHydrationOptions {
  /** Component to render on server */
  server: ReactElement;
  /** Component to hydrate on client */
  client: ReactElement;
}

/**
 * Renders a component in SSR mode, then hydrates it on the client, capturing hydration errors.
 *
 * This simulates the Next.js SSR → client hydration cycle:
 * 1. Server render runs in a server-like environment (no window/document)
 * 2. Client hydration runs in JSDOM with full browser globals
 * 3. Hydration errors are captured via onRecoverableError
 *
 * @param options - Server and client components to render
 * @returns Result with serverHTML, hydrationErrors, and container
 */
export function renderWithHydration(options: RenderWithHydrationOptions): HydrationTestResult {
  const { server, client } = options;
  const hydrationErrors: Array<Error> = [];

  // Phase 1: Server render (SSR simulation)
  // Temporarily clear window and document to simulate real SSR environment
  const originalWindow = globalThis.window;
  const originalDocument = globalThis.document;

  try {
    // Clear browser globals to simulate SSR
    // @ts-expect-error - Intentionally setting to undefined to simulate SSR
    globalThis.window = undefined;
    // @ts-expect-error - Intentionally setting to undefined to simulate SSR
    globalThis.document = undefined;

    // Render to string (SSR)
    const serverHTML = renderToString(server);

    // Phase 2: Client hydration
    // Restore browser globals
    globalThis.window = originalWindow;
    globalThis.document = originalDocument;

    // Create container and inject server HTML
    const container = document.createElement("div");
    container.innerHTML = serverHTML;

    // Hydrate with error capture
    // In React 19 with StrictMode in tests, hydration errors throw instead of calling onRecoverableError
    // We catch both thrown errors and onRecoverableError callbacks
    let root;
    try {
      root = hydrateRoot(container, client, {
        onRecoverableError: (error: unknown) => {
          // Capture hydration errors reported via callback
          if (error instanceof Error) {
            hydrationErrors.push(error);
          } else {
            hydrationErrors.push(new Error(String(error)));
          }
        },
      });

      // Flush hydration work
      act(() => {
        // Hydration happens synchronously in act
      });
    } catch (error) {
      // Capture thrown hydration errors
      if (error instanceof Error) {
        hydrationErrors.push(error);
      } else {
        hydrationErrors.push(new Error(String(error)));
      }
    }

    return {
      serverHTML,
      hydrationErrors,
      container,
    };
  } finally {
    // Always restore globals, even if render throws
    globalThis.window = originalWindow;
    globalThis.document = originalDocument;
  }
}

/**
 * Assert that a hydration test result has no errors.
 *
 * Throws an error with a clear message if hydration errors were detected.
 *
 * @param result - Result from renderWithHydration
 */
export function expectNoHydrationErrors(result: HydrationTestResult): void {
  if (result.hydrationErrors.length > 0) {
    const errorMessages = result.hydrationErrors.map((err, idx) => `  ${idx + 1}. ${err.message}`).join("\n");
    throw new Error(
      `Expected no hydration errors, but found ${result.hydrationErrors.length}:\n${errorMessages}`
    );
  }
}

// Test helper to load actual guide content from filesystem
// This ensures guides namespace is properly initialized for tests

import fs from "fs";
import path from "path";

import { __setGuidesModulesForTests } from "@/locales/guides";
import type { GuidesNamespace, ModuleOverrides, ModuleRecord } from "@/locales/guides.types";

/**
 * Load guide content from filesystem for a specific locale.
 * Call this in beforeAll() or beforeEach() to ensure guides are available in tests.
 */
export function loadGuidesForTest(locales: string[] = ["en"]): void {
  const localesRoot = path.resolve(__dirname, "../../locales");

  const legacyModules: ModuleRecord<GuidesNamespace> = {};
  const splitGlobalModules: ModuleRecord = {};
  const splitContentModules: ModuleRecord = {};

  const readJsonFile = <T = unknown>(filePath: string): T => {
    const data = fs.readFileSync(filePath, "utf8");
    try {
      return JSON.parse(data) as T;
    } catch (error) {
      throw new Error(`Failed to parse JSON from ${filePath}: ${(error as Error).message}`);
    }
  };

  const makeKey = (locale: string, relativePath: string): string =>
    `./${locale}/${relativePath}`.replace(/\\/g, "/");

  for (const locale of locales) {
    const localeDir = path.join(localesRoot, locale);

    if (!fs.existsSync(localeDir)) {
      console.warn(`Locale directory not found: ${localeDir}`);
      continue;
    }

    // Load legacy guides.json if it exists
    const legacyFile = path.join(localeDir, "guides.json");
    if (fs.existsSync(legacyFile) && fs.statSync(legacyFile).isFile()) {
      legacyModules[makeKey(locale, "guides.json")] = {
        default: readJsonFile<GuidesNamespace>(legacyFile),
      };
    }

    // Load split guides structure
    const guidesDir = path.join(localeDir, "guides");
    if (!fs.existsSync(guidesDir) || !fs.statSync(guidesDir).isDirectory()) {
      continue;
    }

    const stack: Array<{ dir: string; segments: string[] }> = [{ dir: guidesDir, segments: [] }];

    while (stack.length > 0) {
      const { dir, segments } = stack.pop()!;
      const children = fs.readdirSync(dir, { withFileTypes: true });

      for (const child of children) {
        if (child.name.startsWith("_")) continue;
        const nextSegments = [...segments, child.name];
        const absolute = path.join(dir, child.name);

        if (child.isDirectory()) {
          stack.push({ dir: absolute, segments: nextSegments });
          continue;
        }

        if (!child.isFile() || !child.name.endsWith(".json")) {
          continue;
        }

        const relativePath = ["guides", ...nextSegments].join("/");
        const data = readJsonFile(absolute);
        splitGlobalModules[makeKey(locale, relativePath)] = { default: data };

        if (nextSegments[0] === "content" && nextSegments.length === 2) {
          splitContentModules[makeKey(locale, relativePath)] = { default: data };
        }
      }
    }
  }

  const overrides: ModuleOverrides = {
    legacy: legacyModules,
    splitGlobal: splitGlobalModules,
    splitContent: splitContentModules,
  };

  __setGuidesModulesForTests(overrides);
}

