/**
 * Parameters for the Axerve/GestPay S2S `callPagamS2S` operation.
 * All string values; numeric currency and amount fields are passed as strings
 * per the GestPay SOAP API contract.
 */
export interface AxervePaymentParams {
  /** Shop login identifier from the Axerve merchant agreement. */
  shopLogin: string;
  /** API key from the Axerve merchant agreement. */
  apiKey: string;
  /** ISO 4217 numeric currency code â€” use "978" for EUR. */
  uicCode: string;
  /** Payment amount as a decimal string, e.g. "10.00". */
  amount: string;
  /** Unique transaction ID generated by the shop (e.g. UUID + cart suffix). */
  shopTransactionId: string;
  /** Full PAN card number. NEVER log or persist this value. */
  cardNumber: string;
  /** Two-digit expiry month, e.g. "12". */
  expiryMonth: string;
  /** Four-digit expiry year, e.g. "2027". */
  expiryYear: string;
  /** Card security code (CVV2/CVC). NEVER log or persist this value. */
  cvv: string;
  /** Optional buyer display name. */
  buyerName?: string;
  /** Optional buyer email address. */
  buyerEmail?: string;
}

/** Result returned by a completed Axerve S2S payment call. */
export interface AxervePaymentResult {
  /** `true` when Axerve returned TransactionResult === "OK". */
  success: boolean;
  /** Shop-assigned transaction ID (echoed from request or returned by Axerve). */
  transactionId: string;
  /** Axerve's bank-side transaction identifier. */
  bankTransactionId: string;
  /** Authorisation code from the card scheme, present on success. */
  authCode?: string;
  /** Axerve error code, present on failure. */
  errorCode?: string;
  /** Human-readable error description from Axerve, present on failure. */
  errorDescription?: string;
}
